INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_TheBridgeIsOk', 'dl_npc_andrew', 'The river is frozen, you can go home.','Der Fluss ist gefroren, du kannst nach Hause gehen.','De Fluss isch gfrore, du chasch heiga.','Ya puedes regresar a casa, el río está congelado.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Andrew_IAm', 'dl_npc_andrew', 'I''m Andrew... hic! Nice to meet ya.','Ich bin Andrew... hicks! Shehr erfreut.','Ich bi de Andrew... hicks! Freut mi.','Soy Andrew (¡hip!) Gusto en conocer... te.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Andrew_IAmDrinking', 'dl_npc_andrew', 'Hehe... shilly question... Having some beers, enjoying my time here... I can''t go back anyway.','Hehe, komische Frage... Ich trinke ein paar Bierchen, geniesshe die Seit da... Kann sowieso nicht zurück.','Hehe... dummi Frog... Ich trink esh paar Bierli, gnüssh die Siit da... Cha showiso nid srugg.','jeje... pregunta apsurda (¡hip!) Tomando unasss cervezasss y... (¡hip!) disfrutando mi tiembo acá... (¡hip!) no buedo regresar de todas formassss.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhyCantYouGoBack', 'dl_npc_andrew', 'Why can''t you go back?','Wieso kannst du nicht zurück?','Weso chasch nid zrugg?','¿Por qué no puedes regresar?');
