INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_bjarne', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Talk', 'dl_npc_bjarne', 'You don''t like to talk much?', 'Du redest wohl nicht so gerne?', 'Du redsch nid so gern, he?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RunasDeal', 'dl_npc_bjarne', 'Runa sent me. I got some Physalis for you.', 'Runa hat mich geschickt. Ich habe Physalis für dich.', 'D''Runa het mi gschickt. Ich ha Physalis für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Physalis', 'dl_npc_bjarne', 'What do you need that many Physalis for?', 'Für was brauchst du so viele Physalis?', 'Für was bruchsch so viel Physalis?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mages', 'dl_npc_bjarne', 'Why wouldn''t Runa trust a mage?', 'Wieso würde Runa keinem Magier vertrauen?', 'Weso wür d''Runa kem Magier vertraue?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mage', 'dl_npc_bjarne', 'Aren''t you a mage yourself?', 'Bist du nicht selbst ein Magier?', 'Bisch du nid selber en Magier?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Bjarne_WhoAreYou', 'dl_npc_bjarne', 'My name''s Bjarne. That should be enough.', 'Mein Name ist Bjarne. Das sollte reichen.', 'Min Name isch Bjarne. Das sött lange.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Bjarne_Talk', 'dl_npc_bjarne', 'No.', 'Nein.', 'Nei.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Bjarne_RunasDeal', 'dl_npc_bjarne', 'Hm. I didn''t expect her to send someone else.', 'Hm. Ich hätte nicht erwartet, dass sie jemand anderen schickt.', 'Hm. Ich hett nid erwartet, dass si öpper andersch schickt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Bjarne_RunasDeal2', 'dl_npc_bjarne', 'Especially not a mage.', 'Und ganz sicher keinen Magier.', 'Und ganz sicher ken Magier.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Bjarne_RunasDeal3', 'dl_npc_bjarne', 'But well... here you go. You should be careful with that...', 'Aber gut... Hier hast du es. Damit solltest du vorsichtig sein... ', 'Aber guet... do hesches. Du sötsch vorsichtig si demit...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Bjarne_Physalis', 'dl_npc_bjarne', 'That''s none of your business.', 'Das geht dich nichts an.', 'Das goht di nüt ah.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Bjarne_Mages', 'dl_npc_bjarne', 'Because she''s afraid of them.', 'Weil sie sich vor ihnen fürchtet.', 'Well si Angst het vor ihne. ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Bjarne_Mage', 'dl_npc_bjarne', 'You should know that.', 'Das solltest du wissen.', 'Das sötsch wüsse.');
