INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Citizen_WhereBlacksmith', 'dl_npc_citizen1', 'Our best blacksmith is Harek. You can find him in the artisans district, in the southwest part of Gandria.', 'Unser bester Schmied ist Harek. Du findest ihn im Handwerkerviertel, im südwestlichen Teil von Gandria.', 'Üse best Schmied isch de Harek. Du findschen im Handwerkerviertel, im Südweste vo Gandria.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Citizen_WhereAlchemist', 'dl_npc_citizen1', 'You''re looking for Syrah. Her house is near the artisans district, in the southern part of Gandria.', 'Du suchst Syrah. Ihr Haus ist in der Nähe des Handwerkerviertels, im südlichen Teil von Gandria.', 'Du suechsch d''Syrah. Ihres Huus isch ide Nöchi vom Handwerkerviertel, im südliche Teil vo Gandria.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ZeffHere', 'dl_npc_citizen1', 'The leatherworker Zeff has his workshop in the artisans district. Although being transformed into a wolf-human, he''s still knows his craft.', 'Der Lederer Zeff hat seine Werkstatt im Handwerkerviertel. Obwohl er in einen Wolfsmenschen verwandelt wurde, beherrscht er sein Handwerk immer noch.', 'De Lederer Zeff het sini Werkstatt im Handwerkerviertel. Sini Fähigkeite hend nid unter sinere Verwandlig inen Wolfsmensch glitte.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ZeffGone', 'dl_npc_citizen1', 'We had a leatherworker once in our city, but he disappeared. Nobody knows what happened to him.', 'Wir hatten mal einen Lederer in unserer Stadt, aber er ist verschwunden. Niemand weiss, was ihm zugestossen ist.', 'Mir hend mal en Lederer i üsere Stadt gha, aber er isch verschwunde. Niemert weiss, wa mit ihm passiert isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Citizen_WhereTavern', 'dl_npc_citizen1', 'The "Basilisk Inn" is in between the artisans district and the harbour, near the market square.', 'Das "Gasthaus zum Basilisken" ist zwischen dem Handwerkerviertel und dem Hafen, in der Nähe des Marktplatzes.', 'S''"Gaschthuus zum Basilisk" isch zwüsche em Handwerkerviertel und em Hafe, ide Nöchi vom Marktplatz.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Citizen_WhereBarracks', 'dl_npc_citizen1', 'The barracks are near the gates of the inner city wall, on the other side of the river.', 'Die Kaserne ist in der Nähe des Tors der inneren Stadtmauer, auf der anderen Seite des Flusses.', 'D''Kaserne isch ide Nöchi vom Tor vode innere Stadtmuur, uf de andere Siite vom Fluss.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Citizen_WhereCathedral', 'dl_npc_citizen1', 'The cathedral is in the northern part of Gandria, on the other side of the river.', 'Die Kathedrale ist im nördlichen Teil von Gandria, auf der anderen Seite des Flusses.', 'D''Kathedrale isch im nördliche Teil vo Gandria, uf de andere Siite vom Fluss.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Citizen_WhereBrothel', 'dl_npc_citizen1', 'Ehm... The brothel is in the harbour district.', 'Ehm... Das Bordell ist im Hafenviertel.', 'Ehm... Es Bordell isch im Hafeviertel.');
