INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Citizen_AboutGandria', 'dl_npc_citizen1', 'Gandria is not only the capital of Admantris, it plays also an important economic role as a port. ', 'Gandria ist nicht nur die Hauptstadt von Admantris sondern hat auch eine bedeutende wirtschaftliche Rolle als Hafenstadt.', 'Gandria isch nid nur d''Hauptstadt vo Admantris sondern het au e wichtigi wirtschaftlichi Rolle als Hafestadt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Citizen_AboutKing', 'dl_npc_citizen1', 'We don''t know much about our king, Logan the Third. He has only recently ascended the throne.', 'Wir wissen nicht viel über unseren König, Logan den Dritten. Er hat den Thron erst kürzlich bestiegen.', 'Mir wüssed nid viel über üsen König, de Logan de Dritt. Er isch erst sit churzem ufem Thron.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Citizen_AboutMages', 'dl_npc_citizen1', 'Mages rule our city. Most of the people in the militia know magic and the king has always been a mage as far as I can remember.', 'Magier regieren unsere Stadt. Die meisten Leute der Miliz beherrschen Magie und der König war schon immer ein Magier, soweit ich mich erinnern kann.', 'D''Magier regiered üsi Stadt. Di meiste Lüt ide Miliz beherrsched Magie und de König isch au scho immer en Magier gsi sowiit ich weiss.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Citizen_AboutMages2', 'dl_npc_citizen1', 'It''s hard being a commoner in a world ruled by magic.', 'Es ist nicht einfach, ein Gewöhnlicher zu sein in einer Welt, die von Magie regiert wird.', 'Es isch nid eifach en Gwöhnliche z''sii inere Welt wo vo Magie regiert wird.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Citizen_AboutNecromany', 'dl_npc_citizen1', 'Hush! We don''t talk about that.', 'Psst! Wir reden nicht über das.', 'Psht! Mir reded nid über das.');
