INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cynthia_ChooseSide', 'dl_npc_cynthia', 'Whose side are you on? Mages or commoners?', 'Auf wessen Seite stehst du? Magier oder Gewöhnliche?', 'Uf welere Siite bisch? Magier oder Gwöhnlichi?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Commoners', 'dl_npc_cynthia', 'Commoners.', 'Gewöhnliche.', 'Gwöhnlichi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mages', 'dl_npc_cynthia', 'Mages.', 'Magier.', 'Magier.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhySide', 'dl_npc_cynthia', 'Why do I have to choose a side?', 'Weshalb muss ich eine Seite wählen?', 'Weso muni e Siite wähle?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cynthia_Commoners', 'dl_npc_cynthia', 'You''re right. Magic is the root of all evil.', 'Du hast recht. Magie ist die Wurzel alles Übels.', 'Du hesch recht. Magie isch d''Wurzle vo allem Übel.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cynthia_Commoners2', 'dl_npc_cynthia', 'Mages could kill you with a single word. Is that fair?', 'Magier könnten dich mit einem einzigen Wort töten. Ist das gerecht?', 'Magier chönted dich mitme einzige Wort töte. Isch da fair?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cynthia_Commoners3', 'dl_npc_cynthia', 'Here, take this pendant, it will protect you against magic. Even against your own.', 'Hier, nimm diesen Anhänger, er wird dich gegen Magie schützen. Sogar gegen deine eigene.', 'Do, nimm de Ahänger, er wird d i gege Magie schütze. Sogar gege dini eiget.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cynthia_Mages', 'dl_npc_cynthia', 'Yes, that''s the only correct choice. We don''t need the commoners anymore.', 'Ja, das ist die einzig richtige Wahl. Wir brauchen die Gewöhnlichen nicht mehr.', 'Jo, das isch di einzig richtig Wahl. Mir bruched di Gwöhnliche nümm.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cynthia_Mages2', 'dl_npc_cynthia', 'Everything they can do, we can do even better.', 'Alles was sie können, können wir noch besser.', 'Alles was si chönd, chömmer no besser.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cynthia_Mages3', 'dl_npc_cynthia', 'Here, take this pendant. It will enhance your magic.', 'Hier, nimm diesen Anhänger. Er wird deine Magie verstärken.', 'Do, nimm de Ahänger. Er wird dini Magie verstärche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cynthia_WhySide', 'dl_npc_cynthia', 'Because one day, there will only be one. So...', 'Weil irgendwann wird es nur noch eine Seite geben. Also...', 'Well irgendwenn wirds nur no ei Siite gä. Also...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cynthia_Busy', 'dl_npc_cynthia', 'I really got other things to do. Leave me alone.', 'Ich hab nun wirklich anderes zu tun. Lass mich in Ruhe.', 'Ich ha ez würkli anders ztue. Loh mi in Rue.');
