INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Edgar_WhatDoWeHave', 'dl_npc_edgar', 'Well, well, look at that. Another filthy thief, caught by our observer spells. I hope you enjoy your stay. He he he.', 'Was haben wir denn hier. Noch so ein dreckiger Dieb, der von unseren Beobachter-Zaubern hierhergebracht wurde. Ich hoffe, du geniesst deinen Aufenthalt hier. Hrhr.', 'Ja lueg mal, wa hemmer denn da. Nomol so en Dieb wo vo üsne Beobachter-Zauber daane verfrachtet worde isch. Ich hoffe, du gnüssisch din Ufenthalt da. Hrhr.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereAreMyThings', 'dl_npc_edgar', 'Where are my belongings?', 'Wo sind meine Sachen?', 'Wo sind mini Sache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HowGetout', 'dl_npc_edgar', 'How can I get released?', 'Wie kann ich wieder freigelassen werden?', 'Wie chani wider usegla werde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ImHungry', 'dl_npc_edgar', 'I''m hungry, can I get something to eat?', 'Ich habe Hunger, krieg ich was zu essen?', 'Ich han Hunger, bechumi öppis zum esse?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ImStillHungry', 'dl_npc_edgar', 'I''m still hungry.', 'Ich habe immer noch Hunger.', 'Ich han immerna Hunger.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HowGold', 'dl_npc_edgar', 'How can I earn the gold to redeem myself?', 'Wie kann ich Gold verdienen, um mich freizukaufen?', 'We chani Gold verdiene zum mich freichaufe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IronOre', 'dl_npc_edgar', 'I got some iron ore for you.', 'Ich habe etwas Eisenerz für dich.', 'Ich han chli Isenerz für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IHaveGold', 'dl_npc_edgar', 'Please let me out.', 'Lass mich bitte raus.', 'Lah mich bitte use.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Edgar_YourThings', 'dl_npc_edgar', 'Did you really think we''d leave a mage his weapon? The only thing you''ll need here is a pickaxe anyway.', 'Hast du wirklich gedacht, wir lassen einem Magier seine Waffe? Eine Spitzhacke ist sowieso das einzige, was du hier brauchen wirst.', 'Hesch würkli denkt, mer lönd emne Magier sini Waffe? Es einzig wod da bruchsch isch sowiso nur din Pickel.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Edgar_YourThings2', 'dl_npc_edgar', 'But your old belongings are stored safely. You''ll get them back on the day you''re released.', 'Aber deine alten Habseligkeiten sind bei uns sicher aufbewahrt. Du kriegst sie wieder, wenn du hier entlassen wirst.', 'Aber dini alte Sache sind bi üs guet ufghobe. Du bechunsch si wider a dem Tag womer dich da entlönd.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Edgar_HowGetout', 'dl_npc_edgar', 'You will have to work in the mines until you can redeem yourself. Gandria won''t pay a single coin for you filthy criminals.', 'Du wirst in den Minen arbeiten, bis du dich freikaufen kannst. Gandria wird keine einzige Münze für euch Abschaum bezahlen.', 'Du wirsch ide Mine schaffe möse bis dich freichaufe chasch. Gandria wird ke einzigi Münze für eu Schlufis zahle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Edgar_WaterAndBread', 'dl_npc_edgar', 'Sure, here you go. You better get used to it, you won''t get anything else for a long time.', 'Sicher, hier hast du was. Gewöhn dich besser dran, du wirst für eine lange Zeit nichts anderes kriegen.', 'Sicher, nimm das. Gwöhn dich besser dra, bechunsch für e langi Ziit nüt anders.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Edgar_HungryAgain', 'dl_npc_edgar', 'You already got your ration. Now get out of my sight!', 'Du hast deine Ration bereits bekommen. Jetzt geh mir aus den Augen.', 'Du hesch dini Ration scho becho. Verpiss dich ez besser.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Edgar_Pickaxe', 'dl_npc_edgar', 'Here, take this pickaxe and mine iron ore in the mines. You can then sell it to me until you have enough gold to be released.', 'Hier, nimm diese Spitzhacke und bau Eisenerz in den Minen ab. Das kannst du mir verkaufen, bis du genügend Gold hast, um hier rauszukommen.', 'Da, nimm die Spitzhacke und bau Iseerz ide Mine ab. Da chaschmer denn verchaufe bis gnueg Gold hesch zum da usecho.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Edgar_Ironore', 'dl_npc_edgar', 'Thank you. Here is your pay.', 'Danke. Hier, dein Lohn.', 'Danke. Da, din Lohn.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Edgar_NotEnoughGold', 'dl_npc_edgar', 'You don''t have enough gold. Go and work a bit more.', 'Du hast nicht genügend Gold. Geh und arbeite noch ein bisschen.', 'Du hesch nid gnueg Gold. Gang na bitz go schaffe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Edgar_YoureReleased', 'dl_npc_edgar', 'Okay, that''s enough. You''re free to go. I hope I won''t see you here again.', 'Okay, das ist genug. Du kannst jetzt gehen. Ich hoffe bloss, dass ich dich hier nie mehr sehen muss.', 'Guet, da isch gnueg. Du chasch ez ga. Ich hoff eifach dasi dich da nie me gse mun.');
