INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_Hello', 'dl_npc_etoz', 'You there! What is a milksop like you doing in the sewers?', 'Du da! Was macht so ein Milchgesicht wie du in der Kanalisation?', 'Hey du! Was het so es Milchbubi da unne ide Kanalisation z''sueche?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YouMilksob', 'dl_npc_etoz', 'I could ask you the same thing.', 'Ich könnte dich dasselbe fragen.', 'Ich chönt dich es gliich fröge.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Unfriendly', 'dl_npc_etoz', 'Are you always this ill-bred?', 'Bist du immer so unhöflich?', 'Bisch du immer so unhöflich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoTalk', 'dl_npc_etoz', 'I have more important stuff to do.', 'Ich habe gerade Wichtigeres zu tun.', 'Ich ha grad Wichtigeres ztue.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_YouMilksob', 'dl_npc_etoz', 'Hn. You don''t seem to know who you''re talking to.', 'Hn. Du weisst wohl nicht, mit wem du sprichst.', 'Hn. Du weisch glaub nid, mit wem du redsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_Unfriendly', 'dl_npc_etoz', 'Oh, I''m not ill-bred. On the contrary - I''m only telling the truth.', 'Oh, ich bin nicht unhöflich. Im Gegenteil - ich sage bloss die Wahrheit.', 'Oh, ich bi nid unhöflich. Im Gegeteil - ich seg nur d''Woret.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_etoz', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_QuestionableMethod', 'dl_npc_etoz', 'Have you seen any nasty creatures in the sewers?', 'Hast du irgendwelche garstigen Kreaturen in der Kanalisation gesehen?', 'Hesch du so garstigi Kreature ide Kanalisation gse?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Sword', 'dl_npc_etoz', 'What is this strange sword you''re carrying around?', 'Was ist dieses seltsame Schwert, das du mit dir herumträgst?', 'Wa isch da komisch Schwert, wod mit der umetreisch?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Powerful', 'dl_npc_etoz', 'And what exactly makes you this "powerful"?', 'Und was genau macht dich so "mächtig"?', 'Und wa genau macht dich so "mächtig"?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Challenge', 'dl_npc_etoz', 'You just seem to be talking big...', 'Du scheinst einfach eine grosse Klappe zu haben...', 'Du hesch glaub eifach chli e grossi Fresse...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotWeak', 'dl_npc_etoz', 'You underestimate me. I''m no weakling.', 'Du unterschätzt mich. Ich bin kein Schwächling.', 'Du unterschätzisch mich. Ich bi ken Lappe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_StartKeyOne', 'dl_npc_etoz', 'Tell me more about that "dangerous" job.', 'Erzähl mir mehr von diesem "gefährlichen" Auftrag.', 'Verzell mer me vo dem "gföhrliche" Uftrag.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_WhoAreYou', 'dl_npc_etoz', 'It surprises me that you haven''t heard of me yet.', 'Es überrascht mich, dass du noch nicht von mir gehört hast.', 'Es überrascht mi, dass nanüt vo mir ghört hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_WhoAreYou2', 'dl_npc_etoz', '(Flips his hair back) I''m Etoz, the most powerful mage you''ll ever see. ', '(Streicht sein Haar zurück) Ich bin Etoz, der mächtigste Magier, den du jemals sehen wirst.', '(Striicht sini Hoor zrugg) Ich bi de Etoz, de mächtigst Magier wod je gse wirsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_QuestionableMethod', 'dl_npc_etoz', 'Well, one is standing right in front of me. (Giggles)', 'Nun, eine steht gerade vor mir. (Kichert)', 'Jeh, eini stoht grad vor mir. (Giggelet)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_QuestionableMethod2', 'dl_npc_etoz', 'But if you are looking for nasty sewer rats and stuff, I saw some in the southern part of the sewers.', 'Aber wenn du garstige Kanalisationsratten und so suchst, da habe ich welche im südlichen Teil der Kanalisation gesehen.', 'Aber wennd garstigi Kanalisationsratte und so suechsch, do hani es paar im südliche Teil vode Kanalisation gse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_QuestionableMethod3', 'dl_npc_etoz', 'But you''ll need not only a key to go there...', 'Aber du brauchst nicht nur einen Schlüssel, um dorthinzukommen...', 'Aber du bruuchsch nid nur en Schlüssel, zum det anecho...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_QuestionableMethod4', 'dl_npc_etoz', 'You clearly lack what it takes to survive down here.', 'Dir fehlt es definitiv an allem, was es braucht, um hier unten zu überleben.', 'Dir fehlts definitiv a allem, wos brucht, zum da unne überlebe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_Sword', 'dl_npc_etoz', 'That''s no "strange" sword, you fool. You better hope that you''ll never get in my way.', 'Das ist kein "seltsames" Schwert, du Narr. Hoffe einfach, dass du mir nie in die Quere kommst.', 'Das isch kes "komisches" Schwert, du Michi. Chasch eifach hoffe, dassd mer nie id Queri chunsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_Sword2', 'dl_npc_etoz', 'This sword would be the last thing you''d see.', 'Dieses Schwert wäre das letzte, was du sehen würdest.', 'Da Schwert wers letzt, wod gse würsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_Powerful', 'dl_npc_etoz', 'Oh, if I would tell you of my adventures, you would tremble in awe.', 'Oh, wenn ich dir meine Abenteuer erzählte, würdest du in Ehrfurcht erzittern.', 'Oh, wenn ich dir mini Abetür verzelle wür, würsch in Ehrfurcht erzittere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_Powerful2', 'dl_npc_etoz', 'I''ve slain countless foes, cast the most complex spells and explored the most dangerous areas... Those stories are nothing for your feeble ears.', 'Ich habe unzählige Feinde besiegt, die komplexesten Zauber gewirkt und die gefährlichsten Gebiete erkundet... diese Geschichten wären nichts für deine zarten Ohren.', 'Ich ha unzähligi Finde besiegt, di schwirigste Zauber zauberet und die gföhrlichste Gebiet erkundet... die Gschichte wäred nüt für dini zarte Ohre.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_Challenge', 'dl_npc_etoz', 'How dare you doubt my skills. I could make you burst into flames with a single word.', 'Wie kannst du es wagen, meine Fähigkeiten anzuzweifeln. Ich könnte dich mit einem einzigen Wort in Flammen aufgehen lassen.', 'Wie chasches du bloss woge, mini Fähigkeite aaz''zwiifle. Ich chönt dich mitme einzige Wort i Flamme ufgo lo.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_Challenge2', 'dl_npc_etoz', 'But I understand your inexperienced eyes can''t recognise a true master. I''ll forgive you for this one time.', 'Aber ich verstehe, dass deine unerfahrenen Augen keinen wahren Meister erkennen können. Dieses eine Mal werde ich dir noch vergeben.', 'Aber ich verstand, dass dini unerfahrene Auge ken wohre Meister erchenne chönd. Damol wirdi der no verge.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_Challenge3', 'dl_npc_etoz', 'Hn. You seem so weak, so helpless... fighting you wouldn''t even be fun.', 'Hn. Du scheinst so schwach, so hilflos zu sein... dich zu bekämpfen würde keinen Spass machen.', 'Hn. Du schinsch so schwach, so hilflos z''sii... dich z''bekämpfe wür nid mal Spass mache.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_NotWeak', 'dl_npc_etoz', '(Etoz raises one eyebrow) Oh, really? Well, you must be such a tough boy then!', '(Etoz hebt eine Augenbraue) Oh, wirklich? Dann musst du ja ein ganz harter Junge sein!', '(De Etoz hebt e Augebraue) Oh, würkli? Denn muesch ja en ganz herte Siech si!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_NotWeak2', 'dl_npc_etoz', 'It is by sheer coincidence that I know some adventure that would be a perfect fit for such a tough one like you.', 'Wie es der Zufall will, wüsste ich da ein Abenteuer, das genau das richtige für einen so harten Jungen wie dich sein würde.', 'Wie''s de Zuefall wett, würi da es Obetüür kenne wo genau richtig wär für so en herte Siech we dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_NotWeak3', 'dl_npc_etoz', 'Lots of gold and glory awaiting you. But, I guess you''re not interested in the real dangerous jobs...', 'Viel Gold und Ruhm würde dich erwarten. Aber ich nehme an, die wirklich gefährlichen Aufgaben interessieren dich nicht...', 'Viel Gold und Ruhm wür di erwarte. Aber ich glaub, di würkli gföhrliche Jobs interessiered di gar nid...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_StartKeyOne', 'dl_npc_etoz', 'If you insist... (Etoz takes a part of a key out of his cloak)', 'Wenn du so darauf beharrst... (Etoz nimmt einen Teil eines Schlüssels aus seinem Mantel)', 'Wennd so druf beharrsch... (De Etoz nimmt ein Teil vomne Schlüssel us sim Mantel)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_StartKeyOne2', 'dl_npc_etoz', 'That''s half of a key opening a dangerous tower.', 'Das ist die Hälfte eines Schlüssels, der einen gefährlichen Turm aufschliesst.', '');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_StartKeyOne3', 'dl_npc_etoz', 'If you''re really so tough, get the other part of the key and I''ll give you this part.', 'Wenn du wirklich so hart bist, hol den anderen Teil des Schlüssels und ich gebe dir meinen Teil.', 'Wenn du würkli so hert bisch, hol de ander Teil vom Schlüssel und ich gib der min Teil.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereKeyPart', 'dl_npc_etoz', 'Where can I find the other part?', 'Wo kann ich den anderen Teil finden?', 'Wo chani de ander Teil finde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereYourPart', 'dl_npc_etoz', 'Where did you get your part from?', 'Woher hast du deinen Teil?', 'Woher hesch denn din Teil?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotKey', 'dl_npc_etoz', 'I got my part of the key.', 'Ich habe meinen Teil des Schlüssels.', 'Ich ha min Teil vom Schlüssel.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_WhereKeyPart', 'dl_npc_etoz', 'This would''ve been up to you to find out. But as you''re so clueless and pitiful, I''ll tell you.', 'Das herauszufinden, wäre deine Aufgabe gewesen. Aber weil du so hilflos und bemitleidenswert bist, werde ich es dir sagen.', 'Das usezfinde wer dini Ufgob gsi. Aber welld so hilflos und bemitleidenswert bisch, segis der.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_WhereKeyPart2', 'dl_npc_etoz', 'There''s a small room in the East of the sewers. You''ll find it there.', 'Es gibt einen kleinen Raum im Osten der Kanalisation. Du wirst ihn dort finden.', 'Es git en chline Ruum im Oste vode Kanalisation. Du wirschen det finde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_WhereYourPart', 'dl_npc_etoz', 'Oh. I had to climb the highest mountains, dive into the deepest sea... cross lava pits and fight dangerous beasts...', 'Oh. Ich musst die höhesten Berge erklimmen, in das tiefste Meer tauchen... musste mich durch Lava-Gruben schlagen und fürchterliche Biester besiegen.', 'Oh. Ich ha di höchste Berge möse erklimme, i di tüfste See tauche... ha mi möse dur Lava-Gruebe kämpfe und schrecklichi Viecher besiege.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_WhereYourPart2', 'dl_npc_etoz', 'Your part is so much easier to get. Be glad.', 'Dein Teil ist so viel einfacher zu holen. Sei froh.', 'Din Teil isch soviel eifacher z''hole. Sig froh.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_GotKey1', 'dl_npc_etoz', 'Easy, wasn''t it. Now let''s combine it...', 'War einfach, nicht wahr. Jetzt müssen wir ihn nur noch zusammensetzen...', 'Eifach gsi, gell. Ez mömmern nur na zemesetze...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_GotKey12', 'dl_npc_etoz', 'And it''s done. Here, take it.', 'Und fertig. Hier, nimm ihn.', 'Und fertig. Do, nimmen.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_GotKey13', 'dl_npc_etoz', 'Find the old tower it opens and show me, what a big boy you are.', 'Finde den Turm, den er öffnet, und zeige mir, was du für ein grosser Junge bist.', 'Find de Turm woner ufmacht und zeigmer, wad füren grosse Bueb bisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_GotKey14', 'dl_npc_etoz', 'Reach the chest on top of the tower and come back to me if you survive...', 'Erreiche die Kiste ganz oben auf dem Turm und komm zu mir zurück, wenn du überleben solltest...', 'Erreich d''Chiste ganz obe ufem Turm und chum zumer zrugg, wennd überlebsch...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereTower', 'dl_npc_etoz', 'Where is this tower?', 'Wo ist dieser Turm?', 'Wo isch de Turm?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyDontYouGo', 'dl_npc_etoz', 'Why don''t you go there yourself?', 'Wieso gehst du nicht selbst hin?', 'Weso gasch nid selber ane?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TowerInfo', 'dl_npc_etoz', 'Tell me more about this tower.', 'Erzähl mir mehr über diesen Turm.', 'Verzell mer me vo dem Turm.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AntiMagic', 'dl_npc_etoz', 'What exactly are "anti-magic crystals"?', 'Was genau sind diese "Antimagiekristalle"?', 'Wa genau sind die "Antimagiekristall?"');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_WhereTower', 'dl_npc_etoz', 'On a forgotten island to the East of the marshland. I hope you know how to swim. (Grins)', 'Auf einer vergessenen Insel im Osten des Sumpflands. Ich hoffe, du kannst gut schwimmen. (Grinst)', 'Ufere vergessene Insle im Oste vom Sumpfland. Ich hoff, du chasch guet schwümme. (Grinst)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_WhyDontYouGo', 'dl_npc_etoz', 'I''ve been there countless times. It''s getting boring, you know.', 'Ich war da schon unzählige Male. Es wird langsam langweilig, weisst du.', 'Ich bi scho unzähligi Mol gsi. Es wird mer langsam langwiilig, weisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_WhyDontYouGo2', 'dl_npc_etoz', 'But you, you need to show me what you got. We''ll see whether you make it.', 'Aber du musst mir noch etwas beweisen. Mal schauen, ob du es hinkriegst.', 'Aber du muesch mer no öppis bewiise. Mol luege, ob dus anebringsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_TowerInfo', 'dl_npc_etoz', 'The tower was built a long time ago by a mage who was nearly as powerful as me.', 'Der Turm wurde vor langer Zeit von einem Magier errichtet, der fast so mächtig war, wie ich.', 'De Turm isch vomne Magier errichtet worde, wo fasch so mächtig gsi isch, we ich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_TowerInfo2', 'dl_npc_etoz', 'He put anti-magic crystals in there to test the real willpower and agility of fellow mages.', 'Er setzte Antimagiekristalle ein, um die wahre Willenskraft und Gewandtheit anderer Magier zu testen.', 'Er het Antimagiekristall igsetzt, zum die wohr Willenschraft und Gwandtheit vo anderne Magier z''teste.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_TowerInfo3', 'dl_npc_etoz', 'As most mages rely only on their magic, it was an impossible task for many of them.', 'Da die meisten Magier sich immer nur auf ihre Magie verlassen, war es eine unmögliche Aufgabe für viele von ihnen.', 'Well sich di meiste Magier immer nur uf ihri Magie verlönd, isches e unmöglichi Ufgab für vili Magier gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_TowerInfo4', 'dl_npc_etoz', 'But the ones who did it - including myself of course - grew stronger than ever before...', 'Aber diejenigen, die es geschafft haben - inklusive mir, natürlich - waren danach stärker als je zuvor.', 'Aber die, wos gschafft hend - inklusive mir natürli - sind nochene stärcher gsi als je zuvor.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_AntiMagic', 'dl_npc_etoz', 'Oh, you''re so pathetic. I really have to explain everything.', 'Oh, du bist so erbärmich. Dir muss man wirklich alles erklären.', 'Oh, du bisch so erbärmlich. Dir mume würkli alles erkläre.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Etoz_AntiMagic2', 'dl_npc_etoz', 'Those crystals block your magic. You won''t be able to use it there.', 'Diese Kristalle blockieren deine Magie. Du kannst dort drin nicht zaubern.', 'Die Kristall blockiered dini Magie. Du chasch det inne nid zaubere.');
