INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_Hey', 'dl_npc_graham', 'Oh, hello! You really came from that creepy cave? You know that it''s forbidden to go there?', 'Oh, hallo! Du bist wirklich aus dieser unheimlichen Höhle gekommen? Du weisst schon, dass es verboten ist, hineinzugehen?', 'Oh, hallo! Du bisch würkli us dere unheimliche Höhli cho? Du weisch scho, dasses verbote isch, det ie z''go?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_graham', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ForbiddenCave', 'dl_npc_graham', 'Why is it forbitten to go into that cave?', 'Wieso ist es verboten, in diese Höhle zu gehen?', 'Weso isches verbote, i die Höhli z''go?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_King', 'dl_npc_graham', 'A royal bard? So do you know the king?', 'Ein königlicher Barde? Kennst du denn den König?', 'En königliche Bard? Kennsch also de König?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Velius', 'dl_npc_graham', 'Do you know someone called "Velius"?', 'Sagt dir der Name "Velius" etwas?', 'Seit der de Name "Velius" öppis?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatDoing', 'dl_npc_graham', 'What are you doing here?', 'Was machst du hier?', 'Wa machsch du do?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotDulcimer', 'dl_npc_graham', 'I found your instrument. On a tree.', 'Ich habe dein Instrument gefunden. Auf einem Baum.', 'Ich ha dis Instrument gfunde. Imne Baum.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_WhoAreYou', 'dl_npc_graham', 'I''m Graham Teawenth, royal bard. Nice to meet you!', 'Ich bin Graham Teawenth, köngiglicher Barde. Freut mich!', 'Ich bi de Graham Teawenth, en königliche Bard. Freut mich!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_ForbiddenCave', 'dl_npc_graham', 'It was an order from the king himself. Apparently, it''s dangerous.', 'Es war eine Anweisung des Königs höchstpersönlich. Offensichtlich ist es gefährlich.', 'Es isch e Awisig vom König höchstpersönlich gsi. Offesichtlich isches gföhrlich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_ForbiddenCave2', 'dl_npc_graham', 'I don''t know why they don''t just barricade it.', 'Ich weiss nicht, wieso sie sie nicht einfach verbarrikadieren.', 'Ich weiss nid, weso si si nid eifach verbarrikadiered.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_King', 'dl_npc_graham', 'Logan the Second? Yes, yes I know him. But he was recently replaced by his weird son.', 'Logan der Zweite? Ja, ja, ich kenne ihn. Aber er wurde kürzlich von seinem seltsamen Sohn ersetzt.', 'De Logan de Zweit? Jojo, ich kennen. Aber er isch chürzlich vo sim komische Sohn ersetzt worde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_King2', 'dl_npc_graham', 'The old king loved to listen to my songs. But the prince... well, he''s not interested in music.', 'Der alte König hat meine Lieder sehr gemocht. Aber der Prinz... Nun, er interessiert sich nicht für Musik.', 'De alt König het mini Lieder sehr gern gha. Aber de Prinz... Naja, er interessiert sich nid für Musig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_King3', 'dl_npc_graham', 'He doesn''t seem interested in anything. Really weird kid. So I''m just sitting here in the gardens and play for the court.', 'Er interessiert sich sowieso für gar nichts. Ein wirklich seltsames Kind. Also sitze ich einfach hier in den Gärten und spiele für den Hofstaat.', 'Er interessiert sich sowieso für gar nüt. Es würkli komisches Chind. Also sitzi eifach do ide Gärte und spiel für de Hofstaat.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_Velius', 'dl_npc_graham', 'Hmm... Yep, that''s one of the royal mages. I haven''t seen him in a while. He''s a pretty reserved kind of guy.', 'Hmm... Yep, das ist einer der königlichen Magier. Ich habe ihn schon lange nicht mehr gesehen. Er ist eine ziemlich verschlossene Persönlichkeit.', 'Hmm... Yep, das isch eine vode königliche Magier. Hanen scho länger nüm gse. Er isch irgendwie en zimli verschlosse Typ.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_WhatDoing', 'dl_npc_graham', 'I''m looking for my instrument, a dulcimer. ', 'Ich suche mein Instrument, eine Dulcimer.', 'Ich suech mis Instrument, e Dulcimer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_WhatDoing2', 'dl_npc_graham', 'Some big raven stole it and carried it into the castle gardens.', 'Eine grosse Krähe hat sie gestohlen und in die Schlossgärten getragen.', 'En grosse Rabevogel het si klaut und id Schlossgärte treit.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_WhatDoing3', 'dl_npc_graham', 'But what''s a royal bard without his instrument?', 'Aber was ist ein königlicher Barde ohne sein Instrument?', 'Aber was isch isch en königliche Bard ohni sis Instrument?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_WhatDoing4', 'dl_npc_graham', 'So, if you happen to find it and bring it back to me, I''ll give you lots of gold!', 'Also, wenn du sie zufälligerweise finden solltest, bring sie mir wieder. Ich gebe dir viel Gold dafür!', 'Also, wenn du si per Zuefall finde sötsch, bring si mer wieder. Ich gibder viel Gold defür!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_GotDulcimer', 'dl_npc_graham', 'Oooooh! My dulcimer! (Graham takes it and strokes it tenderly)', 'Oooooh! Meine Dulcimer! (Graham nimt sie und streicht liebevoll darüber)', 'Oooooh! Mini Dulcimer! (De Graham nimmt si und stricht liebevoll drüber)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_GotDulcimer2', 'dl_npc_graham', 'Thank you so much, I really appreciate your help. Here, take this gold.', 'Vielen Dank, ich schätze deine Hilfe wirklich sehr. Hier, nimm dieses Gold.', 'Merci tusig, ich schätz dini Hilf würkli sehr. Do, nimm das Gold.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Graham_GotDulcimer3', 'dl_npc_graham', 'Now I can finally play it again!', 'Jetzt kann ich endlich wieder auf ihr spielen!', 'Jetz chani endlich wider uf ihre spiele!');
