INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_harek', 'Are you the blacksmith here?', 'Bist du der Schmied hier?', 'Bisch du de Schmied da?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_IAmSmith', 'dl_npc_harek', 'Yes, and I''m the best one around here. I have some weapons and armour here, if you''re interested.', 'Ja, und ich bin der beste, den du hier finden kannst. Ich habe auch einige Rüstungen und Waffen im Angebot, falls du interessiert bist.', 'Ja, und ich bi de best wod da finde wirsch. Han au grad paar Waffe und Rüstige im Aagebot falls interessiert bisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_WhoAreYou2', 'dl_npc_harek', 'I also take on more special jobs, if you can bring me the materials.', 'Ich nehme auch speziellere Sachen in Auftrag, falls du mir die Materialen besorgen kannst.', 'Ich nim au spezielleri Sache in Uftrag, falls du mir d''Materialie defür bsorge chasch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Trade', 'dl_npc_harek', 'Show me your wares.', 'Zeig mir deine Waren.', 'Zeig mir dini Ware.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Thea', 'dl_npc_harek', 'Could you use an apprentice?', 'Könntest du einen Lehrling gebrauchen?', 'Chöntsch en Lehrling bruche?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RivetArmor', 'dl_npc_harek', 'Could you smith me a special armour?', 'Könntest du mir eine spezielle Rüstung anfertigen?', 'Chöntsch du mir e spezielli Rüstig schmide?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BrokenStaff', 'dl_npc_harek', 'I got this broken weapon here, can you fix it?', 'Ich habe diese zerbrochene Waffe hier, kannst du sie flicken?', 'Ich han die verbroche Waffe da, chasch du si flicke?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BrokenStaffComplete', 'dl_npc_harek', 'I got all the parts of the broken staff.', 'Ich habe alle Teile des zerbrochenen Stabs gefunden.', 'Ich han all Teili vom verbrochene Stab gfunde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotIronOre', 'dl_npc_harek', 'I got the iron ore for you.', 'Ich habe das Eisenerz für dich.', 'Ich hans Iseerz für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Craft', 'dl_npc_harek', 'Could you craft something for me?', 'Könntest du mir etwas anfertigen?', 'Chöntsch du mir öppis herstelle?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_Thea', 'dl_npc_harek', 'You?', 'Dich?', 'Dich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_Thea', 'dl_npc_harek', 'No, a girl called Thea is looking for a job.', 'Nein, ein Mädchen namens Thea sucht Arbeit.', 'Nei, es Meitli namens Thea suecht Arbet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_Thea2', 'dl_npc_harek', 'Hm. No. This isn''t a job for a girl.', 'Hm. Nein. Das ist keine Arbeit für ein Mädchen.', 'Hm. Nei. Das isch ke Arbet für es Meitli.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_Thea3', 'dl_npc_harek', 'But you could ask Syrah, the alchemist. She was looking for an apprentice.', 'Aber du könntest Syrah, die Alchemistin, fragen. Sie hat einen Lehrling gesucht.', 'Aber du chöntsch d''Syrah, d''Alchemistin, fröge. Si het en Lehrling gsuecht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_IceArmor', 'dl_npc_harek', 'Of course. You look like a mage, so I guess you could use a magic-infused armour.', 'Natürlich. Du siehst wie ein Magier aus, also denke ich, du könntest eine Magie-infundierte Rüstung gebrauchen.', 'Natürli. Du gsesch we en Magier us, also chöntsch vermuetlich e Magie-infundierti Rüstig bruche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_IceArmor2', 'dl_npc_harek', 'But I''m currently really short on material. First I''d need something that holds the magic...', 'Aber ich habe momentan ziemlich wenig Ressourcen. Zuerst brauche ich etwas magisches dafür... Nur was?', 'Aber ich ha grad zimli wenig Ressource. Zersch mal bruchi nämli öppis magisches...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IHaveHeart', 'dl_npc_harek', 'I got this special looking stone here... (Show Heart of Khajag)', 'Ich habe diesen speziellen Stein hier... (Herz von Khajag zeigen)', 'Ich ha de speziell Stei da... (Herz vom Khajag zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IDontHaveMagic', 'dl_npc_harek', 'Maybe later.', 'Vielleicht später.', 'Vilicht spöter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_NiceStone', 'dl_npc_harek', 'Oh, that''s perfect. I think I could use that for the magic part of the armour.', 'Oh, das ist perfekt. Ich glaube, den könnte ich für den magischen Teil der Rüstung brauchen.', 'Oh, da isch perfekt. Ich glaub, de chönti für de magisch Teil vode Rüstig bruche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_NiceStone2', 'dl_npc_harek', 'However, I still need some iron ore. If you could bring me some from the mine of Gandria, I''ll smith you your armour right away!', 'Ich brauche allerdings auch noch etwas Eisenerz. Wenn du mir etwas aus den Minen von Gandria bringen kannst, werde ich dir deine Rüstung sofort anfertigen.', 'Ich bruch allerdings au na chli Iseerz. Wend mer chli chöntsch bringe us de Mine vo Gandria, wird ich dir dini Rüstig sofort schmiede!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_BrokenStaff', 'dl_npc_harek', '(Looks at the broken part) Hm, that seems to be a part of a rather interesting staff. If you can bring me all pieces of it, I might be able to fix it.', '(Sieht sich das zerbrochene Stück an) Hm, das scheint ein Teil eines ziemlich interessanten Stabs zu sein. Wenn du mir alle Teile davon bringen kannst, werde ich ihn vielleicht reparieren können.', '(Luegt sichs verbrochene Stück ah) Hm, das schiint mer es Stück vomne zimlich interessante Stab z''si. Wenn du mir all Stück devo bringe chasch, wirdin dir vilicht repariere chöne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_BrokenStaffComplete', 'dl_npc_harek', 'Great! Let''s see... (Takes out some tools and starts working...)', 'Grossartig! Lass mal sehen... (Nimmt ein paar Werkzeuge und legt die Stücke auf den Amboss)', 'Guet gmacht! Mal luege... (Nimmt es paar Werkzüg use und leit d''Stück uf de Amboss...)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_BrokenStaffComplete2', 'dl_npc_harek', '... And finished. Here you go!', '... Und fertig. Hier hast du ihn!', '... Und fertig. Da heschen!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_SmithMagicArmor', 'dl_npc_harek', 'Very good. (Harek takes the ingredients and starts hammering on his anvil furiously...)', 'Sehr gut. (Harek nimmt die Materialien und beginnt, wie wild auf seinem Amboss herumzuhämmern...)', 'Sehr guet. (De Harek nimmt d''''Zuetate und fangt ah we wild uf sim Amboss umezhämmere...)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_SmithMagicArmor2', 'dl_npc_harek', 'Here you go. Splendid, isn''t it.', 'Hier ist sie. Prächtig, nicht wahr.', 'Da isch si. Prächtig, gell.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_SmithMagicArmor3', 'dl_npc_harek', 'You can always come back to me if you find other special materials, I''d love working with them.', 'Du kannst immer wieder zu mir kommen wenn du spezielles Material findest - Es ist interessant, es zu verarbeiten.', 'Du chasch immer wider zu mir cho wend speziells Material findsch - Es isch interessant, demit z''schaffe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_Craft', 'dl_npc_harek', 'Sure, did you find some special materials and some iron ore?', 'Sicher, hast du spezielle Materialen und etwas Eisenerz gefunden?', 'Sicher, hesch denn speziells Material und chli Iseerz gfunde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IceScarf', 'dl_npc_harek', 'eq_icescarf,mi_icecrystal,1,mi_ironore,2,gold,40', 'eq_icescarf,mi_icecrystal,1,mi_ironore,2,gold,40', 'eq_icescarf,mi_icecrystal,1,mi_ironore,2,gold,40');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_PearlSabre', 'dl_npc_harek', 'we_pearlsabre,mi_pearlpile,1,mi_ironore,3,gold,75', 'we_pearlsabre,mi_pearlpile,1,mi_ironore,3,gold,75', 'we_pearlsabre,mi_pearlpile,1,mi_ironore,3,gold,75');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LightningPike', 'dl_npc_harek', 'we_lightningpike,mi_elysiatail,1,mi_ironore,5,gold,100', 'we_lightningpike,mi_elysiatail,1,mi_ironore,5,gold,100', 'we_lightningpike,mi_elysiatail,1,mi_ironore,5,gold,100');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_JanusGlaive', 'dl_npc_harek', 'we_janusglaive,mi_janusglaive,1,mi_ironore,4,gold,100', 'we_janusglaive,mi_janusglaive,1,mi_ironore,4,gold,100', 'we_janusglaive,mi_janusglaive,1,mi_ironore,4,gold,100');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LavaArmor', 'dl_npc_harek', 'eq_lavaarmor,mi_corrupt_stone_fire,3,mi_shinystone,1,mi_ironore,5,gold,110', 'eq_lavaarmor,mi_corrupt_stone_fire,3,mi_shinystone,1,mi_ironore,5,gold,110', 'eq_lavaarmor,mi_corrupt_stone_fire,3,mi_shinystone,1,mi_ironore,5,gold,110');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CairnArmor', 'dl_npc_harek', 'eq_cairnarmor,mi_corrupt_stone_ice,3,mi_shinystone,1,mi_ironore,5,gold,110', 'eq_cairnarmor,mi_corrupt_stone_ice,3,mi_shinystone,1,mi_ironore,5,gold,110', 'eq_cairnarmor,mi_corrupt_stone_ice,3,mi_shinystone,1,mi_ironore,5,gold,110');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FireGlaive', 'dl_npc_harek', 'we_fireglaive,mi_yashafangs,1,mi_shinystone,2,mi_ironore,4,gold,130', 'we_fireglaive,mi_yashafangs,1,mi_shinystone,2,mi_ironore,4,gold,130', 'we_fireglaive,mi_yashafangs,1,mi_shinystone,2,mi_ironore,4,gold,130');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_PrismaStone', 'dl_npc_harek', 'I found this rainbow stone. Can you craft something from it?', 'Ich habe diesen Regenbogenstein gefunden. Kannst du etwas daraus herstellen?', 'Ich ha de Rägebogestei gfunde. Chasch öppis drus herstelle?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoMaterial', 'dl_npc_harek', 'I''ll come back later. [BACK]', 'Ich komme später wieder. [ZURÜCK]', 'Ich chume spöter wider. [ZRUGG]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_IceScarf', 'dl_npc_harek', 'An exeptionally beautiful stone! I can truly craft something magical from that.', 'Ein aussergewöhnlich schöner Stein! Daraus kann ich wahrlich etwas magisches herstellen.', 'En ussergwöhnlich schöne Stei! Da drus chani wohrlich öppis schöns mache.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_PearlSabre', 'dl_npc_harek', 'Magical pearls! It has been a long time since I''ve held some of them in my hands... They''ll make a fine sabre.', 'Magische Perlen! Es ist lange her seit ich solche in den Händen gehalten habe... Die werden einen hübschen Säbel abgeben.', 'Magischi Perle! Es isch lang her sit ich sonigi ide Händ gha ha... Die werded en prima Säbel ge.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_LightningPike', 'dl_npc_harek', 'The tail feathers of a storm bird? Great material for a magical lightning pike.', 'Die Schwanzfedern eines Sturmvogels? Ein hervorragendes Material für einen magischen Blitzspeer.', 'D''Schwanzfedere vomne Sturmvogel? Es usgzeichnets Material für en magische Blitzspeer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_JanusGlaive', 'dl_npc_harek', 'Hm, I really start to wonder where you get this stuff from.', 'Hm, langsam frage ich mich wirklich woher du diese Dinge bekommst die du mir bringst.', 'Hm, langsam frögi mi würkli woher du die Sache bechunsch wod mer bringsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_JanusGlaive2', 'dl_npc_harek', 'I can fix this broken weapon but I can only restore a part of its magical power.', 'Ich kann diese zerbrochene Waffe flicken, allerdings kann ich nur einen Teil ihrer magischen Kraft retten.', 'Ich cha die verbroche Waffe flicke, allerdings chani nur en Teil vo ihrere magische Chraft rette.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_LavaArmor', 'dl_npc_harek', 'Lava stones from a lava wraith! These will make a fine, fire-resistant armour.', 'Lavasteine von einem Lava Geist! Diese geben eine schöne, feuerresistente Rüstung ab.', 'Lavastei vomne Lava Geischt! Die gend e schöni, füürresistenti Rüstig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_CairnArmor', 'dl_npc_harek', 'Magic-infused stones from a cairn wraith! These will make a fine, ice-resistant armour.', 'Magie-infundierte Steine von einem Cairn Geist! Diese geben eine schöne, eisresistente Rüstung ab.', 'Magie-infundierti Stei vomne Cairn Geischt! Die gend e schöni, iisresistenti Rüstig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_FireGlaive', 'dl_npc_harek', 'Huge demonic fangs! And just look at the magical power flowing through them. This will make a powerful weapon.', 'Riesige dämonische Fangzähne! Durchtränkt mit magischer Energie. Das gibt eine mächtige Waffe ab.', 'Riesigi dämonischi Fangzäh! Durchtränkt mit magischer Energie. Das git e mächtigi Waffe ab.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_FireGlaive2', 'dl_npc_harek', 'I''ve never worked with something like this before. A truly amazing material.', 'Mit sowas habe ich noch nie gearbeitet. Ein wirklich prächtiges Material.', 'Mit sowas hani nonie gschaffet. Es würkli prächtigs Material.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_PrismaStone', 'dl_npc_harek', 'Hmm. Very interesting, indeed. It looks like condensed magic. I''ll cut that stone for free.', 'Hmm. Sehr interessant, wirklich. Sieht mir ganz nach konzentrierter Magie aus. Ich würde diesen Stein umsonst schleifen.', 'Hmm. Sehr interessant, würkli. Gseht mer ganz noch konzentrierter Magie us. Ich wür de Stei gratis schliffe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_PrismaStone2', 'dl_npc_harek', 'I could cut the stone to focus one color or we could keep all colors, but they''ll shine less.', 'Ich könnte den Stein so schleifen, dass er eine Farbe fokussiert, oder wir könnten alle Farben lassen, allerdings wären sie dann weniger kräftig.', 'Ich chönt de Stei so schliffe, daser ei Farb fokussiert, oder mer chönd au all Farbe lah,a ber si wäred weniger chräftig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Harek_PrismaStone3', 'dl_npc_harek', 'Which magic types or colours do you prefer?', 'Welche Magieart oder Farbe magst du denn haben?', 'Weli Magieart oder Farb wetsch denn ha?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingColor', 'dl_npc_harek', 'Let me choose by colour.', 'Lass mich die Farbe auswählen.', 'Ich wür gern d''Farb uswähle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingMagic', 'dl_npc_harek', 'Let me choose by magic type.', 'Lass mich die Art der Magie auswählen.', 'Ich wür gern d''Art vode Magie uswähle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingGreen', 'dl_npc_harek', 'Green.', 'Grün.', 'Grüen.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingPurple', 'dl_npc_harek', 'Purple.', 'Violett.', 'Violett.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingYellow', 'dl_npc_harek', 'Yellow.', 'Gelb.', 'Gäl.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingOrange', 'dl_npc_harek', 'Orange.', 'Orange.', 'Orange.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingRed', 'dl_npc_harek', 'Red.', 'Rot.', 'Rot.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingCyan', 'dl_npc_harek', 'Cyan.', 'Cyan.', 'Cyan.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingBlue', 'dl_npc_harek', 'Blue.', 'Blau.', 'Blau.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingRainbow.', 'dl_npc_harek', 'Give me all colours.', 'Gib mir alle Farben.', 'Gib mer alli Farbe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ChooseLater', 'dl_npc_harek', 'I''ll choose later.', 'Ich wähle später.', 'Ich wähle spöter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_RingHereYouGo', 'dl_npc_harek', 'Alright. Here you go.', 'Gut. Hier, nimm.', 'Guet. Do, nimm.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingHeal', 'dl_npc_harek', 'I need something that improves my health.', 'Ich brauche etwas, das meine Lebensenergie erhöht.', 'Ich bruch öppis, wo mini Lebensenergie erhöht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingHaste', 'dl_npc_harek', 'I like fast and critical attacks.', 'Ich mag schnelle und kritische Attacken.', 'Ich mag schnelli und kritischi Attacke.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingDivine', 'dl_npc_harek', 'I commited myself to the divine magic.', 'Ich habe mich der heiligen Magie verschrieben.', 'Ich ha mich de heilige Magie verschriebe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingPhysical', 'dl_npc_harek', 'I like to beat people without magic.', 'Ich mag es eigentlich, Leute ohne Magie zu verprügeln.', 'Ich mags eigentlich, Lüt ohni Magie z''verprügle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingFire', 'dl_npc_harek', 'I like to play with fire.', 'Ich spiele gerne mit Feuer.', 'Ich spiel gern mit Füür.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingIce', 'dl_npc_harek', 'Ice magic suits me.', 'Eismagie gefällt mir.', 'Ich ha gern Iismagie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingShadow', 'dl_npc_harek', 'Nothing beats shadow magic.', 'Es geht nichts über Schattenmagie.', 'Es gaht nüt über Schattemagie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RingEverything', 'dl_npc_harek', 'I like everything.', 'Ich mag alles.', 'Ich mag alles.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_RingThis', 'dl_npc_harek', 'Alright. This ring should fit you best.', 'Gut. Dieser Ring passt am besten zu dir.', 'Guet. De Ring passt am beste zu dir.');
