INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_Hello', 'dl_npc_helena2', 'Hey, hey you! You''re back? I thought you''d never return and pay for your room.', 'Hey, hey du! Du bist zurück? Ich dachte, du würdest nicht mehr auftauchen und dein Zimmer bezahlen.', 'Hey, hey du! Du bisch zrugg? Ich ha denkt du würsch nümme uftauche und dis Zimmer zale.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_helena2', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice', 'dl_npc_helena2', 'What room?', 'Was für ein Zimmer?', 'Was füres Zimmer?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HowYouKnow', 'dl_npc_helena2', 'You know me?', 'Du kennst mich?', 'Du kennsch mich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_Helena', 'dl_npc_helena2', 'I''m Helena, the innkeeper of this tavern. We''ve met before!', 'Ich bin Helena, die Wirtin dieser Taverne. Wir haben uns schon mal getroffen!', 'Ich bi d''Helena, d''Wirtin vo dere Taverne. Mer sind üs schomal begegnet!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_Room', 'dl_npc_helena2', 'Your room in this inn, of course?', 'Dein Zimmer in diesem Gasthaus?', 'Dis Zimmer i dem Gaschthuus?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_ComeIn', 'dl_npc_helena2', 'Yes, I do! But it''s been a while. I thought you were long gone.', 'Na klar! Aber es ist schon eine Weile her... Ich dachte, du wärst schon über alle Berge.', 'Natürlich! Aber es isch scho es Wiili her.. Ha denkt du bisch scho über alli Berge.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_ComeIn2', 'dl_npc_helena2', 'So, why don''t you come in and I''ll tell you everything inside.', 'Aber komm doch einfach rein und ich erzähl dir dann alles.', 'Aber chum doch eifach ie und ich verzell der denn alles.');
