INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TellMe', 'dl_npc_helena', 'Okay, tell me what you know about me.', 'Okay, sag mir, was du über mich weisst.', 'Okay, seg mer wa du über mich weisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_TellMe', 'dl_npc_helena', 'Oh, I don''t know much. You were here like a month ago, but only for a very short time.', 'Oh, ich weiss nicht viel. Du warst etwa vor einem Monat hier, allerdings nicht für sehr lange.', 'Oh, ich weiss nid viel. Du bisch öppe vorme Monet da gsi, aber nid für sehr lang.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_TellMe2', 'dl_npc_helena', 'You were in quite a hurry, you just left your luggage here and then rushed out again to fetch something you forgot from the ship... And I never saw you again.', 'Du warst ziemlich im Stress, hast nur dein Gepäck hier gelassen und bist dann wieder hinausgestürmt, weil du noch etwas auf dem Schiff vergessen hattest... Und dann bist du nicht mehr aufgetaucht.', 'Du bisch zimli im Stress gsi, hesch nur dis Züg rasch da anebracht und bisch den wieder usegrennt welld öppis ufem Schiff vergässe hesch... Und denn bisch nümm uftaucht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Ship', 'dl_npc_helena', 'What ship?', 'Was für ein Schiff?', 'Was füre Schiff?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OldThings', 'dl_npc_helena', 'Could I have my luggage back then?', 'Könnte ich mein Gepäck wiederhaben?', 'Derf ich mis Gepäck wider ha?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_Ship', 'dl_npc_helena', 'I just assumed you travelled by ship... You should know this better than me. ', 'Ich nehme an, du bist mit dem Schiff angereist... Du solltest das besser wissen als ich.', 'Ich nime ah, du bisch mit em Schiff agreist... Du sötsch das besser wüsse als ich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_Luggage', 'dl_npc_helena', 'Well... To be honest, I didn''t reckon that you''d return. So I''ve sold most of it... But some things are still there.', 'Nunja... Um ehrlich zu sein, ich habe wirklich nicht damit gerechnet, dass du wiederkommst. Deshalb habe ich das meiste verkauft. Aber ein paar Sachen sind immer noch da.', 'Naja... Zum ehrlich sii hani echt nid demit grechnet das du wider zrugg chunsch. Di meiste Sache hani verchauft... Aber es paar sind na ume.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_Luggage2', 'dl_npc_helena', 'I''ve kept the things that I couldn''t sell right here at the bar.', 'Ich habe die Sachen, die ich nicht verkaufen konnte, hier unter der Theke gelagert.', 'Ich ha die Sache woni nid ha chöne verchaufe grad da under de Bar glageret.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_Luggage3', 'dl_npc_helena', 'It''s your old bag... Useless stuff inside, but it''s all I have left. You don''t have to worry about the rent of your room, of course.', 'Es ist dein alter Rucksack... Hat nur noch unbrauchbare Dinge drin. Dafür musst du natürlich dein Zimmer nicht mehr bezahlen.', 'Es isch din alte Rucksack... Er het nur na unbruchbari Sache drin. Natürli muesch defür d''Mieti vo dim Zimmer nid zahle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_KilledMercenaries', 'dl_npc_helena', 'Well... I didn''t expect you to kill them but... It''s also a way of getting rid of them. You''ll get your chicken, of course...', 'Nun... Ich hätte nicht gerade erwartet, dass du sie tötest, aber es ist auch ein Weg, sie loszuwerden. Du kriegst dein Hühnchen natürlich...', 'Naja... Ich hätt nid grad erwarted dass du si umbringsch, aber das isch au e Möglichkeit, si loszwerde. Dis Güggeli bechunsch natürlich...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_SoldThings', 'dl_npc_helena', 'To whom did you sell my stuff?', 'An wen hast du meine Sachen verkauft?', 'Wem hesch mini Sache verchauft?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LeeroyChicken', 'dl_npc_helena', 'I''ve heard of your famous special chicken...', 'Ich habe von deinem berühmten Hühnchen Spezial gehört...', 'Ich ha vo dim berüemte Güggeli Spezial ghört...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Trade', 'dl_npc_helena', 'What can I get here?', 'Was kann ich hier bekommen?', 'Was chani da so chaufe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_SoldThings', 'dl_npc_helena', 'Oh, I don''t remember. But I''ve only sold some robes and hats anyway.', 'Oh, an die erinnere ich mich nicht mehr. Aber ich habe sowieso nur ein paar Roben und Hüte loswerden können.', 'Oh, a die erinnere ich mich nüm. Aber ich ha sowieso nur es paar Robe und Hüet loswerde chöne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_LeeroyChicken', 'dl_npc_helena', 'And now you want some. Of course. But I can''t cook for you at the moment.', 'Und jetzt willst du auch davon. Natürlich. Ich kann aber momentan nicht für dich kochen.', 'Und jetz wesch au chli. Natürlich. Ich chan aber grad nid für dich choche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_LeeroyChicken2', 'dl_npc_helena', 'Those mercenaries just won''t leave and they scare off my guests. If you manage to make them leave my inn, I''ll cook a chicken for you.', 'Diese Söldner wollen einfach nicht gehen, sie vertreiben mir meine ganze Kundschaft. Wenn du sie dazu bringst, meine Taverne zu verlassen, koche ich ein Hühnchen für dich.', 'Die Söldner wend eifach nid go, si vertriibed mir mini ganz Chundschaft. Wenn du ihne zeigsch, wo d''Türe isch, chochi es Güggeli für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_LeeroyMercenaries', 'dl_npc_helena', 'You''re lucky. I''ll cook one for you because you helped me to get rid of those nasty mercenaries who scared off my customers.', 'Du hast Glück. Ich werde dir eins kochen, da du mir mit diesen üblen Söldnern geholfen hast, die meine Kunden vertrieben haben.', 'Du hesch Glück. Ich wird der eis choche well du die üble Söldner losworde bisch wo mini Chunde verschüücht hend.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Helena_LeeroyMercenaries2', 'dl_npc_helena', '(Helena grills a chicken and it smells delicious) Here, for you. Enjoy your meal.', '(Helena grilliert ein Hühnchen und es duftet vorzüglich) Hier, für dich. Guten Appetit.', '(D''Helena grilliert es Güggeli, es dufted vorzüglich) Do, für dich. En guete!');
