INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_CU', 'dl_npc_ingrid', 'See you later.','Bis später.','Bis spöter.', 'Hasta pronto.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IGotMeat', 'dl_npc_ingrid', 'I got some meat for you with the best regards from Edmond.','Ich habe Fleisch dabei für dich, mit den besten Grüssen von Edmond.','Ich han Fleisch debi für dich, mit de beste Grüess vom Edmond.', 'Aquí tienes. Edmond te manda saludos y un poco de carne.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IGotRoastedMeat', 'dl_npc_ingrid', 'Here is your roasted meat.','Hier, dein gebratenes Fleisch.','Da, dis brötleti Fleisch.', 'Aquí tienes la carne asada.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhoAreYou', 'dl_npc_ingrid', 'Who are you?','Wer bist du?','Wer bisch du?', '¿Quién eres?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ingrid_TakesRawMeat', 'dl_npc_ingrid', 'How nice of you! But, I''m pretty busy at the moment.','Wie lieb von dir! Aber, ich bin momentan gerade noch beschäftigt.','So lieb vo dir! Aber ich bi momentan grad na beschäftigt.', '¡Qué buen gesto! Pero estoy algo ocupada en este momento.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ingrid_GoRoastMeat', 'dl_npc_ingrid', 'I could need your help. Take the meat, roast it over the fire and bring it back to me when you''re finished.','Ich könnte deine Hilfe gebrauchen. Nimm das rohe Fleisch, brate es über dem Feuer hier und bring es mir dann wenn du damit fertig bist.','Nimm das rohe Fleisch, brötle es über dem Füür da und brings mir denn wennd demit fertig bisch.', 'Me vendría bien tu ayuda. Toma la carne, ásala en la fogata y tráemela de vuelta cuando esté lista.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ingrid_TakesRoastedMeat', 'dl_npc_ingrid', 'Perfect, thanks a lot. Boy, you look hungry. Take this stew as a reward, it will strengthen you.','Perfekt, danke vielmals. Du siehst ziemlich hungrig aus Junge, nimm diesen Eintopf als Dank, er wird dich stärken.','Perfekt, danke vilmal. Du gsesch zimlich hungrig uus Bueb, nimm de Eintopf als Dank, er wird dich stärche.', 'Perfecto, muchas gracias. Muchacho, te ves hambriento. Ten este estofado como recompensa; te dará fuerzas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ingrid_IAmIngrid', 'dl_npc_ingrid', 'I''m Ingrid, the cook of this humble village, responsible to feed all the hungry mouths here. And what brings you here?','Ich bin Ingrid, die Köchin dieses bescheidenen Dorfes und muss alle diese hungrigen Mäuler hier stopfen. Und was führt dich hier hin?','Ich bi d''Ingrid, d''Chöchin vo dem bescheidene Dörfli und mun alli die hungerige Müüler stopfe. Und was führt dich da ane?', 'Soy Ingrid, la cocinera de esta humilde aldea, encargada de alimentar a todas la bocas hambrientas de aquí. ¿Qué te trae por acá?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_ImHungry', 'dl_npc_ingrid', 'I''m hungry.','Ich bin hungrig.','Ich han Hunger.', 'Tengo hambre.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_ImLost', 'dl_npc_ingrid', 'I''m lost.','Ich habe mich verlaufen.','Ich han mi verloffe.', 'Estoy perdido.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_ImJustHavingALook', 'dl_npc_ingrid', 'I''m just having a look around.','Ich schau mich hier mal um.','Ich lueg mich mal chli ume da.','Solo estoy viendo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ingrid_GivesMeat', 'dl_npc_ingrid', 'Oh, you poor, poor boy. Here, take this piece of meat, this should help for now.','Oh du armer, armer Junge. Hier, nimm dieses Stück Fleisch, das sollte für''s erste helfen.','Oh du arme, arme Bueb. Da, nimm das Stück Fleisch, das sött fürs erste mal helfe.', 'Oh, pobre, pobre muchacho. Toma este pedazo de carne, te ayudará por ahora.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ingrid_Location', 'dl_npc_ingrid', 'You''re lost? You''re in the middle of the meadows, in the kingdom of Admantris. Why don''t you just take a look on your map you got there?','Du hast dich verlaufen? Du bist inmitten der Weiden des Mittellands, im Königreich Admantris. Wieso schaust du nicht auf deiner Karte nach, die du mit dir herum trägst?','Du hesch dich verloffe? Du bisch inmitte vode Weide vom Mittelland, im Königriich Admantris. Weso luegsch nid eifach uf dinere Charte, wod da mit dir umetreisch?', '¿Estás perdido? Estás en medio de las praderas, en el reino de Admantris. ¿Por qué no revisas el mapa que tienes ahí?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ingrid_DontTrouble', 'dl_npc_ingrid', 'Don''t make trouble.','Mach keinen Ärger.','Mach eifach ke Problem.', 'No causes problemas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhereIsElder', 'dl_npc_ingrid', 'Do you know where the village elder lives?','Weisst du, wo der Dorfälteste lebt?','Weisch du, wo de Dorfältist lebt?', '¿Sabes dónde vive el anciano de la aldea?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhoCanHelp', 'dl_npc_ingrid', 'I lost my memory. Do you know someone who could help me?','Ich habe mein Gedächtnis verloren. Wer könnte mir helfen?','Ich ha mis Gedächtnis verlore. Kensch du öpper, wo mir helfe chönt?', 'Perdí la memoria. ¿Sabes quién podría ayudarme?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ingrid_ElderLocation', 'dl_npc_ingrid', 'Ah, you''re looking for our village elder. A very wise man who always has an open ear for our problems. He lives in the wooden house in the middle of the village.','Ah, du suchst unseren Dorfältesten. Ein sehr weiser Mann, immer mit einem offenen Ohr für unsere Probleme. Er lebt im hölzernen Haus in der Mitte des Dorfes.','Ah, du suechsch üsen Dorfältiste. Er lebt im Holzhuus wo ide Mitti vom Dorf stoht.', 'Entiendo. Estás buscando al anciano de la aldea, un gran sabio que siempre está atento a nuestros problemas. Vive en una cabaña de madera en medio de la aldea.');
