INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Inina_StrangePassage', 'dl_npc_inina2', 'Startling, isn''t it? Those caverns led directly into the sewers of Gandria, without us noticing it.', 'Erschreckend, oder? Diese Höhlen haben direkt in die Kanalisation von Gandria geführt, ohne dass wir davon wussten.', 'Krass, gäu? Die Höhlinä hei diräkt id Kanalisation vo Gandaria gfüehrt, ohni dasmr öppis drvo gwüsst hei.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Inina_StrangePassage2', 'dl_npc_inina2', 'I''ve tried to follow the passage over there, but it has been blocked somehow.', 'Ich habe bereits probiert, diesem Gang dort drüben zu folgen, aber er wurde irgendwie blockiert.', 'I ha scho probiert, däm Gang dört z''fougä, abr äs isch irgendwie blockiert wordä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Inina_StrangePassage3', 'dl_npc_inina2', 'I don''t know a spell that could push through this blockade. Maybe you could succeed?', 'Ich kenne keinen Zauber, der diese Blockade wegdrücken könnte. Vielleicht gelingt es dir ja?', 'I kennä ke Zouber wo die Blockadä wägdrückä chönnti. Villech glingts dir ja? ');
