INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Hey', 'dl_npc_inina','Hey.','Hallo.','Hoi.','¡Hey!');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_Hey', 'dl_npc_inina','Hello, young mage. I''m Inina, High Priestess of the clerics. How can I help you?','Hallo, junger Magier. Ich bin Inina, die Hohepriesterin der Kleriker. Wie kann ich dir helfen?','Tschou junge Magier. I bi d''Inina, Hohiprieschterin vode Kleriker. Wie chani dr häufä?','Hola, joven mago. Soy Inina, la suma sacerdotisa de los clérigos. ¿Cómo he de ayudarte?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatDoing', 'dl_npc_inina','What are you doing here?','Was macht ihr hier?','Was mached ihr do?','¿Qué está haciendo aquí?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Mage', 'dl_npc_inina','How do you know I''m a mage?','Woher weisst du, dass ich Magier bin?','Woher weisch du, dass ich en Magier bi?','¿Cómo sabe que soy un mago?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Aura', 'dl_npc_inina','You can see my aura?','Du kannst meine Aura sehen?','Du chasch mini Aura gseh?', '¿Puede ver mi aura?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Farmers', 'dl_npc_inina','Why do you oppress the farmers? They need your help.','Warum unterdrückt ihr die Bauern?','Wieso unterdrücked ihr d''Buure?', '¿Por qué oprimen a los granjeros? Ellos necesitan ayuda.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_MissingPeople', 'dl_npc_inina','What do you know about the missing people of Gandria?','Was weisst du über die vermissten Bürger von Gandria?','Was weisch du über die vermisste Bürger vo Gandria?', '¿Qué sabe usted de las personas desaparecidas de Gandria?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_MissingPaladin', 'dl_npc_inina','Maybe I could look for the paladin?','Ich könnte vielleicht nach dem Paladin suchen.','Ich chönt vielleicht nachem Paladin sueche.', '¿Tal vez yo pueda buscar al paladín?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_AboutPaladin', 'dl_npc_inina','About the missing paladin...','Wegen des vermissten Paladins...','Wäge em vermisste Paladin...', 'Acerca del paladín perdido...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_CanYouTeachMe', 'dl_npc_inina','Can you teach me something?','Kannst du mir etwas beibringen?','Chasch du mir öppis bibringe?', '¿Me puede enseñar algo?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_WhatDoing', 'dl_npc_inina','Do you see this shrine? Its power is weakened. We''re here to find out why.','Siehst du diesen Schrein? Seine Kraft ist geschwächt. Wir sind hier, um herauszufinden, warum.','Gsesch dä Schryn da? Sini Chraft isch gschwächt. Mir si da zum usefinge wieso.', '¿Ves este templo? Su poder se debilita. Hemos venido a descubrir por qué.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Shrine', 'dl_npc_inina','What kind of shrine is it?','Was für eine Art Schrein ist das?','Was für e Art Schrein isch das?', '¿Qué tipo de templo es este?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_PowerWeakened', 'dl_npc_inina','What could possibly weaken its power?','Was könnte seine Kraft denn einschränken?','Was chönt sini Chraft denn iischränke?', '¿Qué podría debilitar su poder?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatDo', 'dl_npc_inina','What has been done so far to find the cause?','Was habt ihr schon versucht um die Ursache zu finden?','Was hender scho probiert zum d''Ursach finde?', '¿Qué han hecho hasta ahora para encontrar la causa?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_Shrine', 'dl_npc_inina','It''s a shrine of the Eternal Light. Mages and commoners can come here and find strength and peace in prayer.','Es ist ein Schrein des Ewigen Lichts. Magier und Gewöhnliche können herkommen um Stärke und Frieden im Gebet zu suchen.','Äs isch ä Schryn vom ewigä Liecht. Magier u Normaustärblechi chöi da härä cho zum Sterchi u Fridä im Gebät z''fingä.', 'Es un templo de la Eterna Luz al que magos y plebeyos acuden para recibir fortaleza y paz mediante la oración.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_PowerWeakened', 'dl_npc_inina','It seems like there is some energy around that is disturbing the divine magic of the shrine. We need to find the cause of this interfering magic.','Es sieht so aus, als würde die heilige Magie des Schreins durch eine andere Magie gestört werden. Wir müssen dem auf den Grund gehen.','Es gseht so us, aus würd d''heilig Magie vom Schryn dür ä angeri Magie gstört wärdä. Mir müesse däm ufe Grund gah.','Al parecer una energía en las proximidades perturba la magia divina del templo. Debemos encontrar el origen de esta magia perturbadora.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_PowerWeakenedNot', 'dl_npc_inina','But... strange. I can''t feel that aura anymore. Maybe something happened...','Aber... seltsam. Ich kann diese Aura nicht mehr spüren. Vielleicht ist etwas passiert...','Aber... komisch. I cha d''Aura nümme gspüre. Villäch isch öppis passiert...', '?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_WhatDo', 'dl_npc_inina','We''ve sent one of our paladins into the cave behind the shrine, but he has not yet returned.','Wir haben einen unserer Paladine in die Höhle hinter dem Schrein geschickt, aber bis jetzt ist er noch nicht zurückgekehrt.','Mir hei eini vü üsne Paladinä i d''Höhli hingerem Schryn gschickt. Aber bis iz ischsi no nid zrügg cho.','Sí, tal vez puedas ayudarnos. Marcus es el nombre del paladín que envié a la cueva.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_MissingPaladin1', 'dl_npc_inina','Yes, maybe you could help us. The name of the paladin I sent into the cave is Marcus.','Ja, vielleicht könntest du uns helfen. Der Paladin, den wir in die Höhle geschickt haben, heisst Marcus.','Ja, villäch chaschnis häufä. Dr Paladin womer id Höhli gschickt hei heisst Marcus.','Sí, tal vez puedas ayudarnos. Marcus es el nombre del paladín que envié a la cueva.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_MissingPaladin2', 'dl_npc_inina','He has taken my pendant with him. A special pendant that holds divine energy and shines in the dark, so you should be able to find him.','Er hat meinen Anhänger mitgenommen. Ein spezielles Schmuckstück, welches heilige Energie besitzt und in der Dunkelheit leuchtet. Er sollte also leicht zu finden sein.','Är het mi Ahänger mitgno. Äs spezieus Schmuckstück wo heilig Energie bsitzt und im Dunklä lüchtet. Är sött auso eifach z''finge si.','Él lleva consigo mi pendiente especial que almacena algo de energía divina y resplandece en la oscuridad. Deberías poder encontrarlo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_MissingPaladin3', 'dl_npc_inina','You''d better bring something to light your way as well. I cannot accompany you, as I''m still trying to purify the magic of the shrine.','Du solltest besser auch etwas mitnehmen, das dir den Weg beleuchtet. Ich kann dich nicht begleiten, da ich immer noch probiere, die Magie des Schreins zu läutern.','Du söttisch besser öppis mitnäh, wodr dr Wäg belüchtet. I cha di nid begleite, wüu ig immer no probiere, d''Magie vom Schryn z''lüütere.','Es mejor que lleves contigo algo que ilumine tu sendero. No puedo acompañarte; todavía estoy tratando de purificar la magia del templo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_Farmers', 'dl_npc_inina','There were some incidents where people left the city and didn''t return. We have to tackle this problem first before we take care of some missing sheep.','Es gab einige Vorfälle von Bürgern, welche die Stadt verlassen haben und nicht zurückgekehrt sind. Dieses Problem hat höchste Priorität bevor wir uns um ein paar vermisste Schafe kümmern können.','Äs het äs paar Vorfäu gäh vo Bürger, wo d''Stadt vrlah hei u nie me si zrügg cho. Ds Problem isch gad viu wichtiger aus es paar vrmissti Schäfli.','Hubo incidentes de personas que dejaron la ciudad mas nunca regresaron. Debemos aclarar este misterio antes de buscar unas ovejas perdidas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_MissingPeople', 'dl_npc_inina','We suspect that these incidents are connected to the strange magical energy we are observing here. But this has yet to be confirmed.','Wir vermuten, dass die Vorfälle mit der seltsamen magischen Energie zu tun haben, die wir hier beobachten. Wir können aber noch nichts beweisen.','Mir vrmuete das die Vorfäu äuä mit dere komische magische Energie, womer hie beobachtä, z''düe het. Mir suechä aber immer no Bewiisä für ds.','Sospechamos que estos incidentes estén relacionados con la extraña energía mágica que percibimos acá, mas no se ha confirmado aún.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_Mage', 'dl_npc_inina','The Eternal Light is well-disposed towards you. You have a very special aura. (Frowns) Although... something seems to be wrong with you. I feel a slight disturbance in your magic.','Das Ewige Licht ist dir wohlgesinnt. Du hast eine sehr spezielle Aura. (Runzelt die Stirn) Allerdings... irgendetwas stimmt nicht mit dir. Eine seltsame Kraft scheint deine Magie zu stören.','Z ewigä Liecht isch güetig zudr. Du hesch ä sehr spezieui Aura. (Rundzelet d''Stirn) Auderdings... öppis stimmt mit dir nid. E komischi Chraft schint dini Magie z''störe.', 'La Eterna Luz está a tu favor. Tienes un aura muy especial. (Frunce el ceño) No obstante... algo está mal contigo. Siento una leve perturbación en tu magia.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_Aura', 'dl_npc_inina','No, I can only feel it. There are very few mages out there that are able to see auras, the clairvoyant. Sadly, I''m not one of them.','Nein. ich kann sie nur spüren. Es gibt nur sehr wenige Magier, welche Auren sehen können, die Sehenden. Leider gehöre ich nicht dazu.','Äuää, i cha sä nur gspüre. Äs git nur sehr wenigi Magier wo Aure chöi gseh, die Sehendä. Leider ghöri da nid drzue.','No, solo la puedo percibir. Son muy pocos los magos que pueden ver el aura, los clarividentes. A mi pesar, no soy una de ellos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhereLight', 'dl_npc_inina','Where can I get something to light my way?','Wo kann ich etwas finden, das mir den Weg beleuchtet?','Wo chani öppis finde, wo mir de Weg belüüchtet?','¿Dónde puedo conseguir algo para alumbrar mi camino?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhereSearch', 'dl_npc_inina','Where should I start the search?','Wo soll ich mit der Suche beginnen?','Wo sölli mit de Suechi ahfange?','¿Dónde empiezo a buscar?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatsInForMe', 'dl_npc_inina','What''s in for me if I find him?','Was springt für mich dabei heraus, sollte ich ihn finden?','Was wür ich devo ha, wenn ich ihn find?','¿Qué gano yo si lo encuentro?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_FoundPaladin', 'dl_npc_inina','I found the Paladin. He''s dead. (Give the pendant)','Ich habe den Paladin gefunden. Er ist tot. (Anhänger geben)','Ich ha de Paladin gfunde. Er isch tot. (Ahänger gä)','Encontré al paladín. Está muerto. (Regresa el pendiente)');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Back', 'dl_npc_inina','[BACK]','[ZURÜCK]','[ZRUGG]','[REGRESAR]');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_WhereLight', 'dl_npc_inina','You''ll need a torch. I don''t have one, but I''m sure that you can buy one at the farm.','Du brauchst eine Fackel. Ich habe keine bei mir, aber ich bin sicher, dass du auf dem Bauernhof eine kaufen kannst.','Du bruchsch ä Facklä. I ha leider keni drbi, aber bimr sicher, dass de bim Buur eini chasch choufe.','Necesitarás una antorcha. Carezco de una, pero tengo la certeza de que podrás comprar una en la granja.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_WhereSearch', 'dl_npc_inina','He went westward to the cave behind the shrine.','Er ging nach Westen zu einer Höhle.','Är isch i Westä zurä Höhli gangä.','Se fue en dirección oeste, hacia una cueva.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_WhatsInForMe', 'dl_npc_inina','Well, as you''re a mage, I can teach you the path of the Eternal Light. You''ll never need to use a torch again. (Smiles)','Naja, da du ein Magier bist, kann ich dir den Weg den Ewigen Lichts beibringen. Du wirst nie wieder eine Fackel brauchen. (Lächelt)','Ja auso, da du ä Magier bisch, chani dir dr Wäg vom Ewigä Liecht bibringä. Du bruchsch ab izä ke Facklä meh.','Bueno, como eres mago, puedo instruirte en la senda de la Eterna Luz. No necesitarás de una antorcha nunca más. (Sonríe)');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_FoundPaladin', 'dl_npc_inina','Oh no! That''s terrible! He was a good man. I''m going to pray for him. Oh, and thank you for bringing my pendant back.','Oh nein. Das ist furchtbar. Ich werde für ihn beten. Er war ein guter Mann. Oh, und danke, dass du meinen Anhänger zurückgebracht hast.','Ou neei. Ds isch ja schlimm. I sprichä gad es Gebät für ihn. Är isch ä guetä Maa gsi. Oh, und merci, dasde mi Ahänger hesch zrüggbracht.','¡Oh no! Eso es terrible. Él era un buen hombre. Oraré por él. Gracias por devolverme mi pendiente.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_FoundPaladin2', 'dl_npc_inina','We really need to find out what''s behind all this.','Wir müssen unbedingt herausfinden was hinter all dem steckt.','Mir müesse unbedingt usefingä was drhinger steckt.','Realmente debemos encontrar la causa de todo esto.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_LearnLight1', 'dl_npc_inina','Yes, you''ve proven yourself worthy to learn the way of the Eternal Light.','Ja, du hast dich als würdig erwiesen, den Weg des Ewigen Lichts zu lernen.','Ja du hesch di würdig erwisä, dr Wäg vom ewigä Liecht z''leerä.','Sí, has probado ser digno aprendiz de la senda de la Eterna Luz.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_LearnLight2', 'dl_npc_inina','To create a magical light, you''ll need a weapon that is capable of focusing divine magic. Then, concentrate the divine energy around you and let it shine.','Um ein magisches Licht zu erschaffen brauchst du eine Waffe mit der du heilige Magie fokussieren kannst. Dann bündle die heilige Energie um dich herum und lass sie leuchten.','Fürnäs magischs Liecht z''erschaffä bruchsch zersch ä Waffä wo heiligi Magie cha fokusierä. När muesch die heiligi Energie um di bündle und zum lüchtä bringe.','Para crear luz mágica, necesitas un arma capaz de concentrar magia divina. Luego concentra la energía divina a tu alrededor y déjala resplandecer.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_LearnLight3', 'dl_npc_inina','Take this scroll. It will teach you everything you need to know.','Nimm diese Schriftrolle. Sie wird dir alles beibringen was du wissen musst.','Nimm die Schriftrouä. Si erklärtdr aues wodä muesch wüssä.','Toma este pergamino. Te enseñará todo lo que necesitas saber.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_NoTeach', 'dl_npc_inina','We can''t tell yet whether your heart is pure. You first have to prove that you are willing to help us.','Wir können noch nicht sagen, ob dein Herz rein ist. Du musst uns erst beweisen, dass du uns hilfst.','Mir chöi no nid sägä ob dis Härz rein isch. Du muesch zersch biwiisä, dass du üs hiufsch.','Todavía no sabemos si tu corazón es puro o no. Primero debes probar que estás dispuesto a ayudarnos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Mark', 'dl_npc_inina','Maybe *this* influences my magic? (Show the mark)','Vielleicht stört ja *das* meine Magie? (Mal zeigen)','Vilicht stört ja *das* mini Magie? (Zeiche zeige)','¿Tal vez es *esto* lo que influencia mi magia? (Muestra la marca)');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_Mark', 'dl_npc_inina','(Flinches) By the Eternal Light! That''s a curse, and it doesn''t look good...','(Zuckt zusammen) Beim Ewigen Licht! Das ist ein Fluch, und der schaut nicht gut aus...','(Zuckt zämä) Bim Ewigä Liecht! Da isch ä Fluech und dä gseht nid guet us...','(Se sobresalta) ¡Por la Eterna Luz! Eso es una maldición y no se ve bien...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhatIsMark', 'dl_npc_inina','Do you know what it is?','Weisst du, was es ist?','Weisch du, was es isch?', '¿Sabe qué es?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_MarkGetRid', 'dl_npc_inina','Can you help me get rid of it?','Kannst du mir helfen, es loszuwerden?','Chasch mer helfe, das los z''werde?','¿Me puede ayudar a romperla?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_MarkWhoWould', 'dl_npc_inina','Why would someone try to track me?','Wieso sollte mich jemand aufspüren wollen?','Weso sött mich öpper versueche ufzspüre?', '¿Por qué alguien querría rastrearme?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_WhatIsMark', 'dl_npc_inina','Unless I am very much mistaken, this is a curse designed to track you.','Wenn ich mich nicht irre, ist es ein Fluch, der dazu dient, dich aufzuspüren.','Weni mi nid irre, ischs ä Fluech, wo da isch für di ufzspüre.','A menos de que me equivoque, esta maldición fue diseñada para rastrearte.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_MarkGetRid', 'dl_npc_inina','May I? (Places her finger on the mark but pulls it back instantly) No, that won''t work. I''m not familiar with this kind of magic.','Darf ich? (Legt einen Finger auf das Mal aber zieht ihn sofort wieder zurück) Nein, das funktioniert so nicht. Diese Art von Magie ist mir fremd.','Dörfi? (Legt ä Finger uf z Zeichä und ziehtnä gad widr zrügg) Nei, so klappt das nid. Die Art vo Magie isch mir nid ghüür.','¿Me permites? (Pone el dedo sobre la marca, pero lo quita al instante) No, no funcionará. No estoy familiarizada con este tipo de magia.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_MarkGetRid2', 'dl_npc_inina','If you really plan to get rid of that, ask the alchemist Syrah when you arrive in Gandria. She knows much more about curses than I do.','Wenn du es wirklich loswerden willst, frage die Alchemistin Syrah um Rat, wenn du nach Gandria kommst. Sie kennt sich mit Flüchen besser aus als ich.','Wenn du ds wüklech wosch loswärdä, denn frag d''Alchemistin Syrah um Rot, wedä nach Gandria chunsch. Si kenntsäch mit so Flüech besser us aus ig.','Si en realidad deseas romperla, pregunta por Syrah cuando llegues a Gandria. Ella sabe mucho más de maldiciones que yo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_MarkWhoWould', 'dl_npc_inina','Well, the paladins use a similar technique to mark and track down prisoners. But I''d know those marks. If you really don''t know why you bear it, you may be in trouble.','Nun ja, die Paladine brauchen eine ähnliche Technik um die Gefangenen zu markieren und aufzuspüren. Wenn du wirklich nicht weisst, warum du es trägst, könntest du in Schwierigkeiten stecken.','Naja, d''Paladin bruchä ä ähnlächi Technik für Gfangeni z''markiere und z''ufspürä. We du würklech nid weisch, wieso du ds hesch, de hesch äuä ächt es Problem.', 'Bueno, los paladines utilizan una técnica parecida para marcar y rastrear a los prisioneros, pero yo reconocería esas marcas. Si no sabes en realidad por qué la llevas, puedes estar en problemas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_IFoundNote', 'dl_npc_inina','Paladin Marcus carried this note with him... (Show note)','Paladin Marcus trug diese Notiz bei sich... (Notiz zeigen)','De Paladin da het die Notiz bi sich gha... (Notiz zeige)','El paladín Marcus tenía esta nota con él… (Muestra la nota)');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_FoundNote', 'dl_npc_inina','Oh, thank you. Let me see... (Reads the note carefully)','Oh, danke. Lass mal sehen... (liest die Notiz sorgfältig)','Oh, merci. Mau luegä... (Liest d''Notiz sorgfäutig)','Oh, gracias. Déjame ver... (Lee la nota con detenimiento)');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_FoundNote2', 'dl_npc_inina','It seems like he tried to write about a beast that transforms humans into wolves? Maybe that thing is also responsible for the strange aura here.','Es scheint, als hätte er versucht, über eine Bestie zu schreiben, die Menschen in Wölfe verwandelt? Vielleicht hat dieses Ding auch diese seltsame Aura hier zu verantworten.','Äs schiint, aus hät är probiert, über äs Biescht z''schribä, wo mönschä zu Wöuf vrwandlet. Villäch isch wäg däm Ding oh die komischi Aura da...', 'Parece que intentó escribir sobre una bestia que transforma a los humanos en… ¿lobos? Tal vez ese ente es el responsable de su extraña aura.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_FoundNote3', 'dl_npc_inina','As you already got out of the cave alive, maybe you got a chance to hunt that beast down and slay it.','Da du schon einmal lebend durch diese Höhlen gekommen bist, kannst du vielleicht auch dieses Biest finden und töten?','Wüu du schomau lebändig dür die Höhli bisch cho, bringschs äuä oh härä, das Biescht z''fingä und z''tötä?','Como lograste salir de la cueva con vida, tal vez tengas la oportunidad de cazar y asesinar a la bestia.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_FoundNote4', 'dl_npc_inina','I would offer you my undying gratitude and a great reward, of course.','Meine ewige Dankbarkeit wäre dir gewiss und ich würde dich natürlich auch reich entlohnen.','Du hätsch mini ewigi Dankbarkeit... und I würddr natürlech oh gnue Gold gäh drfür.','Te ofrecería mi eterna gratitud y una gran recompensa, por supuesto.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_BeastSlain', 'dl_npc_inina','I''ve slain the beast in the caverns.','Ich habe das Biest in den Höhlen getötet.','Ich hans Viich ide Höhlene killt.','Asesiné a la bestia en la cueva.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_BeastSlain', 'dl_npc_inina','Yes, I''ve already felt it. The evil magic that was obscuring the shrine is gone.','Ja, ich habe es bereits gespürt. Die bösartige Magie, die den Schrein vernebelte, ist weg.','Ja, i has scho gspürt. Die bösi Magie, wo dr Schryn vrnäblet het, isch wäg.','Sí, ya lo sentí. La magia maligna que ensombrecía el templo se ha disipado.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_BeastSlain2', 'dl_npc_inina','You did us a great favour. Here, take this pouch filled with gold as a reward.','Du hast uns einen grossen Gefallen getan. Hier, nimm diesen Beutel mit Gold als Belohnung.','Du hesch üs ä grosse Gfaue gmacht. Da, nimm dr Bütu mit Gold aus Belohnig.', 'Nos has hecho un gran favor. Toma esta bolsa llena de oro como recompensa.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_BeastSlain3', 'dl_npc_inina','I''m greatly impressed by your skills, mage. You may be a worthy aspirant for the clerics. (Takes a piece of paper and writes something down)','Ich bin höchst beeindruckt von deinen Fähigkeiten, Magier. Du könntest ein würdiger Initiant für die Kleriker sein. (Nimmt ein Stück Papier und schreibt etwas darauf)','I bi uf z höchschtä beiidruckt vo dinä Fähigkeitä, Magier. Du chönntsch ä würdige Initiant fürd Kleriker si. (Nimmt äs Stück Papier u schriibt öppis druf)','Tus habilidades me han impresionado en gran medida, mago. Puedes ser un valioso aprendiz para los clérigos. (Saca un pedazo de papel y escribe algo)');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_BeastSlain4', 'dl_npc_inina','Here, take this letter of recommendation with you and bring it to Commander Lloyd once you''re in the city, he is the leader of the paladins.','Hier, nimm dieses Empfehlungsschreiben und bringe es zu Kommandant Lloyd, wenn du in der Stadt bist. Er ist der Anführer der Paladine.','Da, nimm ds Empfähligsschribä u brings zum Kommandant Lloyd, wedä idr Stadt bisch. Är isch dr Ahführär vodä Paladin.', 'Toma, lleva esta carta de recomendación al comandante Lloyd una vez que estés en la ciudad. Él es el líder de los paladines.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Inina_BeastSlain5', 'dl_npc_inina','May the Eternal Light guide thee.','Möge dich das Ewige Licht führen.','Ds Ewigä Liecht wird di füehre.', 'Que la Eterna Luz os guíe.');
