INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_Rumors', 'dl_npc_innkeeper', 'Heard any rumours?','Hast du irgendwelche Gerüchte gehört?','Sind irgendwelchli Grücht im Umlauf?');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_RumorsMore', 'dl_npc_innkeeper', 'Heard more rumours?','Gibt es weitere Gerüchte?','Gits na me Grücht?');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_GiveBeer', 'dl_npc_innkeeper', 'I''m thirsty, give me a beer.','Ich habe Durst, gib mir ein Bier.', 'Ich han Durst, gib mer es Bier.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_SomethingElse', 'dl_npc_innkeeper', 'Do you also sell other things than beer?','Verkaufst du auch noch etwas anderes als Bier?','Verchaufsch du au na öppis anderes wie Bier?');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_INeedSchnapps', 'dl_npc_innkeeper', 'I''m looking for a special schnapps...','Ich suche einen speziellen Schnaps...','Ich suech en spezielle Schnaps...');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_SellMeAnyway', 'dl_npc_innkeeper', 'Please, I really need your schnapps!','Bitte, ich brauche diesen Schnaps ganz dringend!','Bitte, ich bruch de Schnaps unbedingt!');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_SellMeSchnapps', 'dl_npc_innkeeper', 'Give me some "Feudal Fire".','Gib mir so ein "Feudales Feuer".','Gib mer so es "Feudals Füür".');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_HereYouGo', 'dl_npc_innkeeper', 'Sure, here you go.','Sicher. Hier, nimm.','Sicher. Da, nimm.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_NotEnoughGold', 'dl_npc_innkeeper', 'If you want to have a beer, you need enough gold to pay for it.','Wenn du ein Bier haben willst, musst du es auch bezahlen können.','Wenn du es Bier ha wetsch, muesches au chöne zahle.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_TakeItAnyway', 'dl_npc_innkeeper', 'Well, you seem to have enough money to convince me. Here you go.','Nunja, du scheinst genügend Geld zu haben, um mich zu überzeugen. Hier, nimm.','Ja, ich glaub du hesch gnueg Geld zum mich trotzdem z''überzüüge. Da, nimm.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_NoSchnapps', 'dl_npc_innkeeper', 'I can''t sell my schnapps any Tom, Dick or Harry, you know. And especially not to some beggarly guy like you.','Ich kann meinen Schnaps nicht jedem dahergelaufenen Kerl verkaufen, weisst du. Und bestimmt nicht so einem mittellosen Strolch wie dir.','Ich cha min Schnaps nid jedem dahergloffene Strolch verchaufe. Und sicher nid somne verlumpte Bettler we dir.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_NotEnoughForSchnapps', 'dl_npc_innkeeper', 'You don''t have enough gold to buy a bottle, I''m sorry.','Du hast nicht genügend Gold dabei für eine Flasche, tut mir leid.','Du hesch nid gnueg Gold debii für e Fläsche, tuet mer leid.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_NoRumor', 'dl_npc_innkeeper', 'Hm no, nothing of interest.','Hmm nein, ich habe nichts Interessantes mehr zu berichten.','Hm nai, ich han nüt Interessants me z''verzelle.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_SecondRumorP1', 'dl_npc_innkeeper', 'Two farmers told me that recently they are visited regularly by a monster who steals their sheep.','Zwei Bauern haben mir kürzlich erzählt, dass bei ihnen regelmässig ein Monster vorbeischaut und ihnen ihre Schafe stielt.','Zwei Buure hend mer letztins verzellt, dass si regelmässig Bsuech vomne Monster hend wo ihne iri Schöfli chlaut.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_SecondRumorP2', 'dl_npc_innkeeper', 'Sadly, the milita of Gandria doesn''t seem to care too much about this incidents.','Leider kümmert sich die Miliz von Gandria nicht sonderlich um diese Vorfälle.','Leider kümmeret sich d''Miliz vo Gandria nid sonderlich um die Vorfäll.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_FirstRumorP1', 'dl_npc_innkeeper', 'I heard that the king of Admantris has resigned - in favour of his son, Logan the Third.','Ich habe gehört dass der König von Admantris abgedankt hat - zu Gunsten seines Sohns, Logan dem Dritten.','Ich han ghört, dass de König vo Admantris abdankt het - zu Gunste vo sim Sohn, em Logan em Dritte.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_FirstRumorP2', 'dl_npc_innkeeper', 'I don''t really understand though why though, the old king seemed to be in good shape. And his son, well, he didn''t bother often to show his face to the people.','Ich verstehe es aber nicht wirklich. Der alte König war eigentlich noch in einer ganz guten Verfassung. Und sein Sohn, naja, er hat sich dem Volk nicht sonderlich oft gezeigt.','Ich verstands aber nid würklich. De alt König isch eigentlich na zimli fit uf de Bei gsi. Und sin Sohn, naja, er het sis Gsicht em Volk nid allzu oft zeigt.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_WhatsTheName', 'dl_npc_innkeeper', 'Mhm, my booze is famous, isn''t it. So famous, that you surely know its name?','Ja, mein Fusel ist berühmt, nicht wahr? So berühmt, dass du sicher weisst, wie er heisst?','Ja, min Fusel isch berüemt, gell. So berüemt, dass du sicher weisch, wener heisst?');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_Name2', 'dl_npc_innkeeper', 'Fiery Frenzy.','Feurige Feier.','Füürigs Fäscht.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_Name3', 'dl_npc_innkeeper', 'Floral Force.','Finstere Flamme.','Finsteri Flamme,');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_Name4', 'dl_npc_innkeeper', 'Flying Fox.','Fliegender Fuchs.','Flügende Fuchs.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_Name5', 'dl_npc_innkeeper', 'Fierce Farmer.','Flotter Fischer.','Flotte Fischer.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_Name6', 'dl_npc_innkeeper', 'Fake Flame.','Falscher Flügel.','Falsche Flügel.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_Name7', 'dl_npc_innkeeper', 'Forgiving Frost.','Frostige Freude.','Frostigi Freud.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_NameFeudalFire', 'dl_npc_innkeeper', 'Feudal Fire.','Feudales Feuer.','Feudals Füür.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_NameIDontKnow', 'dl_npc_innkeeper', 'I don''t know.','Ich weiss nicht.','Ich weiss nid.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_ComeBackLater', 'dl_npc_innkeeper', 'If you don''t know the name, you don''t need it either. Ask again when you know more.','Wenn du den Namen nicht weisst, brauchst du es auch nicht. Komm wieder, wenn du mehr weisst.','Wer de Name nid weiss, bruchts au nid. Chum wider, wennd me weisch.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_WrongName', 'dl_npc_innkeeper', 'Are you kidding me? That sounds ridiculous. I sell my schnapps only to people who know its name.','Willst du mich verarschen? Das tönt lächerlich. Ich kann meinen Schnaps nur Leuten verkaufen, die wenigstens wissen, wie er heisst.','Wetsch mi verarsche? Das tönt lächerlich. Ich cha min Schnaps nur Lüüt verkaufe, wo wüssed wener heisst.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Innkeeper_RightName', 'dl_npc_innkeeper', 'Very well then, you can have some. But be cautious, it doesn''t bear its name in vain, it burns when it runs down your throat. Hee hee...','Sehr gut, du bekommst welchen. Aber sei gewarnt, er trägt seinen Namen nicht vergebens, es brennt, wenn er deine Kehle hinunterfliesst. Hi hi hi...','Sehr guet. Aber sig gwarnt, er treit sin Name nid vergäbens. Es brennt, wener die Kehle abeflüsst. Hi hi hi...');
