INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Rumors', 'dl_npc_innkeeper', 'Heard any rumours?','Hast du irgendwelche Gerüchte gehört?','Sind irgendwelchi Grücht im Umlauf?', '¿Has escuchado algún rumor?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_RumorsMore', 'dl_npc_innkeeper', 'Heard more rumours?','Gibt es weitere Gerüchte?','Gits na me Grücht?', '¿Más rumores?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_GiveBeer', 'dl_npc_innkeeper', 'I''m thirsty, give me a beer.','Ich habe Durst, gib mir ein Bier.', 'Ich han Durst, gib mer es Bier.', 'Estoy sediento; dame una cerveza.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_SomethingElse', 'dl_npc_innkeeper', 'Do you also sell other things than beer?','Verkaufst du auch noch etwas anderes als Bier?','Verchaufsch du au na öppis anderes wie Bier?', '¿Qué más vendes, además de cerveza?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_INeedSchnapps', 'dl_npc_innkeeper', 'I''m looking for a special schnapps...','Ich suche einen speziellen Schnaps...','Ich suech en spezielle Schnaps...', 'Busco un arguardiente especial.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_SellMeAnyway', 'dl_npc_innkeeper', 'Please, I really need your schnapps!','Bitte, ich brauche diesen Schnaps ganz dringend!','Bitte, ich bruch de Schnaps unbedingt!', 'Por favor, ¡realmente necesito tu aguardiente!');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_SellMeSchnapps', 'dl_npc_innkeeper', 'Give me some "Feudal Fire".','Gib mir so ein "Feudales Feuer".','Gib mer so es "Feudals Füür".','Dame un poco de Fuego Feudal.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_HereYouGo', 'dl_npc_innkeeper', 'Sure, here you go.','Sicher. Hier, nimm.','Secher. Do, nemm.', 'Claro, aquí tienes.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_NotEnoughGold', 'dl_npc_innkeeper', 'If you want to have a beer, you need enough gold to pay for it.','Wenn du ein Bier haben willst, musst du es auch bezahlen können.','Wenn du es Bier ha wetsch, muesches au chönne zahle.', 'Si quieres una cerveza, necesitas suficiente oro para pagarla.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_TakeItAnyway', 'dl_npc_innkeeper', 'Well, you seem to have enough gold to convince me. Here you go.','Nunja, du scheinst genügend Gold zu haben, um mich zu überzeugen. Hier, nimm.','Ja, ech glaub du hesch gnueg Gold zum mech trotzdem z''öberzüüge. Do, nemm.', 'Al parecer tienes suficiente oro para convencerme. Aquí tienes.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_NoSchnapps', 'dl_npc_innkeeper', 'I can''t sell my schnapps any Tom, Dick or Harry, you know. And especially not to some beggarly guy like you.','Ich kann meinen Schnaps nicht jedem dahergelaufenen Kerl verkaufen, weisst du. Und bestimmt nicht so einem mittellosen Strolch wie dir.','Ech cha mine Schnaps ned jedem dahergloffene Strolch verchaufe. Ond secher ned somne verlompte Bättler wie der.', 'Deberías saber que no le vendo mi aguardiente a ningún fulano, zutano o mengano, y mucho menos a un miserable como tu.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_NotEnoughForSchnapps', 'dl_npc_innkeeper', 'You don''t have enough gold to buy a bottle, I''m sorry.','Du hast nicht genügend Gold dabei für eine Flasche, tut mir leid.','Du hesch nid gnueg Gold für es edels Fläschli debi, tuet mer leid.', 'Lo siento, pero no tienes oro suficiente para comprar una botella.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_NoRumor', 'dl_npc_innkeeper', 'Hm no, nothing of interest.','Hmm nein, ich habe nichts Interessantes mehr zu berichten.','Hm nö, ech han nüt Interessants me z''verzelle.', 'Mmm, no, nada interesante.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_SecondRumorP1', 'dl_npc_innkeeper', 'Two farmers told me that recently they are visited regularly by a monster who steals their sheep.','Zwei Bauern haben mir kürzlich erzählt, dass bei ihnen regelmässig ein Monster vorbeischaut und ihnen ihre Schafe stiehlt.','Zwoi Landwerte hend mer letschti verzellt, dass si regelmässig vomne Monster heimgsuecht werded und ehne ehri Schöfli chlaut.', 'Dos granjeros me comentaron que recientemente un monstruo los visita con frecuencia y les roba las ovejas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_SecondRumorP2', 'dl_npc_innkeeper', 'Sadly, the milita of Gandria doesn''t seem to care too much about these incidents.','Leider kümmert sich die Miliz von Gandria nicht sonderlich um diese Vorfälle.','Leider kömmert sich d''Miliz vo Gandria ned sonderlech um die seltsame Vorfäll.', 'Desafortunadamente, a la milicia de Gandria no le parece interesar mucho estos sucesos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_FirstRumorP1', 'dl_npc_innkeeper', 'I heard that the king of Admantris has resigned - in favour of his son, Logan the Third.','Ich habe gehört dass der König von Admantris abgedankt hat - zu Gunsten seines Sohns, Logan dem Dritten.','Ech han ghört, dass de König vo Admantris abdankt het - zo Gunste vo sim Sohn, em Logan de Dretti.', 'Escuché que el rey de Admantris abdicó el trono en favor de su hijo, Logan III.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_FirstRumorP2', 'dl_npc_innkeeper', 'I don''t really understand though why though, the old king seemed to be in good shape. And his son, well, he didn''t bother often to show his face to the people.','Ich verstehe es aber nicht wirklich. Der alte König war eigentlich noch in einer ganz guten Verfassung. Und sein Sohn, naja, er hat sich dem Volk nicht sonderlich oft gezeigt.','Ech verstohs aber ned wörklech. De alt König esch eigentlech no zimli fit uf de Bei gsi. Und sine Sohn, naja, er het sis Gsicht em Volk ned allzu oft zeigt.', 'Realmente no entiendo el porqué. El antiguo rey parecía estar bien. Su hijo, bueno, no se interesó por relacionarse con la gente.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_WhatsTheName', 'dl_npc_innkeeper', 'Mhm, my booze is famous, isn''t it. So famous, that you surely know its name?','Ja, mein Fusel ist berühmt, nicht wahr? So berühmt, dass du sicher weisst, wie er heisst?','Jo, mine Fusel esch berüemt, gau. So berüemt, dass du secher weisch, wiener heisst?', 'Mhmm, mi licor es famoso, ¿verdad? Es tan famoso que de seguro sabes su nombre, ¿cierto?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Name2', 'dl_npc_innkeeper', 'Fiery Frenzy.','Feurige Feier.','Füürigs Fäscht.', 'Fiero Frenesí');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Name3', 'dl_npc_innkeeper', 'Floral Force.','Finstere Flamme.','Finsteri Flamme,', 'Fuerza Floral');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Name4', 'dl_npc_innkeeper', 'Flying Fox.','Fliegender Fuchs.','Flügende Fuchs.', 'Flamenco Feroz');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Name5', 'dl_npc_innkeeper', 'Fierce Farmer.','Flotter Fischer.','Flotte Fischer.', 'Fraile Fogoso');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Name6', 'dl_npc_innkeeper', 'Fake Flame.','Falscher Flügel.','Falsche Flügel.', 'Flama Flamante');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Name7', 'dl_npc_innkeeper', 'Forgiving Frost.','Frostige Freude.','Frostigi Freud.', 'Franco Frío');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_NameFeudalFire', 'dl_npc_innkeeper', 'Feudal Fire.','Feudales Feuer.','Feudals Füür.', 'Fuego Feudal');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_NameIDontKnow', 'dl_npc_innkeeper', 'I don''t know.','Ich weiss nicht.','Ich weiss nid.', 'No lo sé.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_ComeBackLater', 'dl_npc_innkeeper', 'If you don''t know the name, you don''t need it either. Ask again when you know more.','Wenn du den Namen nicht weisst, brauchst du es auch nicht. Komm wieder, wenn du mehr weisst.','Wer de Name ned weiss, bruchts au ned. Chom weder, wennd ne weisch.', 'Si no te sabes el nombre, no lo necesitas entonces. Cuando sepas más, me vuelves a preguntar.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_WrongName', 'dl_npc_innkeeper', 'Are you kidding me? That sounds ridiculous. I sell my schnapps only to people who know its name.','Willst du mich verarschen? Das klingt lächerlich. Ich kann meinen Schnaps nur Leuten verkaufen, die wenigstens wissen, wie er heisst.','Wetsch mech verarsche? Das tönt jo lächerlech. Ech cha mine edel Schnaps nur Lüüt verkaufe, wo wössed wener heisst.', '¿Estás hablando en serio? ¡Qué ridículo! Solamente les vendo mi aguardiente a quienes sepan su nombre.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Innkeeper_RightName', 'dl_npc_innkeeper', 'Very well then, you can have some. But be cautious, it doesn''t bear its name in vain, it burns when it runs down your throat. Hee hee...','Sehr gut, du bekommst welchen. Aber sei gewarnt, er trägt seinen Namen nicht vergebens, es brennt, wenn er deine Kehle hinunterfliesst. Hi hi hi...','Sehr guet. Aber bis gwarnt, er treit sine Name ned vergäbe! Es brönnt d Seel, wener d''Kehle berüehrt. He he he...','Muy bien, puedes llevar un poco. Eso sí, ten cuidado. No se llama así en vano. Quema cuando baja por la garganta. Je je...');
