INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Farmer', 'dl_npc_ivo','Is this your farm?','Ist das dein Hof?','Isch da din Buurehof?', '¿Es esta tu granja?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Merchant', 'dl_npc_ivo','Could you sell me some goods?','Kannst du mir etwas verkaufen?','Chasch du mir öppis verchaufe?', '¿Me podrías vender algunos productos?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Gandria', 'dl_npc_ivo','What can you tell me about Gandria?','Kannst du mir etwas über Gandria erzählen?','Chasch du mer öppis über Gandria verzelle?', '¿Qué me puedes decir de Gandria?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Job', 'dl_npc_ivo','What job can you offer me?','Was für eine Arbeit bietest du an?','Wa füre Arbet büütisch du aa?', '¿Qué trabajo me puedes ofrecer?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Sheep', 'dl_npc_ivo','About your sheep...','Wegen deinen Schafen...','Wege dine Schöfli...', 'En cuanto a las ovejas...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Bye', 'dl_npc_ivo','Bye.','Tschüss.','Tschüss.', 'Adiós');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_Farmer', 'dl_npc_ivo','Yes, I''m the farmer here. Are you looking for a job?','Ja, ich bin der hiesige Bauer. Suchst du Arbeit?','Ja ich bin de Buur vo da. Suechsch du Arbet?', 'Sí, yo soy el granjero aquí. ¿Estás buscando trabajo?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_NoJob', 'dl_npc_ivo','No, not really.','Nein, nicht wirklich.','Nei, nid würkli.', 'No, en realidad no.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_AskSheep', 'dl_npc_ivo','I heard that your sheep are disappearing?','Ich habe gehört, dass deine Schafe verschwinden?','Ich han ghört, das dini Schöfli verschwindet?', 'Escuché que tus ovejas están desapareciendo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_SheepQuest', 'dl_npc_ivo','Yes, can I do something for you?','Ja, kann ich etwas für dich erledigen?','Ja, chani öppis für dich mache?', 'Sí, ¿puedo hacer algo por ti?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_Gandria', 'dl_npc_ivo','Gandria is under the tightly-controlled jurisdiction of the clerics and their paladins.','Gandria ist in der festen Hand der Kleriker und ihren Paladinen.','Gandria isch ide feste Hand vode Kleriker und ihrne Paladin.', 'Gandria está bajo la estricta jurisdicción de los clérigos y sus paladines.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_Paladins', 'dl_npc_ivo','Useless mages with shiny armour and that''s about it. They don''t lift a finger for people like us - still, we work for them.','Nutzlose Magier in glänzender Rüstung und das war''s dann auch schon. Die machen sich keinen Finger krumm für Leute wie uns - trotzdem arbeiten wir für sie.','Nutzlosi Magier in glänzender Rüstig und da isch''s den au scho gsi. Die mached sich ken Finger chrumm für Lüüt we üs - trotzdem schaffed mer für si.', 'Magos inútiles con armaduras brillantes y eso es todo. No levantan ni un dedo por personas como nosotros... aun así, trabajamos para ellos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_FightBack', 'dl_npc_ivo','Why don''t you do something about it?','Wieso unternehmt ihr nichts dagegen?','Weso macheder nüt degege?', '¿Por qué no haces algo al respecto?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_TalkToThem', 'dl_npc_ivo','I could try and talk to them...','Ich könnte versuchen mit ihnen zu sprechen...','Ich chönt probiere, mit ihne z''rede...', 'Puedo intentar hablar con ellos...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_YourProblem', 'dl_npc_ivo','Well, that''s your problem.','Naja, das ist dein Problem.','Naja, das isch diis Problem.', 'Bueno, ese es tu problema.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_FightBack', 'dl_npc_ivo','It wouldn''t be a good idea to rebel against the clerics. As long as they leave us alone, we''re good.','Es wäre keine gute Idee, sich gegen die Kleriker aufzulehnen. So lange sie uns in Frieden lassen, ist alles gut.','Es wer ke gueti Idee, sich gege d''Kleriker ufz''lehne. So lang si üs üse Fride lönd, isch alles guet.', 'No sería buena idea rebelarse en contra de los clérigos. Con que nos dejen en paz, estamos bien.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_TalkToThem', 'dl_npc_ivo','Like we''ve never tried reasoning with them before... No. Talking is no use. Commoners like us are only scum to them.','Als hätten wir nie versucht mit ihnen darüber zu sprechen... Nein, die lassen einfach nicht mit sich reden. Gewöhnliche wie wir sind nicht mehr als Abschaum für sie.','Als hetted mer nie probiert, mit ihne z''rede... Nei, mit dene chasch eifach nid verhandle. Gwöhnlichi we mir sind eh nur Abschuum.', 'Como si no hubiéramos intentado razonar con ellos antes... No. Hablar no tiene sentido. Plebeyos como nosotros solo somos basura para ellos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_MyProblem', 'dl_npc_ivo','I guess it is.','Ja, das ist es leider.','Ja, da isches leider.', 'Supongo que así es.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_Merchant', 'dl_npc_ivo','My son Tristan is responsible for selling our goods. Go and talk to him if you want to buy something.','Mein Sohn Tristan ist für den Verkauf unserer Güter zuständig. Geh und sprich mit ihm, wenn du etwas kaufen möchtest.','Min Sohn Tristan isch für de Verchauf vo üsne Güeter zueständig. Gang und red mit ihm, wend was chaufe willsch.', 'Mi hijo Tristán es el encargado de vender productos. Ve y habla con él si quieres comprar algo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_SheepDisappearing', 'dl_npc_ivo','That''s true, unfortunately.','Das ist leider wahr.','Da isch leider wohr.', 'Eso es cierto desafortunadamente.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_SheepQuest', 'dl_npc_ivo','Lately, some of my sheep went missing. Could be a wolf, could be some bandits.','In letzter Zeit sind einige meiner Schafe verschwunden. Das könnten Wölfe gewesen sein, oder auch Banditen.','I letzter Ziit sind einigi vo mine Schöfli verschwunde. Das chönted Wölf gsi sii, oder au Bandite.', 'Recientemente, algunas de mis ovejas desaparecieron. Pudo haber sido un lobo o bandidos.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_Grateful', 'dl_npc_ivo','I would be very grateful if you could take care of the thief.','Ich wäre dir sehr dankbar, wenn du dich um den Dieb kümmern könntest.','Ich wär dir sehr dankbar, wenn du dich um de Dieb kümmere chöntsch.', 'Te agradecería mucho si pudieras encargarte del ladrón.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_WhereStart', 'dl_npc_ivo','Where should I start searching for the thief?','Wo soll ich mit der Suche beginnen?','Wo sötti afange sueche?', '¿Por dónde debería empezar a buscar al ladrón?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_ZeffIsIt', 'dl_npc_ivo','I know who the thief is...','Ich weiss, wer der Dieb ist...','Ich weiss, wer de Dieb isch...', 'Sé quién es el ladrón...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_MonsterBanished', 'dl_npc_ivo','The sheep thief won''t bother you anymore.','Der Schafdieb wird euch keine Probleme mehr machen.','De Schöfli-Dieb wird eu ke Problem me mache.', 'El ladrón de ovejas ya no te va a molestar.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Back', 'dl_npc_ivo','[BACK]','[ZURÜCK]','[ZRUGG]','[REGRESAR]');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_AskTristan', 'dl_npc_ivo','Hm. You could ask my son, Tristan, he''s in charge of tending the sheep.','Hm. Du könnstest meinen Sohn, Tristan, fragen. Er hütet momentan die Schafe.','Hm. Du chöntsch min Sohn, de Tristan, fröge. Er hüetet momentan d''Schöfli.', 'Hmm... le podrías preguntar a mi hijo, Tristán. Él está a cargo de las ovejas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Cendric_ZeffIsIt', 'dl_npc_ivo','A wolf-monster named Zeff is responsible for your missing sheep!','Ein Wolfmonster namens Zeff ist verantwortlich für die Diebstähle!','Es Wolfmonster namens Zeff isch verantwortlich für die Diebstähl!','Un monstruo lobo llamado Zeff es el responsable de que desaparezcan tus ovejas.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_GoAndKill', 'dl_npc_ivo','Interesting. And now go and make sure that he won''t steal any more sheep. How you do it is for you to decide.','Interessant. Und jetzt geh und stell sicher, dass nicht noch mehr Schafe wegkommen. Wie du das machst, ist dir überlassen.','Interessant. Und jetzt gang und stell sicher, dass nid na me Schöfli wegchömed. We du das machsch, isch dir überlah.','Interesante. Ahora ve y asegúrate de que no vuelva a robar más ovejas. Tú decides cómo hacerlo.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Ivo_ThankYou', 'dl_npc_ivo','Really? The thief won''t come again? Thank you so much for helping us! Go to Tristan and tell him to give you a reward.','Wirklich? Der Schafdieb wird nicht wieder kommen? Danke dir vielmals für deine Hilfe! Geh zu Tristan und sag ihm, dass er dir deine Belohnung geben soll.','Würkli? De Schöflidieb wird nid wider cho? Danke dir tusig mal für dini Hilf. Gang zum Tristan und seg em, dass er dir dini Belohnig gä söll.','¿De verdad? ¿El ladrón no vendrá de nuevo? Muchas gracias por ayudarnos. Busca a Tristán y dile que te dé tu recompensa.');
