INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_jack', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_WhoAreYou', 'dl_npc_jack', 'I''m Jack. Usually I''d sit in my lighthouse instead of this bawdy house here.', 'Ich bin Jack. Normalerweise wär ich ja in meinem Leuchtturm und nicht hier bei den Dirnen.', 'Ich bi de Jack. Normalerwiis wäri ja i mim Lüüchtturm und nid da i dem Puff.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_WhoAreYou2', 'dl_npc_jack', 'But I can''t go back as long as that thing flies around there.', 'Aber ich kann nicht zurück solange dieses Viech dort herumschwirrt.', 'Aber i de chani nid zrugg solang das Viech det umeflügt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Elysia', 'dl_npc_jack', 'What is this "thing" you''re talking about?', 'Von was für einem "Viech" sprichst du?', 'Was füre "Viech" isches denn?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_Elysia', 'dl_npc_jack', 'It looks like a huge bird and is also sounding like one, its shrieks are unbearable.', 'Es sieht aus wie ein riesiger Vogel und tönt auch wie einer, sein Gekreische ist nicht auszuhalten.', 'Es gseht us wenen riesige Vogel und tönt au we eine, sis Umegekreische chame schier nid ushalte.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_Elysia2', 'dl_npc_jack', 'I tried to shoo it away using my frying pan, but it just snatched it away from me and devoured it.', 'Ich habe versucht, ihn mit meiner Bratpfanne zu verscheuchen, aber er hat sie mir entrissen und verschluckt.', 'Ich ha probiert, en mitere Bratpfanne z''verschüüche, aber er het si mer us de Hand grisse und verschluckt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_Elysia3', 'dl_npc_jack', 'It was a really good frying pan, what a shame...', 'Schade um die gute Pfanne...', 'Isch chli schad um die guet Pfanne...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyNotClerics', 'dl_npc_jack', 'Why didn''t you ask the paladins for help?', 'Wieso hast du nicht die Paladine um Hilfe gefragt?', 'Weso hesch nid d''Paladin um Hilf gfrögt?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HelpJack', 'dl_npc_jack', 'I could take care of that problem.', 'Ich könnte mich um dieses Problem kümmern.', 'Ich chönt mich um da Problem kümmere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ElysiaKilled', 'dl_npc_jack', 'I''ve found your frying pan.', 'Ich habe deine Bratpfanne gefunden.', 'Ich ha dini Bratpfanne gfunde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_WhyNotClerics', 'dl_npc_jack', 'Oh, I did! But that sneaky chicken wouldn''t show up when they looked for it.', 'Oh, das habe ich! Aber dieses hinterhältige Federvieh wollte sich nicht zeigen als sie es gesucht haben.', 'Oh, das hani! Aber da hinterhältig Federviechli het sich nid wele zeige wo si go luege sind.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_WhyNotClerics2', 'dl_npc_jack', 'Now they just won''t believe me anymore. But I really don''t want to end up as bird food, I just hope it will leave my lighthouse soon.', 'Jetzt glauben sie mir einfach nicht mehr. Aber ich möchte wirklich nicht als Vogelfutter enden - hoffentlich macht er es sich nicht gemütlich in meinem Turm.', 'Jetzt glaubed si mer eifach nüm. Aber ich wet echt nid als Vogelfueter ende, drum hoffi er flügt bald wieder weg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_HelpJack', 'dl_npc_jack', 'Really? You''d do me a big favour!', 'Wirklich? Du würdest mir einen grossen Gefallen tun!', 'Würkli? Du würsch mer en grosse Gfalle mache!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_HelpJack2', 'dl_npc_jack', 'Here, take the key to my lighthouse. I locked it to prevent people from getting eaten.', 'Da, nimm den Schlüssel zu meinem Leuchtturm. Ich hatte ihn zugeschlossen damit niemand gefressen wird.', 'Do, nimm de Schlüssel zu mim Lüüchtturm. Ich hanen abgschlosse damit niemert ufgfresse wird.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_HelpJack3', 'dl_npc_jack', 'Oh, and while you''re on it, it would be awesome if you could get me my frying pan back.', 'Oh und wenn du schon dabei bist, es wäre toll wenn du mir meine Pfanne wiederbringen könntest.', 'Oh und wend grad debi bisch, es wär würkli toll wend mer mini Pfanne zrugghole chöntsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_ElysiaKilled', 'dl_npc_jack', 'You really did it! That''s my good old pan.', 'Du hast es wirklich geschafft! Das ist meine gute alte Pfanne.', 'Du hesches würkli gschafft! Da isch mini guet alt Pfanne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_ElysiaKilled2', 'dl_npc_jack', 'Now that that chicken''s gone, I can finally go back to my lighthouse.', 'Jetzt wo dieses Federvieh weg ist kann ich endlich wieder in meinen Turm.', 'Jetzt wo das Federviech weg isch chani endlich wieder i min Turm zrugg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jack_ElysiaKilled3', 'dl_npc_jack', 'Here, take the gold I was going to spend here, I don''t need it anymore.', 'Hier, nimm das Gold dass ich hier ausgeben wollte, jetzt brauch ich''s nicht mehr.', 'Da, nimm''s Gold woni da ha wele usge, da bruchi ez nüm.');
