INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jason_Hello', 'dl_npc_jason', 'Hm? Just because Rana loves to talk does not mean I do. Keep it short.', 'Hm? Nur weil Rana gerne viel redet, heisst das nicht, dass ich das auch tue. Halte dich kurz.', 'Hm? Nur wel d''Rana gern viel reded heisst das nid, dasi das au mach. Halt dich churz.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_JasonQuest', 'dl_npc_jason', 'Can I help you somehow?', 'Kann ich dir irgendwie helfen?', 'Chanich dir irgendwie helfe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GargoyleDust', 'dl_npc_jason', 'I got some gargoyle dust for you.', 'Ich habe etwas Gargoylestaub für dich.', 'Ich ha chli Gargoylestaub für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jason_JasonQuest', 'dl_npc_jason', 'Hrm. Yes, actually, you can. My new weapon broke and it can only be fixed with enough gargoyle dust.', 'Hrm. Ja, kannst du eigentlich. Meine neue Waffe ist zerbrochen und sie kann nur mit etwas Gargoylestaub geflickt werden.', 'Hrm. Jo, chasch eigentlich. Mini neu Waffe isch verbroche und si cha nur mit bitz Gargoylestaub gflickt werde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jason_JasonQuest2', 'dl_npc_jason', 'Some gargoyles live in the old temple in the North. If you bring me some gargoyle dust, you can have my old glaive in exchange.', 'Ein paar Gargoyles leben im alten Tempel im Norden. Wenn du mir etwas Gargoylestaub bringst, kannst du dafür meine alte Gleve haben.', 'Es paar Gargoyles lebed im alte Tempel im Norde. Wenn du mir chli Gargoylestau bringsch, gibi der mini alt Gleve defür.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jason_GargoyleDust', 'dl_npc_jason', 'Oh? What a surprise. I never thought you''d be able to kill them.', 'Oh? Was für eine Überraschung. Ich hätte nicht gedacht, dass du sie töten kannst.', 'Oh? Was für Überraschig. Ich hett nie denkt, dass du si töte chasch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jason_GargoyleDust2', 'dl_npc_jason', 'Yeah. But promise is promise. Here, take my old glaive. It''s still a good weapon.', 'Yeah. Aber versprochen ist versprochen. Hier, nimm meine alte Gleve. Es ist immer noch eine gute Waffe.', 'Yeah. Aber versproche isch versproche. Do, nimm mini alt Gleve. Es isch immerno e gueti Waffe.');
