INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Back', 'dl_npc_jason', 'I''ll come back later. [BACK]', 'Ich komme später wieder. [ZURÜCK]', 'Ich chume spöter wieder. [ZRUGG]');
