INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Helmet', 'dl_npc_jeremy', 'Do you know something about the helmet of a certain paladin?', 'Weisst du etwas über den Helm eines gewissen Paladins?', 'Weisch du öppis über de Helm vomne gwüsse Paladin?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_jeremy', 'Who are you guys?', 'Wer seid ihr?', 'Wer sind ihr Lütlis?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_Helmet', 'dl_npc_jeremy', 'Oh, you mean that pathetic paladin that gambled away everything he had? I''ll have to disappoint you, we don''t have that helmet anymore.', 'Oh, du meinst diesen erbärmlichen Paladin der alles verspielt hat? Ich muss dich enttäuschen, diesen Helm haben wir nicht mehr.', 'Oh, du meinsch de erbärmlich Paladin wo alles verzockt het? Da muni di enttüsche, de Helm hämmer nümme.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_Helmet2', 'dl_npc_jeremy', 'We lost it somewhere in the sewers of Gandria. Good luck finding it there, between the nasty rats and oozes...', 'Wir haben ihn irgendwo in der Kanalisation von Gandria verloren. Viel Spass, wenn du ihn dort finden willst, zwischen all den hässlichen Ratten und Schleimen.', 'Mir henden irgendwo ide Kanalisation vo Gandria verlore. Viel Spass wenden det finde wetsch, zwüsched all de grusige Ratte und Schleimlis.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_WhoAreYou', 'dl_npc_jeremy', 'Just some mercenaries. None of your business, buddy.', 'Nur ein paar Söldner. Geht dich nichts an, Kumpel.', 'Bloss es paar Söldner. Gaht dich nüt ah, Kolleg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotYourBuddy', 'dl_npc_jeremy', 'I''m not your buddy, guy.', 'Ich bin dich dein Kumpel, Alter.', 'Ich bi nid din Kolleg, Alte.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Okay', 'dl_npc_jeremy', 'Okay...', 'Okay...', 'Okay...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_NotYourGuy', 'dl_npc_jeremy', 'I''m not your guy, friend.', 'Ich bin nicht dein Alter, Freund.', 'Ich bi nid din Alte, Homie.');
