INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Helmet', 'dl_npc_jeremy', 'Do you know something about the helmet of a certain paladin?', 'Weisst du etwas über den Helm eines gewissen Paladins?', 'Weisch du öppis über de Helm vomne gwüsse Paladin?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LeeroyChicken', 'dl_npc_jeremy', 'Helena wants you to leave her inn. Get out.', 'Helena will, dass ihr ihre Taverne verlässt. Raus mit euch.', 'D''Helena wett eu nüm do ha. Ihr sötted besser go.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_jeremy', 'Who are you guys?', 'Wer seid ihr?', 'Wer sind ihr Lütlis?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ConfrontInina', 'dl_npc_jeremy', '(Confront Jeremy about Inina)', '(Jeremy auf Inina ansprechen)', '(De Jeremy uf d''Inina aspreche)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WontLeave', 'dl_npc_jeremy', 'I won''t leave. Where have you taken her?', 'Ich werde nicht gehen. Wo habt ihr sie hingebracht?', 'Ich wird nid go. Wo hender si hibrocht?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AttackBoss', 'dl_npc_jeremy', 'I have to beat it out of you then. [ATTACK]', 'Ich muss es wohl aus euch herausprügeln. [ANGREIFEN]', 'Ich mueses glaub us eu useprügle. [AAGRIFE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_Helmet', 'dl_npc_jeremy', 'Oh, you mean that pathetic paladin that gambled away everything he had? I''ll have to disappoint you, we don''t have that helmet anymore.', 'Oh, du meinst diesen erbärmlichen Paladin der alles verspielt hat? Ich muss dich enttäuschen, diesen Helm haben wir nicht mehr.', 'Oh, du meinsch de erbärmlich Paladin wo alles verzockt het? Da muni di enttüsche, de Helm hämmer nümme.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_Helmet2', 'dl_npc_jeremy', 'We lost it somewhere in the sewers of Gandria. Good luck finding it there, between the nasty rats and ooze...', 'Wir haben ihn irgendwo in der Kanalisation von Gandria verloren. Viel Spass, wenn du ihn dort finden willst, zwischen all den hässlichen Ratten und Schleimen.', 'Mir henden irgendwo ide Kanalisation vo Gandria verlore. Viel Spass wenden det finde wetsch, zwüsched all de grusige Ratte und Schleimlis.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_LeeroyChicken', 'dl_npc_jeremy', '(Chuckles) And why exactly should we leave? We like it here.', '(Lacht) Und wieso genau sollten wir gehen? Uns gefällt es hier.', '(Gigglet) Und wieso genau söttemer go? Es gfallt üs do.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_LeeroyChicken2', 'dl_npc_jeremy', 'You should have a really good reason to make us leave - and you don''t seem to have one. Get lost, kid.', 'Du solltest einen wirklich guten Grund für uns haben, wenn wir hier raus sollen - und den scheinst du nicht zu haben. Geh jetzt besser, Kleiner.', 'Du söttsch en würkli guete Grund für üs ha, dasmer vo do weg gönd - und de hesch definitiv nid. Gang ez besser, Chliine.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_WhoAreYou', 'dl_npc_jeremy', 'Just some mercenaries. None of your business, buddy.', 'Nur ein paar Söldner. Geht dich nichts an, Kumpel.', 'Bloss es paar Söldner. Gaht dich nüt ah, Kolleg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ConfrontDirect', 'dl_npc_jeremy', 'Have you heard, the High Priestess Inina was abducted!', 'Habt ihr''s schon gehört? Die Hohepriesterin Inina wurde entführt!', 'Henders scho ghört? D''Hohepriesterin Inina isch entführt worde!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ConfrontNicely', 'dl_npc_jeremy', 'Have you seen the High Priestess Inina lately?', 'Habt ihr die Hohepriesterin Inina in letzter Zeit gesehen?', 'Hend ihr d''Hohepriesterin Inina letzins gse?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ConfrontHarsh', 'dl_npc_jeremy', 'We know that you abducted Inina. Confess!', 'Wir wissen, dass ihr Inina entführt habt. Gesteht!', 'Mir wüssed, dass ihr d''Inina entführt hend. Gends zue!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_ConfrontDirect', 'dl_npc_jeremy', 'Interesting. And why are you telling me this?', 'Interessant. Und wieso erzählst du mir das?', 'Interessant. Und wieso verzellsch mer das?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ConfrontDirect1', 'dl_npc_jeremy', 'We suspect you have something to do with her abduction.', 'Wir vermuten ihr habt etwas mit ihrer Entführung zu tun.', 'Mir glaubed ihr hend öppis mit ihrere Entführig z''tue.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Nevermind', 'dl_npc_jeremy', 'Eh, nevermind...', 'Egal, vergiss es...', 'Egal, vergisses...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_ConfrontNicely', 'dl_npc_jeremy', 'No, we haven''t.', 'Nein, haben wir nicht.', 'Nei, hemmer nid.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_ConfrontHarsh', 'dl_npc_jeremy', '(Dangerously quiet) You don''t have any evidence. Mind your own business. Shove off.', '(Gefährlich leise) Du hast keine Beweise. Kümmer dich besser um deinen Kram. Verschwinde.', '(Gföhrlich liislig) Du hesch ke Bewiis. Gaht dich au nüt ah. Verschwind.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_WontLeave', 'dl_npc_jeremy', 'You''re skating on thin ice now. I won''t warn you again. Go.', 'Du bewegst dich auf dünnem Eis. Ich warne dich nicht nochmal. Geh.', 'Du bewegsch dich uf dünnem Iis. Ich warn dich nid nomol. Gang.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_AttackBoss', 'dl_npc_jeremy', '(Sighs) You won''t listen, eh. Roy, Morgiana! Let''s teach him a lesson.', '(Seufzt) Du willst einfach nicht hören, was. Roy, Morgiana! Erteilen wir ihm eine Lektion.', '(Süüfzt) Du begriifsches eifach nid, he. Roy, Morgiana! Zeigemer ihm wo de Bartli de Most holt!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotYourBuddy', 'dl_npc_jeremy', 'I''m not your buddy, guy.', 'Ich bin nicht dein Kumpel, Alter.', 'Ich bi nid din Kolleg, Alte.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Okay', 'dl_npc_jeremy', 'Okay...', 'Okay...', 'Okay...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_NotYourGuy', 'dl_npc_jeremy', 'I''m not your guy, friend.', 'Ich bin nicht dein Alter, Freund.', 'Ich bi nid din Alte, Homie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotYourFriend', 'dl_npc_jeremy', 'I''m not your friend, buddy.', 'Ich bin nicht dein Freund, Kumpel.', 'Ich bi nid din Homie, Kolleg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jeremy_Nothing', 'dl_npc_jeremy', '...', '...', '...');
