INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Hello', 'dl_npc_jonathan2', 'Ah, finally. We need to talk.', 'Ah, endlich. Wir müssen reden.', 'Ah, endlich. Mer mönd rede.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Whatup', 'dl_npc_jonathan2', 'What''s the matter?', 'Was ist los?', 'Was isch los?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_House', 'dl_npc_jonathan2', 'I thought you didn''t want to leave your house?', 'Ich dachte, du wolltest dein Haus nicht verlassen?', 'Ich ha denkt du wetsch nid usem Huus ga?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Whatup', 'dl_npc_jonathan2', 'There''s been a raid on my house, oh it was terrible.', 'Mein Haus wurde überfallen, oh es war schrecklich.', 'Mis Huus isch überfalle worde, oh es isch schrecklich gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Whatup2', 'dl_npc_jonathan2', 'I first thought the clerics were responsible, because they told me they worked for the king.', 'Zuerst dachte ich, die Kleriker würden dahinterstecken, weil sie mir gesagt haben, sie würden für den König arbeiten.', 'Ich ha zersch denkt, d''Kleriker würded dehinder stecke well si mir gseit hend si schaffed für de König.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Whatup3', 'dl_npc_jonathan2', 'But the clerics would not wear purple robes... Anyway, they searched through everything and stole our map.', 'Aber die Kleriker würden keine violetten Roben tragen... Egal, sie haben alles durchsucht und unsere Karte gestohlen.', 'Aber d''Kleriker würed ke violetti Robe träge... Egal, si hend alles dursuecht und üsi Charte klaut.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Whatup4', 'dl_npc_jonathan2', 'Finally, they tried to attack me - but I fought back and got away while their burning robes distracted them.', 'Dann haben sie versucht, mich anzugreifen - aber ich habe mich gewehrt und bin geflüchtet, als sie durch ihre brennenden Roben abgelenkt waren.', 'Denn hends probiert, mich aazgriife - aber ich han mich gewehrt und bi gflüchtet wo si vo ihrne brennende Robe abglenkt gsi sind.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Whatup5', 'dl_npc_jonathan2', 'I was afraid they''d come after you too. I''m glad they didn''t get you yet.', 'Ich fürchtete, sie wären auch hinter dir her. Ich bin froh, dass sie dich noch nicht erwischt haben.', 'Ich han Angst gha dass si au hinter dir her sind. Bin froh, dass si dich nanig verwütscht hend.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_House', 'dl_npc_jonathan2', 'Yeah that''s true. But I had to.', 'Ja das stimmt. Aber ich msste.', 'Jo das stimmt. Aber ich han möse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyAfterMe', 'dl_npc_jonathan2', 'Why would they be after me?', 'Wieso sollten sie hinter mir her sein?', 'Weso sötteds hinter mir her sii?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Describe', 'dl_npc_jonathan2', 'Can you desribe them?', 'Kannst du sie beschreiben?', 'Chasch si beschriibe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyMap', 'dl_npc_jonathan2', 'Why did they steal the map?', 'Wieso haben sie die Karte gestohlen?', 'Weso hends d''Charte klaut?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Map', 'dl_npc_jonathan2', 'What should we do now without the map?', 'Was sollten wir jetzt ohne die Karte tun?', 'Was sölemer ez ohni d''Charte mache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhyAfterMe', 'dl_npc_jonathan2', 'They were clearly looking for elementalists. They needed someone to help them follow the instructions on the map.', 'Sie haben offensichtlich nach Elementarmagier gesucht. Sie brauchen jemanden, der den Anweisungen auf der Karte folgen kann.', 'Si hend offesichtlich noch Elementarmagier gsuecht. Si bruched öpper wo de Awiisige uf de Charte folge chan.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Describe', 'dl_npc_jonathan2', 'There were two of them, mages in long, purple robes. They wielded some kind of purple magic, something I''ve never seen before.', 'Sie waren zu zweit, Magier in langen, violetten Roben. Sie haben eine seltsame, violette Magie benutzt, die ich noch nie zuvor gesehen habe.', 'Si sind z''zweite gsi, Magier i lange, violette Robe. Si hend e violetti Magie brucht, öppis woni bis ez nanie gse han.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhyMap', 'dl_npc_jonathan2', 'Maybe they knew more about it than we do... ', 'Vielleicht wissen sie mehr davon als wir...', 'Vilicht wüsseds me devo we mir...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Map', 'dl_npc_jonathan2', 'I still know where it lead. That''s why I wanted to meet you here.', 'Ich weiss immer noch wohin sie geführt hat. Deshalb wollte ich dich hier treffen.', 'Ich weiss immerno wo si anegführt het. Drum hani di au do treffe wele.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Map2', 'dl_npc_jonathan2', 'It lead to the Highlands of Admantris. And the key to get there was elemental magic.', 'Sie hat ins Hochland von Admantris geführt. Und der Schlüssel, um dort hinzukommen war Elementarmagie.', 'Si het is Hochland vo Admantris gführt. Und de Schlüssel zum det hicho isch Elementarmagie gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_Elemental', 'dl_npc_jonathan2', 'I''ve mastered spells from all elements.', 'Ich habe Zauber von allen Elementen gelernt.', 'Ich ha Zauber vo allne Element glernt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Element', 'dl_npc_jonathan2', 'That''s awesome. You should be able to unveil the secret of the old elementalists then.', 'Das ist toll. Du solltest das Geheimnis der alten Elementarmagier nun enthüllen können.', 'Das isch toll Du sötsch es Gheimnis vode alte Elementarmagier ez enthülle chöne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Element2', 'dl_npc_jonathan2', 'Follow the path to the highlands and find the temple of the ancients. ', 'Folge dem Pfad ins Hochland und finde den Tempel der Ältesten.', 'Folg em Pfad is Hochland und find de Tempel vode Älteste.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Element3', 'dl_npc_jonathan2', 'Find the secret before those other mages do it.', 'Finde das Geheimnis bevor diese anderen Magier es tun.', 'Finds Gheimnis bevors di andere Magier finded.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Element4', 'dl_npc_jonathan2', 'I''ll do my best to keep them from following you. Hurry!', 'Ich werde mein bestes geben, um sie davon abzuhalten, dir zu folgen. Beeil dich!', 'Ich wird mis beste gä zum si devo abhalte, dir z''folge. Mach schnell!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Go', 'dl_npc_jonathan2', 'What are you waiting for? Hurry!', 'Auf was wartest du? Beeil dich!', 'Uf was wartisch? Mach schnell!');
