INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Hey', 'dl_npc_jonathan3', 'You''re back, perfect. I found the mages who robbed me and secretly followed them to this crypt.', 'Du bist zurück, perfekt. Ich habe die Magier gefunden, die mich ausgeraubt haben, und bin ihnen bis zu dieser Krypta gefolgt.', 'Du bisch zrugg, perfekt. Ich ha die Magier gfunde, wo mich usgraubt hend, und bi ihne bis zu dere Krypta gfolgt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Hey2', 'dl_npc_jonathan3', 'And you, did you find something interesting in the temple?', 'Und du, hast du etwas Interessantes im Tempel gefunden?', 'Und du, hesch öppis interessants im Tempel gfunde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NoBooks', 'dl_npc_jonathan3', 'Did you find some books for me?', 'Hast du ein paar Bücher für mich gefunden?', 'Hesch es paar Büecher für mich gfunde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Books', 'dl_npc_jonathan3', 'I found some books for you. (Give documents)', 'Ich habe ein paar Bücher für dich gefunden. (Dokumente geben)', 'Ich ha es paar Büecher für dich gfunde. (Dokument gä)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoBook', 'dl_npc_jonathan3', 'I''ve found a library but I still need some books for you.', 'Ich habe eine Bibliothek gefunden, aber ich brauche noch ein paar Bücher für dich.', 'Ich hane Bibliothek gfunde, aber ich bruch no es paar Büecher für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Books', 'dl_npc_jonathan3', 'Woah. Woah! (Jonathan skims the documents) ', 'Woah. Woah! (Jonathan fliegt über die Dokumente)', 'Woah. Woah! (De Jonathan flügt über d''Dokument)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Books2', 'dl_npc_jonathan3', 'That''s exactly what we were looking for. The old, forgotten anti-magic. Dangerous magic.', 'Das ist genau, nach was wir gesucht haben. Die alte, vergessene Antimagie. Gefährliche Magie.', 'Das isch gnau das, noch dem mer gsuecht hend. Di alt, vergesse Antimagie. Gföhrlichi Magie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Books3', 'dl_npc_jonathan3', 'Even though some pages are missing in this book, I should return as fast as possible to the academy. Will you come with me?', 'Obwohl hier einige Seiten in diesem Buch fehlen, sollte ich so schnell wie möglich zur Akademie zurück. Kommst du mit?', 'Obwohl do einigi Siite i dem Buech fähled, sötti so schnell we möglich zude Akademie zrugg. Chunsch mit?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Business', 'dl_npc_jonathan3', 'I still got business here.', 'Ich habe hier immer noch zu tun.', 'Ich ha do immerno z''tue.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LoveTo', 'dl_npc_jonathan3', 'I''d love to...', 'Ich würde ja gern...', 'Ich wür jo gern...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Business', 'dl_npc_jonathan3', 'You''re right. You should follow those mages. Maybe you even find the missing pages. See you at the academy!', 'Du hast recht. Du solltest diesen Magiern folgen. Vielleicht findest du sogar die fehlenden Seiten. Man sieht sich in der Akademie!', 'Du hesch recht. Du sötsch dene Magier folge. Vilicht findsch sogar die fehlende Siite. Me gseht sich ide Akademie!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LoveTo', 'dl_npc_jonathan3', 'But you need to follow those mages, right? Maybe you even find the missing pages. Anyway, see you at the academy!', 'Aber du musst diesen Magiern folgen, oder? Vielleicht findest du sogar die fehlenden Seiten. Auf jeden Fall sieht man sich in der Akademie!', 'Aber du muesch dene Magier folge, gell? Vilicht findsch sogar di fehlende Siite. Uf jedefall gseht me sich ide Akademie!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NoBook', 'dl_npc_jonathan3', 'Alright. I''ll wait here then.', 'Gut. Ich warte dann hier.', 'Guet. Ich warte denn do.');
