INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WalkIntoHouse', 'dl_npc_jonathan', 'Hey, what are you doing here? You can''t just walk into other people''s houses.', 'Hey, was machst du hier? Du kannst nicht einfach in die Häuser anderer Leute reinspazieren.', 'Hey, was machsch du da? Du chasch nid eifach id Hüser vo fremde Lüt iespaziere!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_DoorWasOpen', 'dl_npc_jonathan', 'But... the door was open.', 'Aber... die Türe war offen.', 'Aber... d''Türe isch offe gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LookingForYou', 'dl_npc_jonathan', 'I was looking for you.', 'Ich habe nach dir gesucht.', 'Ich ha dich gsuecht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NoExcuNdse', 'dl_npc_jonathan', 'Well, that''s not really an excuse. Why are you here?', 'Na, das ist nicht wirklich eine Ausrede. Wieso bist du hier?', 'Ja guet, aber da isch nid würkli en Usred. Weso bisch da?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatDoYouWant', 'dl_npc_jonathan', 'What do you want from me then?', 'Was willst du denn von mir?', 'Was wetsch denn vo mir?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Letter', 'dl_npc_jonathan', 'Someone wants me to meet you. (Show the letter and give the map)', 'Jemand will, dass ich dich treffe. (Brief zeigen und Karte geben)', 'Öppert will, dass ich dich triff. (Brief zeige und Chartä ge)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Leave', 'dl_npc_jonathan', 'I was just about to leave.', 'Ich wollte gerade wieder gehen.', 'Ich bi ja scho wider weg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Letter', 'dl_npc_jonathan', '(Reads the letter carefully) Cyrus! So good to hear from him. So, you''re from the academy? How are things going there? And what did Cyrus find out?', '(Liest den Brief sorgfältig) Cyrus! Es ist gut, von ihm zu hören. Du kommst also von der Akademie? Ist dort alles in Ordnung? Und was hat Cyrus herausgefunden?', '(List de Brief sorgfältig) Cyrus! Es isch guet, vo ihm z''ghöre. Du chunsch also vode Akademie? Was het de Cyrus denn usegfunde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Disappoint', 'dl_npc_jonathan', 'I''m sorry to disappoint you...', 'Ich muss dich leider enttäuschen...', 'Ich mu dich leider enttüsche...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_TellsTheStory', 'dl_npc_jonathan', '(Cendric tells the story about losing his memory and getting here)', '(Cendric erzählt die Geschichte wie er sein Gedächnis verlor und hier gelandet ist)', '(Cendric verzellt d''Gschicht we er sis Gedächnis verlore het und da glandet isch)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Wow', 'dl_npc_jonathan', 'Wow... Losing the your whole memory is pretty unusual, even for a mage.', 'Wow... Das ganze Gedächnis zu verlieren ist ziemlich ungewöhnlich, auch für einen Magier.', 'Wow... Es ganz Gedächnis z''verlüre isch zimli ungwöhnlich, au füren Magier.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Wow2', 'dl_npc_jonathan', 'And summoning an iceball is the only spell you remember, right? It''s good you came here. We will solve this together.', 'Und einen Eisball zu beschwören ist der einzige Zauber, an den du dich erinnerst, ja? Es ist gut, dass du zu mir gekommen bist. Wir werden das zusammen lösen.', 'Und en Iisball z''beschwöre isch de einzig Zauber wod na chasch, gell. Es isch guet bisch zu mir cho. Mir werded das zeme uflöse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Academy', 'dl_npc_jonathan', 'What academy are you talking about?', 'Von was für einer Akademie sprichst du?', 'Vo wa fürere Akademie redsch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Elementalists', 'dl_npc_jonathan', 'What are elementalists?', 'Was sind Elementarmagier?', 'Was sind Elementarmagier?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Affinity', 'dl_npc_jonathan', 'What does "affinity" mean?', 'Was bedeutet "Affinität"?', 'Was heisst "Affinität"?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatShouldWeDo', 'dl_npc_jonathan', 'And what should we do now?', 'Und was sollten wir jetzt tun?', 'Und wa sölemer jetz mache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ProveMyself', 'dl_npc_jonathan', 'How can I prove myself?', 'Wie kann ich mich beweisen?', 'We chan ich mich bewiise?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Academy', 'dl_npc_jonathan', 'The only academy in Admantris where elementalists are educated. It is situated on the other side of the sea. You have obviously studied there.', 'Die einzige Akademie in Admantris, die Elementarmagier ausbildet. Sie befindet sich auf der anderen Seite des Meeres. Du hast offensichtlich dort studiert.', 'Di einzig Akademie in Admantris wo Elementarmagier usbildet. Si liit uf de andere Siite vom Meer. Du hesch offesichtlich det studiert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Elementalists', 'dl_npc_jonathan', 'Mages with an affinity to elemental magic. There are not too much left in Admantris, but I''m one of them... and you are - probably - too.', 'Magier mit einer Affinität zur Elementarmagie. Es gibt nicht mehr viele davon in Admantris aber ich bin einer von ihnen... und du - wahrscheinlich - auch.', 'Magier mit enere Affinität zude Elementarmagie. Es git nüm so viel devo in Admantris, aber ich bi eine... und du - wahrschinli - au.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Affinity', 'dl_npc_jonathan', 'Each mage is born with a certain affinity to a type of magic. This affinity determines which spells he can learn. We elementalists are lucky - we are able to understand the whole spectrum of magic.', 'Jeder Magier wird mit einer gewissen Affinität zu einem Magietyp geboren. Dieser bestimmt, welche Zauber er erlernen kann. Wir Elementarmagier haben Glück - wir können das gesammte Spektrum der Magie verstehen.', 'Jede Magier wird mit enere gwüsse Affinität zumne Magietyp gebore. De bestimmt, weli Zauber er lerne chan. Mir Elementarmagier hend Glück - mir chönd es ganze Spektrum vode Magie verstah.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NextStep', 'dl_npc_jonathan', 'Hm. I knew that Cyrus was going to send me one of his students to support my research here. But to be honest, I hoped for someone who is a bit more...', 'Hm. Ich wusste, dass mir Cyrus einer seiner Studenten zur Unterstützung meiner Forschungen schicken würde. Aber um ehrlich zu sein, habe ich mit jemandem gerechnet, der etwas mehr...', 'Hm. Ich ha scho gwüsst, dass de Cyrus mir eine vo sine Studente schicke wird. Aber zum ehrlich si, ich ha mit öpperem grechnet wo chli me... ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NextStep2', 'dl_npc_jonathan', '...qualified. Also, someone who lost his memory is suspicious. Before I let you in on my studies, you should first prove that you''re a worthy elementalist.', '...qualifiziert ist. Ausserdem ist jemand, der sein Gedächnis verloren hat, ein bisschen verdächtig. Bevor ich dich in meine Forschungen einweihe, musst du beweisen, dass du ein würdiger Elementarmagier bist.', '...qualifiziert isch. Usserdem isch öpper, wo sis Gedächnis verlore het, scho zimli verdächtig. Bevor ich dich i mini Forschige iweihe, muesch zersch bewiise, dass du en würdige Elementarmagier bisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_FireballQuest', 'dl_npc_jonathan', 'Every mage is able to learn how to summon an iceball, it''s an easy spell. Controlling fire, on the other hand, is considered nearly impossible for mages without an elemental affinity.', 'Jeder Magier kann lernen, wie man einen Eisball beschwört. Feuer zu kontrollieren ist für Magier ohne eine Affinität zur Elementarmagie aber fast unmöglich.', 'Jede Magier cha lerne, we me en Iisball beschwört. Füür z''kontrolliere isch aber für en Magier ohni Affinität zur Elementarmagie fascht unmöglich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_FireballQuest2', 'dl_npc_jonathan', 'Some fire rats from the sewers recently invaded the laboratory in my basement. Find a way to lure them and collect the essence of fire from their lungs. Come back to me when you''re done and I''ll see if you''re able to learn how to summon a fireball.', 'Ein paar Feuerratten aus der Kanalisation sind in mein Labor in meinem Keller eingedrungen. Finde heraus, wie du sie anlocken kannst und bringe mir die Essenzen des Feuers aus ihren Lungen. Wenn du zurück bist, werde ich sehen, ob ich dir beibringen kann, wie man einen Feuerball beschwört.', 'Es paar Füürratte us de Kanalisation sind i mis Labor i mim Chäller idrunge. Find use, wed si alocke chasch und bring mer d''Füüressenze us ihrne Lunge. Denn wirdi mal luege, obi der biibringe cha, weme en Füürball beschwört.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotFireEssence', 'dl_npc_jonathan', 'I got the essences of fire for you.', 'Ich habe die Essenzen des Feuers für dich.', 'Ich han d''Füüressenze für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LearntFireball', 'dl_npc_jonathan', 'I''ve read the scroll.', 'Ich habe die Schriftrolle gelesen.', 'Ich han d''Schriftrolle gläse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CU', 'dl_npc_jonathan', 'See you later.', 'Bis später.', 'Bis spöter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_EssenceComplete', 'dl_npc_jonathan', 'Very good. Maybe you can still become a decent elementalist.', 'Sehr gut. Vielleicht können wir doch noch einen anständigen Elementarmagier aus dir machen.', 'Sehr guet. Vilicht chömmer ja doch na en aständige Elementarmagier us dir mache.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_EssenceComplete2', 'dl_npc_jonathan', 'Here, take this scroll and read it. Talk to me when you''ve learned the spell.', 'Hier, nimm diese Schriftrolle und lies sie. Sprich wieder mit mir wenn du den Zauber erlernt hast.', 'Da, nimm die Schriftrolle und lies si. Red wider mit mir wend de Zauber glernt hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball', 'dl_npc_jonathan', 'And you''ve already acquired its knowledge. I think we''re ready to get started then.', 'Und du hast ihr Wissen bereits erlangt. Ich denke, wir können nun anfangen.', 'Und du hesches au scho tscheggt. Ich denk, mer chönd denn grad afange.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball2', 'dl_npc_jonathan', 'My research focuses on the old, forgotten knowledge of the first elementalists. I already know that they must have worked on spells that are way more powerful than any of the elemental magic that is known today. ', 'Ich forsche an dem alten, vergessenen Wissen der ersten Elementarmagier. Ich habe bereits herausgefunden, dass sie früher Zauber kannten, die weit mächtiger sind, als alle Elementarmagie, die wir heute kennen.', 'Ich forsche am alte, vergessene Wüsse vode erste Elementarmager. Ich han scho usegfunde, das si früener viel mächtigeri Zauber kennt hend, als alli Elementarmagie, womer hüt so bruched.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball3', 'dl_npc_jonathan', 'I''ve found evidence that there''s an abandoned site of the first Elementalists somewhere near Gandria, that''s why I''m working here.', 'Ich habe Beweise gefunden, dass es irgendwo in der Nähe der Stadt Gandria eine verlassene Stätte der ersten Elementarmagier gibt, deshalb arbeite ich hier.', 'Ich han Bewiis gfunde, dass es irgendwo ide Nöchi vo Gandria en verlassene Ort git wo di erste Elementarmagier gläbt hend, drum schaffi au da.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball4', 'dl_npc_jonathan', 'I''m pretty sure that the map that Cyrus sent me is the key to find that location and solve the mysteries that lay there. But there''s only one small problem with that map...', 'Ich bin mir ziemlich sicher dass diese Karte, die Cyrus mir geschickt hat, der Schlüssel ist, um diesen Ort zu finden. Allerdings gibt es da ein kleines Problem mit dieser Karte...', 'Ich bi mer zimli sicher dass die Charte, wo de Cyrus mir gschickt het, de Schlüssel isch zum de Ort z''finde. Es git da aber es chlises Problem mit dere Charte...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball5', 'dl_npc_jonathan', 'To decipher it, elemental magic is not sufficient. But elemental magic combined with the power of another type of magic, we should be able to break the protection spell.', 'Elementarmagie alleine reicht nicht, um sie zu entschlüsseln. Kombiniert mit einem anderen Typ der Magie müsste man den Schutzzauber aber brechen können.', 'Elementarmagie elei langt nid zum si entschlüssle. Zeme mit enere andere Magieart sötmes aber anebringe, de Schutzzauber z''breche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AnotherMage', 'dl_npc_jonathan', 'So, should we ask another mage to help us?', 'Sollten wir also einen weiteren Magier um Hilfe bitten?', 'Söttemer also en andere Magier um Hilf bitte?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatAboutYou', 'dl_npc_jonathan', 'What about you? Don''t you know some other spells?', 'Was ist mit dir? Kennst du keine anderen Zauber?', 'Was isch mit dir? Kennsch du ke anderi Zauber?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BreakingProtection', 'dl_npc_jonathan', 'Do you think breaking a protection spell is a good idea?', 'Denkst du es ist eine gute Idee, einen Schutzzauber zu brechen?', 'Denksch denn es seg e gueti Idee, en Schutzzauber z''breche?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyRisky', 'dl_npc_jonathan', 'Why is that risky for you?', 'Wieso ist das riskant für dich?', 'Weso isch da riskant für dich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_AnotherMage', 'dl_npc_jonathan', 'To be honest, I don''t really trust any other mage in the city. The less people know about this map, the better.', 'Um ehrlich zu sein traue ich keinem anderen Magier in der Stadt. Je weniger Leute von dieser Karte wissen, desto besser.', 'Zum ehrlich si trau ich kem andere Magier i dere Stadt. Je weniger Lüt vo dere Charte wüssed, desto besser.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatAboutYou', 'dl_npc_jonathan', 'Although we elementalists can learn every type of magic, I''ve always focused my studies on elemental magic only. ', 'Obwohl wir Elementarmagier jede Magieart erlernen können, habe ich mich bisher nur mit Elementarmagie beschäftigt.', 'Obwohl mir Elementarmagier jedi Magieart lerne chönd, hani mich bis ez nur mit Elementarmagie beschäftigt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatAboutYou2', 'dl_npc_jonathan', 'And now, it''s too risky for me to go and learn something new. No, I need someone with a face that is not yet known to the citicens of Gandria. Someone like you.', 'Und jetzt ist es zu riskant für mich, etwas neues zu lernen. Nein, ich brauche jemanden, dessen Gesicht hier in Gandria nicht so bekannt ist. Jemanden wie dich.', 'Und jetz isches zu riskant für mich zum öppis neus z''lerne. Nei, ich bruch öpper mitme unbekanntere Gsicht da in Gandria. Öppert we dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_BreakingProtection', 'dl_npc_jonathan', 'You mean, there''s a good reason for this spell? Well, if there is, we''ll find out soon enough.', 'Du meinst, es gibt einen guten Grund für diesen Zauber? Wenn es den gibt, werden wir es früh genug herausfinden.', 'Du meinsch, es git en guete Grund für de Zauber? Ja wens de git, denn findemer da scho gnueg früe use.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhyRisky', 'dl_npc_jonathan', 'It''s just because lately, I get the impression that someone tries to mess with my work. Research notes and people disappear, I don''t think you''re an exception.', 'Es ist einfach so dass ich in letzter Zeit immer mehr das Gefühl habe, dass jemand versucht, meine Arbeit zu stören. Forschungsnotizen und Leute verschwinden plötzlich... Ich denke nicht dass du eine Ausnahme warst.', 'Es isch eifach so, dass ich i letzter Ziit immer me es Gfühl ha, dass öppert probiert, mini Arbet z''störe. Forschigsnotize und Lüt verschwinded plötzlich... Ich denk nid dass du e Usnahm gsi bisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhyRisky2', 'dl_npc_jonathan', 'It doesn''t seem to be a good idea for me to go outside until this city has gone back to normal.', 'Rausgehen scheint keine gute Idee für mich zu sein bis diese Stadt wieder normal ist.', 'Usega isch glaub eifach ke gueti Idee für mich bis die Stadt wider normal isch.');
