INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WalkIntoHouse', 'dl_npc_jonathan', 'Hey, what are you doing here? You can''t just walk into other people''s houses.', 'Hey, was machst du hier? Du kannst nicht einfach in die Häuser anderer Leute reinspazieren.', 'Hey, was machsch du da? Du chasch nid eifach id Hüser vo fremde Lüt iespaziere!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_DoorWasOpen', 'dl_npc_jonathan', 'But... the door was open.', 'Aber... die Türe war offen.', 'Aber... d''Türe isch offe gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LookingForYou', 'dl_npc_jonathan', 'I was looking for you.', 'Ich habe nach dir gesucht.', 'Ich ha dich gsuecht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NoExcuNdse', 'dl_npc_jonathan', 'Well, that''s not really an excuse. Why are you here?', 'Na, das ist nicht wirklich eine Ausrede. Wieso bist du hier?', 'Ja guet, aber da isch nid würkli en Usred. Weso bisch da?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatDoYouWant', 'dl_npc_jonathan', 'What do you want from me then?', 'Was willst du denn von mir?', 'Was wetsch denn vo mir?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Letter', 'dl_npc_jonathan', 'Someone wants me to meet you. (Show the letter and give the map)', 'Jemand will, dass ich dich treffe. (Brief zeigen und Karte geben)', 'Öppert will, dass ich dich triff. (Brief zeige und Chartä ge)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Leave', 'dl_npc_jonathan', 'I was just about to leave.', 'Ich wollte gerade wieder gehen.', 'Ich bi ja scho wider weg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Letter', 'dl_npc_jonathan', '(Reads the letter carefully) Cyrus! So good to hear from him. So, you''re from the academy? How are things going there? And what did Cyrus find out?', '(Liest den Brief sorgfältig) Cyrus! Es ist gut, von ihm zu hören. Du kommst also von der Akademie? Ist dort alles in Ordnung? Und was hat Cyrus herausgefunden?', '(List de Brief sorgfältig) Cyrus! Es isch guet, vo ihm z''ghöre. Du chunsch also vode Akademie? Was het de Cyrus denn usegfunde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Disappoint', 'dl_npc_jonathan', 'I''m sorry to disappoint you...', 'Ich muss dich leider enttäuschen...', 'Ich mu dich leider enttüsche...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_TellsTheStory', 'dl_npc_jonathan', '(Cendric tells the story about losing his memory and getting here)', '(Cendric erzählt die Geschichte wie er sein Gedächnis verlor und hier gelandet ist)', '(Cendric verzellt d''Gschicht we er sis Gedächnis verlore het und da glandet isch)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Wow', 'dl_npc_jonathan', 'Wow... Losing the your whole memory is pretty unusual, even for a mage.', 'Wow... Das ganze Gedächnis zu verlieren ist ziemlich ungewöhnlich, auch für einen Magier.', 'Wow... Es ganz Gedächnis z''verlüre isch zimli ungwöhnlich, au füren Magier.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Wow2', 'dl_npc_jonathan', 'And summoning an iceball is the only spell you remember, right? It''s good you came here. We will solve this together.', 'Und einen Eisball zu beschwören ist der einzige Zauber, an den du dich erinnerst, ja? Es ist gut, dass du zu mir gekommen bist. Wir werden das zusammen lösen.', 'Und en Iisball z''beschwöre isch de einzig Zauber wod na chasch, gell. Es isch guet bisch zu mir cho. Mir werded das zeme uflöse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Academy', 'dl_npc_jonathan', 'What academy are you talking about?', 'Von was für einer Akademie sprichst du?', 'Vo wa fürere Akademie redsch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Elementalists', 'dl_npc_jonathan', 'What are elementalists?', 'Was sind Elementarmagier?', 'Was sind Elementarmagier?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Affinity', 'dl_npc_jonathan', 'What does "affinity" mean?', 'Was bedeutet "Affinität"?', 'Was heisst "Affinität"?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatShouldWeDo', 'dl_npc_jonathan', 'And what should we do now?', 'Und was sollten wir jetzt tun?', 'Und wa sölemer jetz mache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ProveMyself', 'dl_npc_jonathan', 'How can I prove myself?', 'Wie kann ich mich beweisen?', 'We chan ich mich bewiise?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Academy', 'dl_npc_jonathan', 'The only academy in Admantris where elementalists are educated. It is situated on the other side of the sea. You have obviously studied there.', 'Die einzige Akademie in Admantris, die Elementarmagier ausbildet. Sie befindet sich auf der anderen Seite des Meeres. Du hast offensichtlich dort studiert.', 'Di einzig Akademie in Admantris wo Elementarmagier usbildet. Si liit uf de andere Siite vom Meer. Du hesch offesichtlich det studiert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Elementalists', 'dl_npc_jonathan', 'Mages with an affinity to elemental magic. There are not too much left in Admantris, but I''m one of them... and you are - probably - too.', 'Magier mit einer Affinität zur Elementarmagie. Es gibt nicht mehr viele davon in Admantris aber ich bin einer von ihnen... und du - wahrscheinlich - auch.', 'Magier mit enere Affinität zude Elementarmagie. Es git nüm so viel devo in Admantris, aber ich bi eine... und du - wahrschinli - au.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Affinity', 'dl_npc_jonathan', 'Each mage is born with a certain affinity to a type of magic. This affinity determines which spells he can learn. We elementalists are lucky - we are able to understand the whole spectrum of magic.', 'Jeder Magier wird mit einer gewissen Affinität zu einem Magietyp geboren. Dieser bestimmt, welche Zauber er erlernen kann. Wir Elementarmagier haben Glück - wir können das gesammte Spektrum der Magie verstehen.', 'Jede Magier wird mit enere gwüsse Affinität zumne Magietyp gebore. De bestimmt, weli Zauber er lerne chan. Mir Elementarmagier hend Glück - mir chönd es ganze Spektrum vode Magie verstah.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NextStep', 'dl_npc_jonathan', 'Hm. I knew that Cyrus was going to send me one of his students to support my research here. But to be honest, I hoped for someone who is a bit more...', 'Hm. Ich wusste, dass mir Cyrus einer seiner Studenten zur Unterstützung meiner Forschungen schicken würde. Aber um ehrlich zu sein, habe ich mit jemandem gerechnet, der etwas mehr...', 'Hm. Ich ha scho gwüsst, dass de Cyrus mir eine vo sine Studente schicke wird. Aber zum ehrlich si, ich ha mit öpperem grechnet wo chli me... ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NextStep2', 'dl_npc_jonathan', '...qualified. Also, someone who lost his memory is suspicious. Before I let you in on my studies, you should first prove that you''re a worthy elementalist.', '...qualifiziert ist. Ausserdem ist jemand, der sein Gedächnis verloren hat, ein bisschen verdächtig. Bevor ich dich in meine Forschungen einweihe, musst du beweisen, dass du ein würdiger Elementarmagier bist.', '...qualifiziert isch. Usserdem isch öpper, wo sis Gedächnis verlore het, scho zimli verdächtig. Bevor ich dich i mini Forschige iweihe, muesch zersch bewiise, dass du en würdige Elementarmagier bisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_FireballQuest', 'dl_npc_jonathan', 'Every mage is able to learn how to summon an iceball, it''s an easy spell. Controlling fire, on the other hand, is considered nearly impossible for mages without an elemental affinity.', 'Jeder Magier kann lernen, wie man einen Eisball beschwört. Feuer zu kontrollieren ist für Magier ohne eine Affinität zur Elementarmagie aber fast unmöglich.', 'Jede Magier cha lerne, we me en Iisball beschwört. Füür z''kontrolliere isch aber für en Magier ohni Affinität zur Elementarmagie fascht unmöglich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_FireballQuest2', 'dl_npc_jonathan', 'Some fire rats from the sewers recently invaded the laboratory in my basement. Find a way to lure them and collect the essence of fire from their lungs. Come back to me when you''re done and I''ll see if you''re able to learn how to summon a fireball.', 'Ein paar Feuerratten aus der Kanalisation sind in mein Labor in meinem Keller eingedrungen. Finde heraus, wie du sie anlocken kannst und bringe mir die Essenzen des Feuers aus ihren Lungen. Wenn du zurück bist, werde ich sehen, ob ich dir beibringen kann, wie man einen Feuerball beschwört.', 'Es paar Füürratte us de Kanalisation sind i mis Labor i mim Chäller idrunge. Find use, wed si alocke chasch und bring mer d''Füüressenze us ihrne Lunge. Denn wirdi mal luege, obi der biibringe cha, weme en Füürball beschwört.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotFireEssence', 'dl_npc_jonathan', 'I got the essences of fire for you.', 'Ich habe die Essenzen des Feuers für dich.', 'Ich han d''Füüressenze für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LearntFireball', 'dl_npc_jonathan', 'I''ve read the scroll.', 'Ich habe die Schriftrolle gelesen.', 'Ich han d''Schriftrolle gläse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CU', 'dl_npc_jonathan', 'See you later.', 'Bis später.', 'Bis spöter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_EssenceComplete', 'dl_npc_jonathan', 'Very good. Maybe you can still become a decent elementalist.', 'Sehr gut. Vielleicht können wir doch noch einen anständigen Elementarmagier aus dir machen.', 'Sehr guet. Vilicht chömmer ja doch na en aständige Elementarmagier us dir mache.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_EssenceComplete2', 'dl_npc_jonathan', 'Here, take this scroll and read it. Talk to me when you''ve learned the spell.', 'Hier, nimm diese Schriftrolle und lies sie. Sprich wieder mit mir wenn du den Zauber erlernt hast.', 'Da, nimm die Schriftrolle und lies si. Red wider mit mir wend de Zauber glernt hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball', 'dl_npc_jonathan', 'And you''ve already acquired its knowledge. I think we''re ready to get started then.', 'Und du hast ihr Wissen bereits erlangt. Ich denke, wir können nun anfangen.', 'Und du hesches au scho tscheggt. Ich denk, mer chönd denn grad afange.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball2', 'dl_npc_jonathan', 'My research focuses on the old, forgotten knowledge of the first elementalists. I already know that they must have worked on spells that are way more powerful than any of the elemental magic that is known today. ', 'Ich forsche an dem alten, vergessenen Wissen der ersten Elementarmagier. Ich habe bereits herausgefunden, dass sie früher Zauber kannten, die weit mächtiger sind, als alle Elementarmagie, die wir heute kennen.', 'Ich forsche am alte, vergessene Wüsse vode erste Elementarmagier. Ich han scho usegfunde, das si früener viel mächtigeri Zauber kennt hend, als alli Elementarmagie, womer hüt so bruched.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball3', 'dl_npc_jonathan', 'I''ve found evidence that there''s an abandoned site of the first Elementalists somewhere near Gandria, that''s why I''m working here.', 'Ich habe Beweise gefunden, dass es irgendwo in der Nähe der Stadt Gandria eine verlassene Stätte der ersten Elementarmagier gibt, deshalb arbeite ich hier.', 'Ich han Bewiis gfunde, dass es irgendwo ide Nöchi vo Gandria en verlassene Ort git wo di erste Elementarmagier gläbt hend, drum schaffi au da.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball4', 'dl_npc_jonathan', 'I''m pretty sure that the map that Cyrus sent me is the key to find that location and solve the mysteries that lay there. But there''s only one small problem with that map...', 'Ich bin mir ziemlich sicher dass diese Karte, die Cyrus mir geschickt hat, der Schlüssel ist, um diesen Ort zu finden. Allerdings gibt es da ein kleines Problem mit dieser Karte...', 'Ich bi mer zimli sicher dass die Charte, wo de Cyrus mir gschickt het, de Schlüssel isch zum de Ort z''finde. Es git da aber es chlises Problem mit dere Charte...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball5', 'dl_npc_jonathan', 'To decipher it, elemental magic is not sufficient. But using elemental magic combined with the power of another type of magic, we should be able to break the protection spell.', 'Elementarmagie alleine reicht nicht, um sie zu entschlüsseln. Kombiniert mit einem anderen Typ der Magie müsste man den Schutzzauber aber brechen können.', 'Elementarmagie elei langt nid zum si entschlüssle. Zeme mit enere andere Magieart sötmes aber anebringe, de Schutzzauber z''breche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AnotherMage', 'dl_npc_jonathan', 'So, should we ask another mage to help us?', 'Sollten wir also einen weiteren Magier um Hilfe bitten?', 'Söttemer also en andere Magier um Hilf bitte?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatAboutYou', 'dl_npc_jonathan', 'What about you? Don''t you know some other spells?', 'Was ist mit dir? Kennst du keine anderen Zauber?', 'Was isch mit dir? Kennsch du ke anderi Zauber?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BreakingProtection', 'dl_npc_jonathan', 'Do you think breaking a protection spell is a good idea?', 'Denkst du es ist eine gute Idee, einen Schutzzauber zu brechen?', 'Denksch denn es seg e gueti Idee, en Schutzzauber z''breche?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyRisky', 'dl_npc_jonathan', 'Why is that risky for you?', 'Wieso ist das riskant für dich?', 'Weso isch da riskant für dich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatShouldIDo', 'dl_npc_jonathan', 'I see. So, what should I do?', 'Alles klar. Was muss ich also tun?', 'Alles klar. Wa mueni also mache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_AnotherMage', 'dl_npc_jonathan', 'To be honest, I don''t really trust any other mage in the city. The less people know about this map, the better.', 'Um ehrlich zu sein traue ich keinem anderen Magier in der Stadt. Je weniger Leute von dieser Karte wissen, desto besser.', 'Zum ehrlich si trau ich kem andere Magier i dere Stadt. Je weniger Lüt vo dere Charte wüssed, desto besser.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatAboutYou', 'dl_npc_jonathan', 'Although we elementalists can learn every type of magic, I''ve always focused my studies on elemental magic only. ', 'Obwohl wir Elementarmagier jede Magieart erlernen können, habe ich mich bisher nur mit Elementarmagie beschäftigt.', 'Obwohl mir Elementarmagier jedi Magieart lerne chönd, hani mich bis ez nur mit Elementarmagie beschäftigt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatAboutYou2', 'dl_npc_jonathan', 'And now, it''s too risky for me to go and learn something new. No, I need someone with a face that is not yet known to the citizens of Gandria. Someone like you.', 'Und jetzt ist es zu riskant für mich, etwas neues zu lernen. Nein, ich brauche jemanden, dessen Gesicht hier in Gandria nicht so bekannt ist. Jemanden wie dich.', 'Und jetz isches zu riskant für mich zum öppis neus z''lerne. Nei, ich bruch öpper mitme unbekanntere Gsicht da in Gandria. Öppert we dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_BreakingProtection', 'dl_npc_jonathan', 'You mean, there''s a good reason for this spell? Well, if there is, we''ll find out soon enough.', 'Du meinst, es gibt einen guten Grund für diesen Zauber? Wenn es den gibt, werden wir es früh genug herausfinden.', 'Du meinsch, es git en guete Grund für de Zauber? Ja wens de git, denn findemer da scho gnueg früe use.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhyRisky', 'dl_npc_jonathan', 'It''s just because lately, I get the impression that someone tries to mess with my work. Research notes and people disappear, I don''t think you''re an exception.', 'Es ist einfach so dass ich in letzter Zeit immer mehr das Gefühl habe, dass jemand versucht, meine Arbeit zu stören. Forschungsnotizen und Leute verschwinden plötzlich... Ich denke nicht dass du eine Ausnahme warst.', 'Es isch eifach so, dass ich i letzter Ziit immer me es Gfühl ha, dass öppert probiert, mini Arbet z''störe. Forschigsnotize und Lüt verschwinded plötzlich... Ich denk nid dass du e Usnahm gsi bisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhyRisky2', 'dl_npc_jonathan', 'It doesn''t seem to be a good idea for me to go outside until this city has gone back to normal.', 'Rausgehen scheint keine gute Idee für mich zu sein bis diese Stadt wieder normal ist.', 'Usega isch glaub eifach ke gueti Idee für mich bis die Stadt wider chli normaler isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatShouldIDo', 'dl_npc_jonathan', 'You''ll have to learn another type of magic. There are - as far as I know - three mage guilds in Gandria, each focusing on another domain of magic.', 'Du wirst eine neue Magieart lernen müssen. In Gandria gibt es - soweit ich weiss - drei Magiergilden, von denen sich jede auf einen anderen Bereich der Magie spezilisiert.', 'Du wirsch e neui Magieart lerne möse. In Gandria gits - sowiit ich weiss - drü verschideni Magiergilde, vo dene sich jedi ufen andere Bereich vode Magie spezialisiert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatShouldIDo2', 'dl_npc_jonathan', 'I guess you already know one of the guilds: The clerics, which currently rule this city. But there are mages who practice necromancy or twilight magic in this city, you just have to find them.', 'Ich nehme an, du kennst bereits eine der Gilden: Die Kleriker. Es gibt aber auch Magier in dieser Stadt, die Nekromantie oder Dämmerungsmagie praktizieren, du musst sie nur finden.', 'Ich denke, du kensch scho eini vode Gilde: D''Kleriker. Es git aber au Magier i dere Stadt, wo Nekromantie oder Dämmerigsmagie praktiziered, du muesch si nur finde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyGuild', 'dl_npc_jonathan', 'I already know some divine magic. (Summon a light)', 'Ich kann bereits heilige Magie. (Ein Licht beschwören)', 'Ich cha aber scho chli heiligi Magie. (Es Liechtli beschwöre)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereNecromancers', 'dl_npc_jonathan', 'How can I find the necromancers?', 'Wie kann ich die Nekromanten finden?', 'We chan ich d''Nekromante finde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereThieves', 'dl_npc_jonathan', 'Where can I learn some twilight magic?', 'Wo kann ich Dämmerungsmagie lernen?', 'Wo chani Dämmerigsmagie lerne?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereClerics', 'dl_npc_jonathan', 'What if I wanted to join the clerics?', 'Und wenn ich den Klerikern beitreten will?', 'Und wenn ich zude Kleriker wett?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Light', 'dl_npc_jonathan', 'We can''t decipher the map using some simple magic. You''ll have to gain the trust of one of the three guilds in order to learn more advanced magic.', 'Wir können die Karte nicht mit trivialer Magie entschlüsseln. Du musst eine der drei Gilden dazu bringen dir zu vertrauen, damit sie dir mächtigere Magie beibringen.', 'Mir chönd die Charte nid mit trivialer Magie entschlüssle. Du muesch eini vode drei Gildene dezue bringe dir z''vertraue, dass sie dir mächtigeri Magie biibringed.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereNecromancers', 'dl_npc_jonathan', 'They need to stay hidden because their magic is not allowed in Gandria. But if I were you, I''d ask the alchemist Syrah, she seems to know some people.', 'Sie dürfen sich nicht öffentlich zeigen weil ihre Magie in Gandria verboten ist. Wenn ich dich wäre, würde ich die Alchemistin Syrah fragen, sie hat wahrscheinlich Kontakte.', 'Si dörfed sich nid öffentlich zeige well iri Magie in Gandria verbote isch. Wenn ich dich wer würi aber mal d''Alchemistin Syrah fröge, si kennt da so Lüt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereThieves', 'dl_npc_jonathan', 'Mages who practice twilight magic are mostly considered as thieves. I''d start searching in the sewers of Gandria.', 'Magier die sich mit Dämmerungsmagie beschäftigen sind meistens einfach Diebe. Ich würde in der Kanalisation von Gandria mit der Suche beginnen.', 'Magier wo sich mit Dämmerigsmagie beschäftiged sind meistens eifach Dieb. Ich wür ide Kanalisation vo Gandria afange sueche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereClerics', 'dl_npc_jonathan', 'Try to gain the trust of their leader and convince them of your skills.', 'Versuch, das Vertrauen ihres Anführers zu gewinnen und überzeug sie von deinen Fähigkeiten.', 'Probier, es Vertraue vo ihrem Aführer z''becho und zeig ihne wad so druf hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_JoinedClerics', 'dl_npc_jonathan', 'I''m now a member of the Order of the Eternal Light.', 'Ich bin jetzt ein Mitglied des Ordens des Ewigen Lichts.', 'Ich bi ez es Mitglied vom Orde vom Ewige Liecht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_JoinedThieves', 'dl_npc_jonathan', 'I now belong to the Shadow Stalkers.', 'Ich gehöre jetzt zu den Schattenläufern.', 'Ich ghör ez zude Schatteläufer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_JoinedNecromancers', 'dl_npc_jonathan', 'I''m now a member of the necromancers.', 'Ich gehöre jetzt zu den Nekromanten.', 'Ich ghör ez zude Nekromante.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_JoinedClerics', 'dl_npc_jonathan', 'Maybe the most reasonable choice...', 'Wahrscheinlich die vernünftigste Wahl...', 'Wohrschindli di vernünftigst Wahl...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_GoodJobGuild', 'dl_npc_jonathan', 'Well, good job on joining a mage guid. I wasn''t able to decrypt your map with just elemental magic.', 'Gut, dass du eine Magiergilde gefunden hast. Ich konnte deine Karte bis jetzt nicht mit Elementarmagie entschlüsseln.', 'Guet, dass e Magiergilde gfunde hesch. Bis ez hanis nanig gschafft, dini Charte mit bloss Elementarmagie z''entschlüssle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_CombinedSkillsDivine', 'dl_npc_jonathan', 'But combined with your divine magic, it could work.', 'Aber zusammen mit deiner heiligen Magie könnte es funktionieren.', 'Aber zeme mit dinere heilige Magie chönts funktioniere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_JoinedThieves', 'dl_npc_jonathan', 'The underground of Gandria, eh. Just don''t try to steal MY things.', 'Der Untergrund von Gandria, heh. Versuch einfach nicht, meine Sachen zu stehlen.', 'De Untergrund vo Gandria, he. Probier eifach nid, mini Sache z''chlaue.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_CombinedSkillsTwilight', 'dl_npc_jonathan', 'But combined with your twilight magic, it could work.', 'Aber zusammen mit deiner Zwielichtsmagie könnte es funktionieren.', 'Aber zeme mit dinere Dämmerigsmagie chönts funktioniere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_JoinedNecromancers', 'dl_npc_jonathan', 'I didn''t expect you''d go there. An interesting choice, indeed.', 'Ich hätte nicht erwartet, dass du dort hin gehst. Eine interessante Wahl.', 'Ich hätt nid erwartet, dass du det ane gasch. E interessanti Wahl.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_CombinedSkillsNecro', 'dl_npc_jonathan', 'But combined with your necromancy, it could work.', 'Aber zusammen mit deiner Nekromantie könnte es funktionieren.', 'Aber zeme mit dinere Nekromantie chönts funktioniere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_StartElementMaster', 'dl_npc_jonathan', 'I knew they would not make it easy for us. Like they really don''t want to be found.', 'Ich wusste sie würden es uns nicht einfach machen. Als wollten sie nicht gefunden werden.', 'Ich ha gwüsst si würdeds üs nid eifach mache. We wenn si gar nid gfunde werde wend.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_StartElementMaster2', 'dl_npc_jonathan', 'But we need to discover the secret they''re hiding. Fire, Water, Air, Earth... You already know about the first two.', 'Aber wir müssen das Geheimnis lüften, das sie vor uns verstecken. Wasser, Feuer, Luft, Erde... Die ersten zwei kennst du bereits.', 'Aber mir mönd s''Gheimnis lüfte wo si vor üs verstecked. Füür, Wasser, Luft, Erde... Di erste zwei kennsch scho.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_StartElementMaster3', 'dl_npc_jonathan', 'You need to learn how to control air and earth then. Together, we will find them.', 'Du musst also noch lernen, wie man Luft und Erde kontrolliert. Zusammen werden wir sie finden.', 'Du muesch also no lerne, weme Luft und Erde kontrolliert. Zeme werdemer si finde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereSpells', 'dl_npc_jonathan', 'Can you teach me those spells?', 'Kannst du mir diese Zauber beibringen?', 'Chasch du mir die Zauber biibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YouLearn', 'dl_npc_jonathan', 'Why don''t you learn those spells yourself?', 'Wieso lernst du diese Zauber nicht selber?', 'Wieso lernsch du die Zauber nid selber?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BirdSlain', 'dl_npc_jonathan', 'I''ve slain the wind monster.', 'Ich habe das Windmonster getötet.', 'Ich hans Windmonster killt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereSpells', 'dl_npc_jonathan', 'No, I don''t know much about air or earth spells. You need to find them yourself.', 'Nein, ich weiss nicht viel über Luft- oder Erdzauber. Du musst sie selbst finden.', 'Nei, ich weiss nid viel über Luft- oder Erdzauber. Du muesch si selber finde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereSpells2', 'dl_npc_jonathan', 'I''ve heard that the lighthouse keeper Jack has a problem with a wind-monster. That may help you with learning some wind magic.', 'Ich habe gehört dass der Leuchtturmwächter Jack ein Problem mit einem Windmonster hat. Das könnte dir helfen, etwas über das Element zu lernen.', 'Ich ha ghört dass de Lüüchtturmwächter Jack es Problem mitme Windmonster het. Das chönt der helfe, öppis über Windmagie z''lerne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereSpells3', 'dl_npc_jonathan', 'I assume you can find Jack in the brothel...', 'Ich glaube, du kannst Jack im Bordell finden...', 'Ich glaub, du chasch de Jack im Bordell finde...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_YouLearn', 'dl_npc_jonathan', 'My focus are only fire spells. Also, I don''t feel safe going out of my house anymore.', 'Ich konzentriere mich nur auf Feuerzauber. Ausserdem fühle ich mich ausserhalb meines Hauses nicht mehr sicher.', 'Ich konzentrier mich nur uf Füürzauber. Usserdem fühli mi usse a mim Huus nümme sicher.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_YouLearn2', 'dl_npc_jonathan', 'I''m pretty sure I''m being watched - Someone followed me lately on my way home. ', 'Ich bin mir ziemlich sicher dass ich beobachtet werde - Jemand folgte mir kürzlich auf dem Weg nach Hause.', 'Ich bimer zimli sicher dassi beobachted wird - Öpper ischmer letzins ufem Weg hei nagloffe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_YouLearn3', 'dl_npc_jonathan', 'And with all the people disappearing... No, no, I leave this to you.', 'Und mit all den Leuten die verschwinden... Nein, nein, das überlass ich dir.', 'Und mit all dene Lüt wo verschwinded... Nei, nei, das überlahn ich dir.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_BirdSlain', 'dl_npc_jonathan', 'And you''ve managed to absorb its magical powers. Well done.', 'Und du hast seine magische Kraft in dir aufgenommen. Gut gemacht.', 'Und du hesch sini magischi Chraft i dir ufgno. Guet gmacht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_BirdSlain2', 'dl_npc_jonathan', 'Now there''s only one element left - earth.', 'Jetzt bleibt nur noch ein Element übrig - Erde.', 'Ez bliibt nur no eis Element übrig - Erde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_BirdSlain3', 'dl_npc_jonathan', 'The only mage in Gandria who knows some earth magic is a guy called Koray.', 'Der einzige Magier in Gandria der sich mit Erdmagie auskennt ist ein Typ names Koray.', 'De einzig Magier i Gandria wo sich chli mit Erdmagie uskennt isch en Typ namens Koray.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_BirdSlain4', 'dl_npc_jonathan', 'He went to the marshlands in the South of Gandria lately. Maybe he''ll teach you what he knows.', 'Er ging kürzlich in die Marschen im Süden von Gandria. Vielleicht bringt er dir ja sein Wissen bei.', 'Er isch letztins is Sumpfland im Süde vo Gandria gange. Vilicht bringter dir jo bi waner weiss.');
