INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WalkIntoHouse', 'dl_npc_jonathan', 'Hey, what are you doing here? You can''t just walk into other people''s houses!', 'Hey, was machst du hier? Du kannst nicht einfach in die Häuser anderer Leute reinspazieren!', 'He, was tuasch du do? Du kasch nid aifach ind Hüüser vu fremda Lüüt inalaufa!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_DoorWasOpen', 'dl_npc_jonathan', 'But... the door was open.', 'Aber... die Türe war offen.', 'Aber... d''Türe isch offe gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LookingForYou', 'dl_npc_jonathan', 'I was looking for you.', 'Ich habe nach dir gesucht.', 'Ich ha dich gsuecht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NoExcuNdse', 'dl_npc_jonathan', 'Well, that''s not really an excuse. Why are you here?', 'Na, das ist nicht wirklich eine Ausrede. Wieso bist du hier?', 'Jo guat, aber das isch nid würkli a Usred. Wieso bisch du do?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatDoYouWant', 'dl_npc_jonathan', 'What do you want from me then?', 'Was willst du denn von mir?', 'Was wetsch denn vu miar?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Letter', 'dl_npc_jonathan', 'Someone wants me to meet you. (Show the letter and give the map)', 'Jemand will, dass ich dich treffe. (Brief zeigen und Karte geben)', 'Öppert will, dass ich dich triff. (Brief zeige und Chartä ge)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Leave', 'dl_npc_jonathan', 'I was just about to leave.', 'Ich wollte gerade wieder gehen.', 'Ich bi ja scho wider weg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Letter', 'dl_npc_jonathan', '(Reads the letter carefully) Cyrus! So good to hear from him! So, you''re from the academy? How are things going there? And what did Cyrus find out?', '(Liest den Brief sorgfältig) Cyrus! Es ist gut, von ihm zu hören. Du kommst also von der Akademie? Ist dort alles in Ordnung? Und was hat Cyrus herausgefunden?', '(Liist dr Briaf sorgfältig) Cyrus! As isch guat, vu ihm z''ghöra. Du kusch also vu dr Akademie? Was het dr Cyrus denn usagfunda?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Disappoint', 'dl_npc_jonathan', 'I''m sorry to disappoint you...', 'Ich muss dich leider enttäuschen...', 'Ich mu dich leider enttüsche...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_TellsTheStory', 'dl_npc_jonathan', '(Cendric tells the story about losing his memory and getting here)', '(Cendric erzählt die Geschichte wie er sein Gedächtnis verlor und hier gelandet ist)', '(De Cendric verzellt d''Gschicht wie er sis Gedächtnis verlore het und da glandet isch)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Wow', 'dl_npc_jonathan', 'Wow... Losing your memory completely is pretty unusual, even for a mage.', 'Wow... Das ganze Gedächtnis zu verlieren ist ziemlich ungewöhnlich, auch für einen Magier.', 'Wow... Z ganza Gedächtnis z''verlüra isch ziemli ungwöhnlich, au füra Magier.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Wow2', 'dl_npc_jonathan', 'And summoning an iceball is the only spell you remember, right? It''s good you came here. We will solve this together.', 'Und einen Eisball zu beschwören ist der einzige Zauber, an den du dich erinnerst, ja? Es ist gut, dass du zu mir gekommen bist. Wir werden das zusammen lösen.', 'Und a Iisball z''beschwöra isch dr einzig Zauber wo no kasch, gäll. As isch guat, dass zu miar kho bisch. Miar wärdend das zäma uflösa.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Academy', 'dl_npc_jonathan', 'What academy are you talking about?', 'Von was für einer Akademie sprichst du?', 'Vo wa fürere Akademie redsch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Elementalists', 'dl_npc_jonathan', 'What are elementalists?', 'Was sind Elementarmagier?', 'Was sind Elementarmagier?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Affinity', 'dl_npc_jonathan', 'What does "affinity" mean?', 'Was bedeutet "Affinität"?', 'Was heisst "Affinität"?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatShouldWeDo', 'dl_npc_jonathan', 'And what should we do now?', 'Und was sollen wir jetzt tun?', 'Und wa sölemer jetz mache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ProveMyself', 'dl_npc_jonathan', 'How can I prove myself?', 'Wie kann ich mich beweisen?', 'We chan ich mich bewiise?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Academy', 'dl_npc_jonathan', 'The only academy in Admantris where elementalists are educated. It is situated on the other side of the sea. You have obviously studied there.', 'Die einzige Akademie in Admantris, die Elementarmagier ausbildet. Sie befindet sich auf der anderen Seite des Meeres. Du hast offensichtlich dort studiert.', 'Di einzig Akademie in Admantris wo Elementarmagier usbildet. Si liit uf dr andera Siita vom Meer. Du hesch offasichtlich det studiart.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Elementalists', 'dl_npc_jonathan', 'Mages with an affinity to elemental magic. There are not too many left in Admantris, but I''m one of them... and you are - probably - too.', 'Magier mit einer Affinität zur Elementarmagie. Es gibt nicht mehr viele davon in Admantris aber ich bin einer von ihnen... und du - wahrscheinlich - auch.', 'Magier mitara Affinität zur Elementarmagie. As git nüm so viel drvo in Admantris, aber i bin eina... und du - wohrschiinli - au.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Affinity', 'dl_npc_jonathan', 'Each mage is born with a certain affinity to a type of magic. This affinity determines which spells they can learn.', 'Jeder Magier wird mit einer gewissen Affinität zu einem Magie-Typ geboren. Dieser bestimmt, welche Zauber er erlernen kann.', 'Jeda Magier wird mitera gwüssa Affinität zuma Magietyp gebora. Dä bestimmt, welli Zauber mr lerna kann.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Affinity2', 'dl_npc_jonathan', 'Us elementalists are lucky - we are able to understand the whole spectrum of magic.', 'Wir Elementarmagier haben Glück - wir können das gesamte Spektrum der Magie verstehen.', 'Miar Elementarmagier hend Glück - miar könnd z ganza Spektrum vur Magie verstoh.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NextStep', 'dl_npc_jonathan', 'Hm. I knew that Cyrus was going to send me one of his students to support my research here. But to be honest, I hoped for someone a bit more...', 'Hm. Ich wusste, dass mir Cyrus einen seiner Studenten zur Unterstützung meiner Forschungen schicken würde. Aber um ehrlich zu sein, habe ich mit jemandem gerechnet, der etwas mehr...', 'Hm. I han schu gwüsst, dass dr Cyrus miar eina vu sina Studenta schicka wird. Aber um ehrlich zsii, i han mit öperem grächnet wo kli meh... ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NextStep2', 'dl_npc_jonathan', '...qualified. Also, someone who lost his memory is suspicious.', '...qualifiziert ist. Ausserdem ist jemand, der sein Gedächtnis verloren hat, schon ein bisschen verdächtig.', '...qualifiziert isch. Usserdem isch eina, wo sis Gedächtnis verlora het, schu huara verdächtig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_NextStep3', 'dl_npc_jonathan', 'Before I let you in on my studies, you should first prove that you''re a worthy elementalist.', 'Bevor ich dich in meine Forschungen einweihe, musst du beweisen, dass du ein würdiger Elementarmagier bist.', 'Bevor i di in mini Forschiga iweiha, muasch zerscht bewiisa, dass du a würdiga Elementarmagier bisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_FireballQuest', 'dl_npc_jonathan', 'Every mage is able to learn how to summon an iceball, it''s an easy spell. Controlling fire, on the other hand, is considered nearly impossible for mages without an elemental affinity.', 'Jeder Magier kann lernen, wie man einen Eisball beschwört. Feuer zu kontrollieren ist für Magier ohne eine Affinität zur Elementarmagie aber fast unmöglich.', 'Jeda Magier kann lerna, wia ma a Iisball beschwört. Füür z''kontrolliera isch aber für a Magier ohni Affinität zur Elementarmagie fascht unmöglich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_FireballQuest2', 'dl_npc_jonathan', 'Some fire rats from the sewers recently invaded the laboratory in my basement.', 'Ein paar Feuerratten aus der Kanalisation sind in mein Labor in meinem Keller eingedrungen.', 'As paar Füürratta us dr Kanalisation sind in miis Labor i mim Käller iidrunga.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_FireballQuest3', 'dl_npc_jonathan', 'Find a way to lure them out and collect the essence of fire from their lungs.', 'Finde heraus, wie du sie anlocken kannst und bringe mir die Essenzen des Feuers aus ihren Lungen.', 'Find usa, wia si alocka kasch und bring miar d''Füüressenza us ihrna Lungana.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_FireballQuest4', 'dl_npc_jonathan', 'Come back to me when you''re done and I''ll see if you''re able to learn how to summon a fireball.', 'Wenn du zurück bist, werde ich sehen, ob ich dir beibringen kann, wie man einen Feuerball beschwört.', 'Denn wirdi mol luaga, ob i diar biibringa kann, wia ma a Füürball beschwört.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotFireEssence', 'dl_npc_jonathan', 'I got the essences of fire for you.', 'Ich habe die Essenzen des Feuers für dich.', 'Ich han d''Füüressenze für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IsMyAuraOk', 'dl_npc_jonathan', 'My curse was weakened. Is it better now?', 'Mein Fluch wurde geschwächt. Ist jetzt besser?', 'Min Fluech isch gschwächt worde. Isch ez besser?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LearntFireball', 'dl_npc_jonathan', 'I''ve read the scroll.', 'Ich habe die Schriftrolle gelesen.', 'Ich han d''Schriftrolle gläse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CU', 'dl_npc_jonathan', 'See you later.', 'Bis später.', 'Bis spöter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_EssenceComplete', 'dl_npc_jonathan', 'Very good. Maybe you can still become a decent elementalist.', 'Sehr gut. Vielleicht können wir doch noch einen anständigen Elementarmagier aus dir machen.', 'Huara guat. Villicht kömmer jo doch no a aständiga Elementarmagier us diar macha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_EssenceComplete2', 'dl_npc_jonathan', 'Here, take this scroll and read it. Talk to me when you''ve learned the spell.', 'Hier, nimm diese Schriftrolle und lies sie. Sprich wieder mit mir wenn du den Zauber erlernt hast.', 'Do, nimm dia Schriftrolla und lies si. Red wieder mit miar, wenn dr Zauber glernt hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_RemoveMark', 'dl_npc_jonathan', 'But, what''s this? A strange magical aura is surrounding you.', 'Aber, was ist das? Du hast wirklich eine seltsame magische Aura.', 'Aber, wa isch da? Du hesch würkli e komischi magischi Aura.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_RemoveMark2', 'dl_npc_jonathan', 'Feels like an evil curse... I won''t teach you how to summon a fireball until you got a healthy elemental aura again.', 'Fühlt sich an wie ein böser Fluch... Ich werde dir nicht beibringen, einen Feuerball zu beschwören, bis du wieder eine gesunde elementare Aura hast.', 'Fühlt sich a we en bösa Fluach... Ich wird der nid bibringa, weme en Füürball beschwört, bevor du nid wiedar e gsundi, elementari Aura hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_IsMyAuraOk', 'dl_npc_jonathan', 'Yes. Much better.', 'Ja. Viel besser.', 'Jo. Ez isch guat.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball', 'dl_npc_jonathan', 'And you''ve already acquired its knowledge. I think we''re ready to get started then.', 'Und du hast ihr Wissen bereits erlangt. Ich denke, wir können nun anfangen.', 'Und du heschs au schu checkt. I denk, miar könnd denn grad afanga.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball2', 'dl_npc_jonathan', 'My research focuses on the old, forgotten knowledge of the first elementalists.', 'Ich forsche an dem alten, vergessenen Wissen der ersten Elementarmagier.', 'I forscha am alta, vergessena Wüssa vu de ersta Elementarmagier.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball3', 'dl_npc_jonathan', 'I already know that they must have worked on spells that are way more powerful than any of the elemental magic that is known today. ', 'Ich habe bereits herausgefunden, dass sie früher Zauber kannten, die weit mächtiger sind, als alle Elementarmagie, die wir heute kennen.', 'I han schu usagfunda, dass sie früaner viel mächtigeri Zauber kennt hend, als jeglichi Elementarmagie, wo mr hüt so brucht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball4', 'dl_npc_jonathan', 'I''ve found evidence that there''s an abandoned site of the first Elementalists somewhere near Gandria, that''s why I''m working here.', 'Ich habe Beweise gefunden, dass es irgendwo in der Nähe der Stadt Gandria eine verlassene Stätte der ersten Elementarmagier gibt, deshalb arbeite ich hier.', 'I han Bewiis gfunda, dasses irgendwo in dr Nöchi vu Gandria a verlassna Ort git, wo di erschta Elementarmagier glebt hend, drum schaff i au do.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball5', 'dl_npc_jonathan', 'I''m pretty sure that the map that Cyrus sent me is the key to find that location and solve the mysteries that lie there. But there''s one small problem with that map...', 'Ich bin mir ziemlich sicher, dass diese Karte, die Cyrus mir geschickt hat, der Schlüssel ist, um diesen Ort zu finden. Allerdings gibt es da ein kleines Problem mit dieser Karte...', 'I bi mr ziemli sicher dass dia Karta, wo dr Cyrus miar gschickt het, dr Schlüssel isch zum de Ort z''finda. As git do aber a klises Problem mit dera Karta...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_LearnedFireball6', 'dl_npc_jonathan', 'To decipher it, elemental magic is not sufficient. But using elemental magic combined with the power of another type of magic, we should be able to break the protection spell.', 'Elementarmagie alleine reicht nicht, um sie zu entschlüsseln. Kombiniert mit einem anderen Typ der Magie müsste man den Schutzzauber aber brechen können.', 'Elementarmagie allai langet nid zum si entschlüssla. Zema mitera andera Magieart sött mrs aber anabringa, dr Schutzzauber z''brecha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AnotherMage', 'dl_npc_jonathan', 'So, should we ask another mage to help us?', 'Sollten wir also einen weiteren Magier um Hilfe bitten?', 'Söttemer also en andere Magier um Hilf bitte?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatAboutYou', 'dl_npc_jonathan', 'What about you? Don''t you know some other spells?', 'Was ist mit dir? Kennst du keine anderen Zauber?', 'Was isch mit dir? Kennsch du ke anderi Zauber?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BreakingProtection', 'dl_npc_jonathan', 'Do you think breaking a protection spell is a good idea?', 'Denkst du es ist eine gute Idee, einen Schutzzauber zu brechen?', 'Denksch denn es seg e gueti Idee, en Schutzzauber z''breche?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyRisky', 'dl_npc_jonathan', 'Why is that risky for you?', 'Wieso ist das riskant für dich?', 'Weso isch da riskant für dich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatShouldIDo', 'dl_npc_jonathan', 'I see. So, what should I do?', 'Alles klar. Was muss ich also tun?', 'Alles klar. Wa mueni also mache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_AnotherMage', 'dl_npc_jonathan', 'To be honest, I don''t really trust any other mage in the city. The less people know about this map, the better.', 'Um ehrlich zu sein traue ich keinem anderen Magier in der Stadt. Je weniger Leute von dieser Karte wissen, desto besser.', 'Um ehrlich zsii trau i keinem andara Magier in dera Stadt. Je weniger Lüüt vu dera Karta wüssend, desto besser.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatAboutYou', 'dl_npc_jonathan', 'Although we elementalists can learn every type of magic, I''ve always focused my studies on elemental magic only. ', 'Obwohl wir Elementarmagier jede Magie-Art erlernen können, habe ich mich bisher nur mit Elementarmagie beschäftigt.', 'Obwohl miar Elementarmagier jedi Magieart lerna könnd, hani mi bis ez nur mit Elementarmagie beschäftigt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatAboutYou2', 'dl_npc_jonathan', 'And now, it''s too risky for me to go and learn something new. No, I need someone with a face that is not yet known to the citizens of Gandria. Someone like you.', 'Und jetzt ist es zu riskant für mich, etwas Neues zu lernen. Nein, ich brauche jemanden, dessen Gesicht hier in Gandria nicht so bekannt ist. Jemanden wie dich.', 'Und ez ischs z''riskant für mi, was neus z''lerna. Nai, i bruch öper mitma unbekanntera Gsicht do in Gandria. So öper wia di.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_BreakingProtection', 'dl_npc_jonathan', 'You mean, is there a good reason for this spell? Well, if there is, we''ll find out soon enough.', 'Du meinst, es gibt einen guten Grund für diesen Zauber? Wenn es den gibt, werden wir es früh genug herausfinden.', 'Du mainsch, as git a guata Grund für de Zauber? Jo wenns de git, denn findemer das schu gnuag früah usa.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhyRisky', 'dl_npc_jonathan', 'It''s just because lately, I get the impression that someone tries to mess with my work.', 'Es ist einfach so, dass ich in letzter Zeit immer mehr das Gefühl habe, dass jemand versucht, meine Arbeit zu stören.', 'As isch eifach so, dass i in letschter Ziit immer meh z''Gfühl han, dass aswer probiart, mini Arbet z''störa.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhyRisky2', 'dl_npc_jonathan', 'Research notes and people disappear, I don''t think you''re an exception.', 'Forschungsnotizen und Leute verschwinden plötzlich... Ich denke nicht, dass du eine Ausnahme warst.', 'Forschigsnotiza und Lüüt verschwindend ufs Mol... I denk nid, dass du a Usnahm gsi bisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhyRisky3', 'dl_npc_jonathan', 'It doesn''t seem to be a good idea for me to go outside until this city has gone back to normal.', 'Rausgehen scheint keine gute Idee für mich zu sein, bis diese Stadt wieder normal ist.', 'Usa goh isch glaub eifach kai guati Idee für mi, bis dia Stadt wieder kli normaler isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatShouldIDo', 'dl_npc_jonathan', 'You''ll have to learn another type of magic. There are - as far as I know - three mage guilds in Gandria, each focusing on another domain of magic.', 'Du wirst eine neue Magie-Art lernen müssen. In Gandria gibt es - soweit ich weiss - drei Magiergilden, von denen sich jede auf einen anderen Bereich der Magie spezialisiert.', 'Du wirsch a neui Magieart lerna müassa. In Gandria gits - sowiit i weiss - drei verschideni Magiergildana, vo dena sich jedi uf a andera Bereich vur Magie spezialisiart.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhatShouldIDo2', 'dl_npc_jonathan', 'I guess you already know one of the guilds: The clerics, who currently rule this city. But there are mages who practice necromancy or twilight magic in this city, you just have to find them.', 'Ich nehme an, du kennst bereits eine der Gilden: Die Kleriker. Es gibt aber auch Magier in dieser Stadt, die Nekromantie oder Dämmerungsmagie praktizieren, du musst sie nur finden.', 'I denka, du kennsch schu eini vo dena Gildana: D''Kleriker. As git aber au Magier in dera Stadt, wo Nekromantie oder Dämmerigsmagie praktiziarend, du muasch si nur finda.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyGuild', 'dl_npc_jonathan', 'I already know some divine magic. (Summon a light)', 'Ich kann bereits heilige Magie. (Ein Licht beschwören)', 'Ich cha aber scho chli heiligi Magie. (Es Liechtli beschwöre)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereNecromancers', 'dl_npc_jonathan', 'How can I find the necromancers?', 'Wie kann ich die Nekromanten finden?', 'We chan ich d''Nekromante finde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereThieves', 'dl_npc_jonathan', 'Where can I learn some twilight magic?', 'Wo kann ich Dämmerungsmagie lernen?', 'Wo chani Dämmerigsmagie lerne?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereClerics', 'dl_npc_jonathan', 'What if I wanted to join the clerics?', 'Und wenn ich den Klerikern beitreten will?', 'Und wenn ich zude Kleriker wett?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_Light', 'dl_npc_jonathan', 'We can''t decipher the map using some simple magic. You''ll have to gain the trust of one of the three guilds in order to learn more advanced magic.', 'Wir können die Karte nicht mit trivialer Magie entschlüsseln. Du musst eine der drei Gilden dazu bringen dir zu vertrauen, damit sie dir mächtigere Magie beibringen.', 'Miar könnd dia Karta nid mit trivialer Magie entschlüssla. Du muasch eini vo dena drei Gildana drzua bringa diar z''vertraua, damit si diar mächtigeri Magie biibringend.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereNecromancers', 'dl_npc_jonathan', 'They need to stay hidden because their magic is not allowed in Gandria. But if I were you, I''d ask the alchemist Syrah, she seems to know some people.', 'Sie dürfen sich nicht öffentlich zeigen, weil ihre Magie in Gandria verboten ist. Wenn ich du wäre, würde ich die Alchemistin Syrah fragen, sie hat wahrscheinlich Kontakte.', 'Si dörfend sich nid öffentlich zeiga, will ihri Magie in Gandria vrbota isch. Wenn i di wär, würi aber mol d''Alchemistin Syrah froga, si kennt da so Lüüt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereThieves', 'dl_npc_jonathan', 'Mages who practice twilight magic are mostly considered thieves. I''d start searching in the sewers of Gandria.', 'Magier, die sich mit Dämmerungsmagie beschäftigen, sind meistens einfach Diebe. Ich würde in der Kanalisation von Gandria mit der Suche beginnen.', 'Magier, wo sich mit Dämmerigsmagie beschäftigend, sind meistens eifach Dieba. I wür in dr Kanalisation vu Gandria afanga suacha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereClerics', 'dl_npc_jonathan', 'Try to gain the trust of their leader and convince them of your skills.', 'Versuch, das Vertrauen ihres Anführers zu gewinnen und überzeug sie von deinen Fähigkeiten.', 'Vrsuach, z''Vertraua vo ihrem Afüahrer z''kriaga und zeig ihna, was du so druff hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_JoinedClerics', 'dl_npc_jonathan', 'I''m now a member of the Order of the Eternal Light.', 'Ich bin jetzt ein Mitglied des Ordens des Ewigen Lichts.', 'Ich bi ez es Mitglied vom Orde vom Ewige Liecht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_JoinedThieves', 'dl_npc_jonathan', 'I now belong to the Shadow Stalkers.', 'Ich gehöre jetzt zu den Schattenläufern.', 'Ich ghör ez zude Schatteläufer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_JoinedNecromancers', 'dl_npc_jonathan', 'I''m now a member of the necromancers.', 'Ich gehöre jetzt zu den Nekromanten.', 'Ich ghör ez zude Nekromante.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_JoinedClerics', 'dl_npc_jonathan', 'Maybe the most reasonable choice...', 'Wahrscheinlich die vernünftigste Wahl...', 'Wohrschiinli di vernünftigscht Wahl...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_GoodJobGuild', 'dl_npc_jonathan', 'Well, good job on joining a mage guild. I wasn''t able to decrypt your map with just elemental magic.', 'Gut, dass du eine Magiergilde gefunden hast. Ich konnte deine Karte bis jetzt nicht mit Elementarmagie entschlüsseln.', 'Guat, dass a Magiergilda gfunda hesch. Bis ez hanis nonid gschafft, dini Karta nur mit Elementarmagie z''entschlüssla.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_CombinedSkillsDivine', 'dl_npc_jonathan', 'But combined with your divine magic, it could work.', 'Aber zusammen mit deiner heiligen Magie könnte es funktionieren.', 'Aber zema mit dinera heiliga Magie könnts funktioniera.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_JoinedThieves', 'dl_npc_jonathan', 'The underground of Gandria, eh. Just don''t try to steal MY things.', 'Der Untergrund von Gandria, heh. Versuch einfach nicht, meine Sachen zu stehlen.', 'Dr Untergrund vu Gandria, he. Probiar eifach nid, mini Sacha z''klaua.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_CombinedSkillsTwilight', 'dl_npc_jonathan', 'But combined with your twilight magic, it could work.', 'Aber zusammen mit deiner Zwielichtsmagie könnte es funktionieren.', 'Aber zema mit dinera Dämmerigsmagie könnts funktioniera.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_JoinedNecromancers', 'dl_npc_jonathan', 'I didn''t expect you''d go there. An interesting choice, indeed.', 'Ich hätte nicht erwartet, dass du dort hin gehst. Eine interessante Wahl.', 'I hätt nid erwartet, dass du det ana gohsch. A interessanti Wahl.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_CombinedSkillsNecro', 'dl_npc_jonathan', 'But combined with your necromancy, it could work.', 'Aber zusammen mit deiner Nekromantie könnte es funktionieren.', 'Aber zema mit dinera Nekromantie könnts funktioniera.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_StartElementMaster', 'dl_npc_jonathan', 'I knew they would not make it easy for us. It''s like they really don''t want to be found...', 'Ich wusste, sie würden es uns nicht einfach machen. Als wollten sie nicht gefunden werden...', 'I han schu gwüsst, si werdens üs nid eifach macha. Wia wenn si gar nid gfunda werda wend...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_StartElementMaster2', 'dl_npc_jonathan', 'But we need to discover the secret they''re hiding. Fire, Water, Air, Earth... You already know about the first two.', 'Aber wir müssen das Geheimnis lüften, das sie vor uns verstecken. Wasser, Feuer, Luft, Erde... Die ersten zwei kennst du bereits.', 'Aber miar müand z''Gheimnis lüfta wo si vor üs versteckend. Füür, Wasser, Luft, Erda... Di ersta zwei kennsch schu.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_StartElementMaster3', 'dl_npc_jonathan', 'You need to learn how to control air and earth then. Together, we will find them.', 'Du musst also noch lernen, wie man Luft und Erde kontrolliert. Zusammen werden wir sie finden.', 'Du muasch also no lerna, wia ma Luft und Erda kontrolliart. Zema werdemr si finda.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereSpells', 'dl_npc_jonathan', 'Can you teach me those spells?', 'Kannst du mir diese Zauber beibringen?', 'Chasch du mir die Zauber biibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YouLearn', 'dl_npc_jonathan', 'Why don''t you learn those spells yourself?', 'Wieso lernst du diese Zauber nicht selber?', 'Wieso lernsch du die Zauber nid selber?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BirdSlain', 'dl_npc_jonathan', 'I''ve slain the wind monster.', 'Ich habe das Windmonster getötet.', 'Ich hans Windmonster killt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereSpells', 'dl_npc_jonathan', 'No, I don''t know much about air or earth spells. You need to find them yourself.', 'Nein, ich weiss nicht viel über Luft- oder Erdzauber. Du musst sie selbst finden.', 'Na-a, i weiss nid viel über Luft- oder Erdzauber. Du muasch si allai finda.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereSpells2', 'dl_npc_jonathan', 'I''ve heard that the lighthouse keeper Jack has a problem with a wind-monster. That may help you with learning some wind magic.', 'Ich habe gehört, dass der Leuchtturmwächter Jack ein Problem mit einem Windmonster hat. Das könnte dir helfen, etwas über das Element zu lernen.', 'I han mitkriagt dass dr Lüüchtturmwächter Jack as Problem mitma Windmonster het. Das könnti dr helfa, öpis über Windmagie z''lerna.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_WhereSpells3', 'dl_npc_jonathan', 'I assume you can find Jack in the brothel...', 'Ich glaube, du kannst Jack im Bordell finden...', 'I denka, du kasch dr Jack im Bordell finda...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_YouLearn', 'dl_npc_jonathan', 'My focus are only fire spells. Also, I don''t feel safe going out of my house anymore.', 'Ich konzentriere mich nur auf Feuerzauber. Ausserdem fühle ich mich ausserhalb meines Hauses nicht mehr sicher.', 'I konzentriar mi nur uf Füürzauber. Usserdem fühl i mi usserhalb vu mim Huus nüma sicher.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_YouLearn2', 'dl_npc_jonathan', 'I''m pretty sure I''m being watched - Recently, someone followed me home. ', 'Ich bin mir ziemlich sicher, dass ich beobachtet werde - Jemand folgte mir kürzlich auf dem Weg nach Hause.', 'I bin mr huara sicher, dass ma mi beobachtet - As isch mr letschtens ufem Heiweg öper noh gloffa.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_YouLearn3', 'dl_npc_jonathan', 'And with all the people disappearing... No, no, I leave this to you.', 'Und mit all den Leuten, die verschwinden... Nein, nein, das überlass ich dir.', 'Und mit all dena Lüüt wo verschwindend... Nai, na-a, das überlohn i diar.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_BirdSlain', 'dl_npc_jonathan', 'And you''ve managed to absorb its magical powers. Well done.', 'Und du hast seine magische Kraft in dir aufgenommen. Gut gemacht.', 'Und du hesch sini magischi Kraft in diar ufgnoh. Guat gmacht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_BirdSlain2', 'dl_npc_jonathan', 'Now there''s only one element left - earth.', 'Jetzt bleibt nur noch ein Element übrig - Erde.', 'Ez bliibt nur no eis Element übrig - Erda.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_BirdSlain3', 'dl_npc_jonathan', 'The only mage in Gandria who knows some earth magic is a guy called Koray.', 'Der einzige Magier in Gandria, der sich mit Erdmagie auskennt, ist ein Typ namens Koray.', 'Dr einzig Magier in Gandria, wo sich a kli mit Erdmagie uskennt, isch a Typ namens Koray.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Jonathan_BirdSlain4', 'dl_npc_jonathan', 'He went to the marshlands in the South of Gandria recently. Maybe he''ll teach you what he knows.', 'Er ging kürzlich in die Marschen im Süden von Gandria. Vielleicht bringt er dir ja sein Wissen bei.', 'Er isch letschtens ins Sumpfland im Süda vu Gandria ganga. Villicht bringtr diar jo bi was er weiss.');
