INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Karma_ExplainKamasutra3', 'dl_npc_karma', 'The book also covers the theory of love and describes what triggers desire and how to improve one''s own physical attraction.', 'Das Buch beschreibt auch die Theorie der Liebe und erklärt, was Verlangen hervorruft und wie man die eigene sexuelle Attraktivität steigern kann.', 'Es Buech beschriibt au d''Theorie vode Liebi und erchkärt, we Verlange entstoht und weme di eiget sexuell Attraktivität steigere cha.');
