INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatDoing', 'dl_npc_koray', 'What are you doing here?', 'Was machst du hier?', 'Was machsch du da?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Nuray', 'dl_npc_koray', 'Nuray is looking for you.', 'Nuray sucht nach dir.', 'D''Nuray suecht dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Earth', 'dl_npc_koray', 'Do you know something about earth magic?', 'Kennst du dich mit Erdmagie aus?', 'Kennsch dich mit Erdmagie us?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Business', 'dl_npc_koray', 'Waiting for someone like me?', 'Du wartest auf jemanden wie ich?', 'Du wartisch uf öpper we mich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_InterestedAgain', 'dl_npc_koray', 'Tell me more about why you need an escort.', 'Erzähl mir mehr davon, wieso du Geleitschutz brauchst.', 'Verzell mer me devo weso du Geleitschutz bruchsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Telekinesis', 'dl_npc_koray', 'What''s this "telekinesis"?', 'Was ist diese "Telekinese?"', 'Was isch die "Telekinese?"');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_StartQuest', 'dl_npc_koray', 'I''ll escort you if you teach me your magic.', 'Ich werde dir Geleitschutz geben wenn du mir deine Magie beibringst.', 'Ich wird der Geleitschutz ge wenn du mir dini Magie bibringsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_StoleEye', 'dl_npc_koray', 'You stole that amulet, didn''t you?', 'Du hast dieses Amulett gestohlen, oder?', 'Du hesch das Amulett klaut, gell?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_WhatDoing', 'dl_npc_koray', 'I''m waiting... for someone like you maybe. ', 'Ich warte... vielleicht auf jemanden wie dich.', 'Ich wartä... vilicht uf öppo wie dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_WhatDoing2', 'dl_npc_koray', 'That paladin on the other side of the bridge was quite a weirdo.', 'Dieser Paladin auf der anderen Seite der Brücke war ein ziemlicher Spinner.', 'De Paladin uf de anderä Siite vode Brugg isch zimli dureknallt gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_WhatDoing3', 'dl_npc_koray', 'But you... you may be the one.', 'Aber du... du könntest es sein.', 'Aber du... du chöntsches sii.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_Nuray', 'dl_npc_koray', 'Oh, she doesn''t have to worry. Everything''s fine here. ', 'Oh, sie muss sich keine Sorgen machen. Es ist alles gut hier.', 'Oh, si muän sich kei Sorgä mache. Do isch alles suppo.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_Nuray2', 'dl_npc_koray', 'I just got unfinished business here. After that, I''ll return to Gandria.', 'Ich muss hier nur noch etwas erledigen. Danach werde ich nach Gandria zurückkehren.', 'Ich muän do nur no was erledigä. Nochär würi noch Gandria zrugg goh.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_Earth', 'dl_npc_koray', 'Maybe... (smiles)', 'Vielleicht... (lächelt)', 'Vilicht... (lächlet)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_Business', 'dl_npc_koray', 'Yes, you look like someone who knows how to handle a weapon.', 'Ja, du siehst aus wie jemand, der weiss, wie man mit einer Waffe umgeht.', 'Jo, du gsesch us wie öpper wo weiss, wieme mitärä Waffe umgoht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_Business2', 'dl_npc_koray', 'I need an escort. Are you interested?', 'Ich brauche Geleitschutz. Interessiert?', 'Ich bruch Geleitschutz. Interessiert?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Interested', 'dl_npc_koray', 'Tell me more about it.', 'Erzähl mir mehr davon.', 'Verzell mer me devo.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotInterested', 'dl_npc_koray', 'I don''t have time for this at the moment.', 'Ich habe momentan keine Zeit.', 'Ich han momentan ke Ziit.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_Interested', 'dl_npc_koray', 'Well, I lost a very precious necklace, the "Eye of the Moon". ', 'Nun, ich habe eine sehr wertvolle Halskette verloren, das "Auge des Mondes".', 'Weisch, ich ha e sehr wertvolli Halschettä verlore, es "Mondaug".');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_Interested2', 'dl_npc_koray', 'I had to run away with it and jumped down from a small hill into the forest.', 'Ich musste damit flüchten und bin von einem kleinen Hügel heruntergesprungen.', 'Ich ha möse demit flüchte und bi vomänä chliine Hügel abegumpät.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_Interested3', 'dl_npc_koray', 'Unfortunately, it got caught in the branches of a tree and I can''t get near it alone.', 'Unglücklicherweise hat es sich in den Ästen eines Baums verfangen und ich komm alleine nicht genügend nah ran.', 'Dummerwiis häts sich ide Äst vomnä Baum verfange und ich chum elei nöd gnueg nöch ane.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_Interested4', 'dl_npc_koray', 'But if you could get me near that tree, I could get it using some telekinesis.', 'Aber wenn du mich in die Nähe dieses Baums bringen könntest, könnte ich es mit Telekinese runterholen.', 'Aber wenn du mich id Nöchi vo dem Baum bringe chöntsch, chöntis mit Telekinese abehole.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_Telekinesis', 'dl_npc_koray', 'That''s a type of earth magic, using the earth''s magnetism to move objects. Quite handy if you ask me.', 'Das ist eine Art Erdmagie. Man benutzt den Erdmagnetismus, um entfernte Objekte zu bewegen. Ist ziemlich nützlich, glaub mir.', 'Das isch e Art Erdmagie. Me brucht es Magnetfeld vode Erde zum Sache wo wiit weg sind z''bewege. Isch zimli nützlich, weisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_Telekinesis2', 'dl_npc_koray', 'Actually, I''m affine to twilight magic, but this spell kind of complements my skills.', 'Eigentlich bin ich zur Dämmerungsmagie affin, aber dieser Zauber ergänzt meine Fähigkeiten einfach gut.', 'Eigentlich bini zude Dämmerigsmagie affin, aber de Zaubor ergänzt mini Fähigkeite recht guet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_StartQuest', 'dl_npc_koray', 'Alright. Follow me.', 'Gut. Folge mir.', 'Guet. Chum mit.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_StoleEye', 'dl_npc_koray', '"Stolen" is such a harsh word. I''d rather say I found it. (Grins)', '"Gestohlen" ist so ein hartes Wort. Ich würde eher sagen, ich habs gefunden. (Grinst)', '"Gstohle" isch so es herts Wort. Ich wür ehno sege, ich has gfunde. (Grinst)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Koray_GoIn', 'dl_npc_koray', 'Let''s go.', 'Los geht''s.', 'Gömmo.');
