INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatDoing', 'dl_npc_lee', 'What are you doing here?', 'Was machst du da?', 'Was machsch du da?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lee_WhatDoing', 'dl_npc_lee', 'Yeah that''s what I ask myself too. I''d rather be in the middle of the ocean.', 'Ja das frage ich mich auch. Ich wäre lieber mitten auf dem Meer.', 'Jo das frogi mi au. I wär liaber ufam Meer gra.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lee_WhatDoing2', 'dl_npc_lee', 'But thanks to that fool Luv, we missed our ship and are stuck here.', 'Aber dank diesem Trottel Luv haben wir unser Schiff verpasst und sitzen jetzt hier fest.', 'Aber dank däm Trottel Luv hend miar üses Schiff verpasst und sitzad jeza do fest.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FirstRant', 'dl_npc_lee', 'Your mate told me you''re the one to blame.', 'Dein Kumpel hat gesagt, du seist schuld an eurem Schlamassel.', 'Din Kolleg het gseit, du segsch schuld a eurem Schlamassel.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_SecondRant', 'dl_npc_lee', 'Your mate says you shouldn''t have gotten drunk.', 'Dein Kumpel sagt du hättest dich nicht betrinken sollen.', 'Din Kolleg seit du hetsch dich nid betrinke söle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lee_OtherSays', 'dl_npc_lee', 'Oh yeah, did he? Well you can tell him that we would have made it if he wasn''t drunk when our ship cast off.', 'Ach ja, hat er das? Du kannst ihm sagen er hätte sich nicht betrinken sollen als unser Schiff abgelegt hat.', 'Ach jo, häter das? Du chaschäm sega er het sich halt nid betrinka söla wo üses Schiff abglait het.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lee_SecondRant', 'dl_npc_lee', 'Pha! If he hadn''t been sleeping when our captain called our sailors back, we wouldn''t have missed it either!', 'Pha! Hätte er nicht geschlafen, als unser Kapitän die Matrosen zurück aufs Schiff gerufen hat, hätten wir es auch nicht verpasst!', 'Pha! Wener ni pennt het, wo üsa Kapitän d''Matrosa zrugg ufs Schiff gruefa hät, hetemärs au ni verpasst!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lee_Thanks', 'dl_npc_lee', 'Thanks!', 'Danke!', 'Danka!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CalmDown', 'dl_npc_lee', 'I think you both should calm down.', 'Ich denke ihr beide solltet euch beruhigen.', 'Ich denk ihr beidi söted eu beruhige.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HelpYou', 'dl_npc_lee', 'Maybe I could help you.', 'Vielleicht kann ich euch helfen.', 'Vilicht chani eu helfe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_QuestComplete', 'dl_npc_lee', 'I got a compass for you.', 'Ich habe einen Kompass für euch.', 'Ich hanen Kompass für eu.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lee_CalmDown', 'dl_npc_lee', 'Yes, maybe you''re right. This doesn''t get us anywhere.', 'Ja, vielleicht hast du recht. Das bringt uns auch nicht weiter.', 'Jo, velicht hesch rächt. Da bringt üs au ni witer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lee_CalmDown2', 'dl_npc_lee', 'We know where our captain wanted to sail and we could buy a small boat here.', 'Wir wissen, wo unser Kapitän hinwollte und wir könnten hier auch ein kleines Boot kaufen.', 'Miar wüssad, wo üsa Kapitän heräwella het und miar chönted au as chlises Boot chaufe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lee_CalmDown3', 'dl_npc_lee', 'But to sail over the sea, we''d need a compass and nobody in this damn city has one.', 'Doch um über das Meer zu segeln bräuchten wir einen Kompass und niemand in dieser verfluchten Stadt hat einen.', 'Aber zum übers Meer sägla brüchtemer a Kompass und niamert i dera verfluachte Stadt het eina.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lee_HelpYou', 'dl_npc_lee', 'Yes, maybe. If you could get us a working compass, we''d give you all we have left.', 'Ja, vielleicht. Wenn du uns einen funktionierenden Kompass besorgen kannst, werden wir dir alles geben, was wir noch haben.', 'Jo, velicht. Wenn du üs a funktionierenda Kompass bringsch, gänd miar diar alles wo mr hend.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lee_QuestComplete', 'dl_npc_lee', 'Awesome! Thank you so much, I can''t wait to set sail! ', 'Grossartig! Danke vielmals, ich kann es kaum erwarten, wieder in die See zu stechen!', 'Super! Danka viel mol, chas chum erwarta wieder is Meer z''stecha!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lee_QuestComplete2', 'dl_npc_lee', 'Here, take this pile of magical pearls we found. They are worth a lot here!', 'Hier, nimm diesen Haufen magischer Perlen, die wir gefunden haben, sie sind hier eine Menge wert.', 'Do, nimm de Hock magischi Perla womar gfunda hend, si sind do richtig viel wert.');
