INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_Thanks', 'dl_npc_leeroy2', 'Thank you.', 'Danke.', 'Mersi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_DidIt', 'dl_npc_leeroy2', 'I did it! See, I was not wrong about those DRAGONS after all.', 'Ich habe es geschafft! Mit diesen DRACHEN hatte ich also doch Recht.', 'Ih has fertig bracht! Ha, hesch gseh?! Hani doch Rächt gha mit dene DRACHE.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_DidIt2', 'dl_npc_leeroy2', 'But what if they come back and you need to fight them all alone?', 'Aber was, wenn sie zurückkommen und du musst ganz alleine gegen sie kämpfen?', 'Aber was isch we si zrügg chöme und du muesch ganz ellei gägä die Viicher kämpfe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_DidIt3', 'dl_npc_leeroy2', 'Here, you''d better take my sword. I don''t need it anymore.', 'Hier, nimm besser mein Schwert. Ich brauche es nicht mehr.', 'Hie, nimm gschider mis Schwert. Ih bruches nümm.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_DidIt4', 'dl_npc_leeroy2', 'And now, I''m off to a new adventure! See ya.', 'Und jetzt geht''s auf zu neuen Abenteuern! Bis bald.', 'Und jetzt gahni neui Abetüür gah erläbe! Bis irgendeinisch.');
