INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_Stop', 'dl_npc_leeroy', 'Hey you, stop right there! You can''t cross that bridge.', 'Hey du, keinen Schritt weiter! Du kannst nicht über diese Brücke.', 'Hey du, ken Schritt wiiter! Du chasch nid über die Brugg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyStop', 'dl_npc_leeroy', 'Why can''t I cross that bridge?', 'Wieso darf ich nicht auf die Brücke?', 'Weso derfi nid uf die Brugg?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_leeroy', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoDragons', 'dl_npc_leeroy', 'I don''t see any dragons.', 'Ich sehe keine Drachen.', 'Ich gse kei Drache.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CrossBridge', 'dl_npc_leeroy', 'But I want to cross the bridge!', 'Aber ich will über die Brücke!', 'Aber ich wett über d''Brugg!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_LeeroyChicken', 'dl_npc_leeroy', 'I could get you some chicken.', 'Ich könnte dir ein Hühnchen bringen.', 'Ich chönt der es Güggeli bringe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotBright', 'dl_npc_leeroy', 'You''re not the sharpest knife in the drawer, are you.', 'Du bist schon nicht gerade der hellste, was?', 'Du hesch scho chli en Schade, gell?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ChickenDone', 'dl_npc_leeroy', 'I got your chicken.', 'Ich habe dein Hühnchen.', 'Ich ha dis Güggeli.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_WhyStop', 'dl_npc_leeroy', 'You won''t believe it, but there are DRAGONS on this bridge.', 'Du wirst es nicht glauben, aber es hat DRACHEN auf dieser Brücke.', 'Du wirsches nid glaube, aber es het DRACHE uf dere Brugg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_WhyStop2', 'dl_npc_leeroy', 'I''m just making sure nobody gets mauled before I''ve slain them.', 'Ich schaue nur dass niemand zerfleischt wird bevor ich sie erledige.', 'Ich lueg nur das niemert zerfleischt wird bevor ich si bodige.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_WhoAreYou', 'dl_npc_leeroy', 'I''m Leeroy, former paladin of the Order. Lloyd fired me because I didn''t follow his stupid orders.', 'Ich bin Leeroy, ehemaliger Paladin des Ordens. Lloyd hat mich gefeuert weil ich seine dummen Befehle missachtet habe.', 'Ich bi de Leeroy, ehemalige Paladin vom Orde. De Lloyd het mi entloh well ich sini dumme Befehl missachtet ha.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_WhoAreYou2', 'dl_npc_leeroy', 'They think they don''t need me but I''ll show them! I''m gonna slay those DRAGONS!', 'Sie denken, sie kämen auch ohne mich klar, aber da haben sie sich geschnitten. Ich werde diese DRACHEN töten!', 'Si denked, si bruched mich nid, aber dene wirdis zeige! Ich wird die DRACHE abmurkse!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_NoDragons', 'dl_npc_leeroy', 'D''uh, that''s because they''re hiding! But they are just waiting for someone like you to feast on.', 'Tz, das ist weil sie sich verstecken! Sie warten nur auf jemanden wie dich den sie verspeisen können.', 'Tz, da isch well si sich verstecked! Si warted nur uf öpper we dich wo si zum Znacht ne chönd.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_CrossBridge', 'dl_npc_leeroy', 'You can''t because if someone''s going to slay those DRAGONS, that''s gonna be me.', 'Du kannst aber nicht, weil ich derjenige sein werde, der diese DRACHEN tötet.', 'Du chasch aber nid, well ich de sii wird wo die DRACHE killt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_CrossBridge2', 'dl_npc_leeroy', 'But I can''t fight with an empty stomach - I need some tasty chicken first.', 'Aber ich kann nicht mit einem leeren Magen kämpfen - zuerst brauch ich ein leckeres Hühnchen.', 'Aber ich cha nid mitme leere Mage kämpfe - zersch bruchi es feins Güggeli.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_CrossBridge3', 'dl_npc_leeroy', 'Unfortunately, I need to hold the line here. I can''t let some other guy steal my DRAGONS.', 'Leider muss ich hier die Stellung halten. Ich kann es nicht zulassen, dass ein anderer meine DRACHEN klaut.', 'Leider muni do d''Stellig halte. Ich chas eifach nid zuelah das en andere mini DRACHE chlaut.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_LeeroyChicken', 'dl_npc_leeroy', 'That would be great. But I only eat the finest chicken.', 'Das wäre toll. Aber ich esse nur die besten Hühnchen.', 'Das wär toll. Aber ich iss nur di beste Güggeli.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_LeeroyChicken2', 'dl_npc_leeroy', 'You know the innkeeper Helena in Gandria? Her special grilled chicken is second to none. Bring me one of those and I''ll slay those DRAGONS for you.', 'Kennst du die Wirtin Helena in Gandria? Ihr Brathühnchen Spezial ist unübertroffen. Bring mir so eins und ich töte diese DRACHEN für dich.', 'Kensch d''Wirtin Helena in Gandra? Ihres brötlet Spezial-Güggeli isch eifach unübertroffe. Bring mer das und ich kill die DRACHE für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_NotBright', 'dl_npc_leeroy', 'I prefer swords over knives, you know.', 'Schwerter statt Wörter!', 'Ich ha ken Schade, ich mach Schade!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_ChickenDone', 'dl_npc_leeroy', 'And why did it take you so long? I nearly starved! (Leeroy takes the chicken and devours it hastily)', 'Und wieso hat das so lange gedauert? Ich wäre fast verhungert! (Leeroy nimmt das Hühnchen und verschlingt es hastig)', 'Und wieso het das so lang duuret? Ich wär schier verhungeret! (De Leeroy nimmts Güggeli und druckts schnell abe)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Leeroy_ChickenDone2', 'dl_npc_leeroy', 'But now I''m ready. Let''s do this!', 'Aber jetzt bin ich bereit. Los geht''s!', 'Aber jetz bini recht parat du. Jetzt gohts los!');
