INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_WhatsGoingOn', 'dl_npc_lloyd2', 'By the Eternal Light! What''s going on here?', 'Beim Ewigen Licht! Was ist hier eigentlich los?', 'Was - bim Ewige Liecht - lauft do eigentlich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_WasLookingForYou', 'dl_npc_lloyd2', 'Commander Lloyd, what a coincidence! I was looking for you to give you this letter from Inina.', 'Kommandant Lloyd, was für ein Zufall! Ich habe dich gesucht um dir diesen Brief von Inina zu überreichen.', 'Kommandant Lloyd, jo sonen Zuefall! Ich ha dir eh de Brief do vode Inina gä wele.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_ReadLetter', 'dl_npc_lloyd2', '(Lloyd reads the letter) Hm, interesting. Thank you for helping us with that problem.', '(Lloyd liest den Brief) Hm, interessant. Danke, dass du uns bei diesem Problem geholfen hast.', '(De Lloyd list de Brief) Hm, interessant. Danke, dass üs mit dem Problem gholfe hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_ReadLetter2', 'dl_npc_lloyd2', 'Inina wanted you to join the Clerics, but you''ve already made another decision.', 'Inina wollte, dass du den Klerikern beitrittst, doch du hast dich bereits anders entschieden.', 'D''Inina het wele dass du de Kleriker biitrittsch, doch du hesch dich scho andersch entschide.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_ReadLetter3', 'dl_npc_lloyd2', 'She also wrote that she needs your help again, looking for hints that could help us find out where that beast came from.', 'Sie schreibt auch dass sie nochmals deine Hilfe benötigt, beim Suchen von weiteren Hinweisen, wo diese Biest hergekommen ist.', 'Si schriibt au dass si nomol dini Hilf bruucht, bim Sueche vo wiitere Hiiwis wo das Biescht hercho isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_ReadLetter4', 'dl_npc_lloyd2', 'But first, back to that lighthouse, what was going on there?', 'Aber zuerst zurück zu diesem Leuchtturm, was ist dort passiert?', 'Aber zersch zrugg zu dem Lüüchtturm, was isch det passiert?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_KillBird', 'dl_npc_lloyd2', 'I had to slay a monstrous bird.', 'Ich musste einen monströsen Vogel erledigen.', 'Ich ha en monströse Vogel möse bodige.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NothingImportant', 'dl_npc_lloyd2', 'Nothing of interest. I just enjoyed the thunderstorm.', 'Nichts interessantes. Ich hatte nur ein wenig Spass mit dem Gewitter.', 'Nüt interessants. Ich ha nur chli Spass gha mitem Gwitter.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YourJob', 'dl_npc_lloyd2', 'Technically, I only did what you were supposed to do.', 'Eigentlich habe ich eure Arbeit gemacht.', 'Eigentli hani eure Job gmacht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_KillBird', 'dl_npc_lloyd2', 'Hm, Jack told me about that monster, but we''ve never seen it. Good job on hunting it down.', 'Hm, Jack hat mir von diesem Monster erzählt, aber wir haben es nie gesehen. Gute Arbeit.', 'Hm, de Jack het mer vo dem Monster verzellt, aber mir hends nie gse. Guet gmacht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_NothingImportant', 'dl_npc_lloyd2', 'Hmm. It seemed to be a quite extraordinary thunderstorm. But if you say so...', 'Hmm. Das war ein ziemlich aussergewöhnliches Gewitter. Aber wenn du meinst...', 'Hmm. Das isch es zimlich ussergwöhnlichs Gwitter gsi. Aber wend meinsch...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_YourJob', 'dl_npc_lloyd2', 'Which means that you''ve slain that bird Jack told us about? Good job on hunting it down.', 'Du hast also diesen Vogel erledigt, von dem uns Jack erzählt hat? Gute Arbeit.', 'Du hesch also de Vogel erledigt vo dem de Jack üs verzellt het? Guet gmacht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_HurryUp', 'dl_npc_lloyd2', 'Nevertheless, Inina still needs your help discovering the strange caverns. She''s been away for too long now.', 'Trotzdem braucht Inina immer noch deine Hilfe was das Erkunden dieser seltsamen Höhlen angeht. Sie ist jetzt schon zu lange weg.', 'Trotzdem brucht d''Inina immer na dini Hilf bim Erforsche vo dene komische Höhle. Si isch ez scho zu lang weg.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_HurryUp2', 'dl_npc_lloyd2', 'I want you to immediately go back to the caverns and support her. That''s not a request!', 'Ich will dass du ohne weitere Abstecher zurück zu den Höhlen gehst und sie unterstützt. Das ist keine Bitte!', 'Ich wett dass du ohni wiiteri Abstecher zrugg id Höhlene gasch und si unterstützisch. Das isch kei Bitt!');
