INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_Hello', 'dl_npc_lloyd4', 'What... what happened here?! What did you...', 'Was... was ist denn hier passiert?! Was hast du...', 'Was... was isch denn do passiert?! Was hesch du...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_Hello2', 'dl_npc_lloyd4', '(Shocked) You killed them? I told you to interrogate them, and to be careful! They were our only suspects!', '(Schockiert) Du hast sie getötet? Ich hab dir doch gesagt, du sollst sie befragen, und zwar vorsichtig! Sie waren unsere einzigen Verdächtigen!', '(Schockiert) Du hesch si killt? Ich ha der doch gseit du sölsch si befröge, und zwar vorsichthig! Si sind üsi einzige Verdächtige gsi!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IDidntMean', 'dl_npc_lloyd4', 'I didn''t mean to!', 'Das wollte ich nicht!', 'Das hani nid wele!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CalmDown', 'dl_npc_lloyd4', 'Calm down, we''ll find a solution.', 'Beruhig dich, wir finden eine Lösung.', 'Chill mal, mer finded scho e Lösig.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TheyAttacked', 'dl_npc_lloyd4', 'They attacked me! It was self-defence.', 'Sie haben mich angegriffen! Es war Selbstverteidigung.', 'Sie hend mich agriffe! Es isch Selbstverteidigung gsi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TheyDeserved', 'dl_npc_lloyd4', 'They deserved it.', 'Sie haben es verdient.', 'Si hends verdient.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_IDidntMean', 'dl_npc_lloyd4', '(Sighs) Throwing your magic at people kills them apparently. Anyway, we''ve got to find a solution now.', '(Seufzt) Wenn du Leuten deine Magie um die Ohren haust, sterben sie offensichtlich. Egal, nun müssen wir eine Lösung finden.', '(Süüfzt) Wenn du dini Magie de Lüüt an Grind wirfsch, sterbeds offsichtlich. Egal, mer mönd ez e Lösig finde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_CalmDown', 'dl_npc_lloyd4', 'Do you have a suggestion?', 'Hast du einen Vorschlag?', 'Hesch denn en Vorschlag?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_TheyAttacked', 'dl_npc_lloyd4', 'Hrr. I''ll believe you. But this was the last time I asked you to interrogate someone. Anyway, what should we do now?', 'Hrr. Ich werde dir glauben. Aber das war das letzte Mal, dass ich dich jemanden befragen lasse. Wie soll es denn jetzt weitergehen?', 'Hrr. Ich mue dir wohl glaube. Aber da isch s''letzt mal gsi woni dich öpper befroge lah. Egal, we sölls ez wiitergo?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_TheyDeserved', 'dl_npc_lloyd4', 'Maybe they did, but it''s not your job to decide their fate. Anyway, what should we do now?', 'Vielleicht haben sie das. Dennoch solltest du nicht einfach über ihr Schicksal entscheiden. Nun, wie soll es jetzt weitergehen?', 'Vilicht hends das ja. Trotzdem chasch nid eifach über ihres Schicksal entscheide. Egal, we sölls ez wiitergo?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FoundLetter', 'dl_npc_lloyd4', 'I found this letter in their pockets. (Give letter)', 'Ich habe diesen Brief in ihren Taschen gefunden. (Brief geben)', 'Ich ha de Brief i ihre Täsche gfunde. (Brief gä)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_FoundLetter', 'dl_npc_lloyd4', 'Interesting. Let me have a look at it.', 'Interessant. Zeig mal her.', 'Interessant. Zeig mol.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_FoundLetter2', 'dl_npc_lloyd4', 'It seems to be from their boss. (Mumbles) mages... mark... Who is that "V."?', 'Er scheint von ihrem Auftraggeber zu kommen. (Murmelt) Magier... Mal... Wer ist dieser "V."?', 'Es schiint vo ihrem Uftraggeber z''sii. (Murmlet) Magier... Zeiche... Wer isch de "V."?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_tldr', 'dl_npc_lloyd4', 'I didn''t read the letter yet...', 'Ich habe den Brief noch nicht gelesen...', 'Ich ha de Brief nanig glese...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_FoundLetter3', 'dl_npc_lloyd4', 'Here, take it back. Somebody is still abducting mages. Inina is not the only one who''s missing.', 'Hier, nimm ihn wieder. Jemand verschleppt immer noch Magier. Inina ist nicht die einzige, die vermisst wird.', 'Do, nimmen wieder. Öppert verschleppt immerno Magier. D''Inina isch nid di einzig, wo vermisst wird.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_FoundLetter4', 'dl_npc_lloyd4', 'I''ve heard about other cases. We must do something! ', 'Ich habe bereits von anderen Fällen gehört. Wir müssen etwas dagegen unternehmen!', 'Ich ha scho vo anderne Fäll ghört. Mer mönd öppis degege mache!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_FoundLetter5', 'dl_npc_lloyd4', 'The letter''s author is a "V." and he mentions the highlands. Go and look for further clues there. Meanwhile I''m going to find out who that "V." is.', 'Der Autor des Briefs ist ein gewisser "V.", und er schreibt vom Hochland. Geh und finde heraus, ob es dort weitere Hinweise gibt. Währenddessen bringe ich in Erfahrung, wer dieser "V." ist.', 'De Autor vom Brief isch en gwüsse "V." und er schriibt öppis vom Hochland. Gang und lueg, obs det wiiteri Hiwiis git. Währenddesse findi use, wer de "V." sii chönt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_FoundLetter6', 'dl_npc_lloyd4', 'Oh, and keep an eye open for any hints regarding Inina.', 'Oh und falls du irgendwelche Hinweise siehst, was Inina betrifft... lass es mich wissen.', 'Oh und wennd irgendwelchi Hiiwis vo wege de Inina findsch - verzell mer devo.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereSearch', 'dl_npc_lloyd4', 'Where are those highlands?', 'Wo ist dieses Hochland?', 'Wo isch das Hochland?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyWork', 'dl_npc_lloyd4', 'And why exactly should I keep working for you?', 'Und wieso genau soll ich noch für dich arbeiten?', 'Und weso gnau söli na für dich schaffe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Bye', 'dl_npc_lloyd4', 'I''ll see what I can do.', 'Ich werde sehen, was ich tun kann.', 'Mal luege wani cha mache.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_WhereSearch', 'dl_npc_lloyd4', 'I think he''s talking about the highlands of Admantris, to the West of the marshlands.', 'Ich glaub er meint das Hochland von Admantris, westlich des Sumpflands.', 'Ich glaub er meint s''Hochland vo Admantris, westlich vom Sumpfland.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_WhyWork', 'dl_npc_lloyd4', 'Admantris is in danger. Gandria is in danger. And you probably are, too. Why would you stop helping me?', 'Admantris ist in Gefahr. Gandria ist in Gefahr. Und du bist wahrscheinlich auch in Gefahr. Wieso solltest du mir nicht helfen?', 'Admantris isch in Gfohr. Gandria isch in Gfohr. Und du bisch wahrschindli au in Gfohr. Weso sötsch mer nid helfe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_KeepWorking', 'dl_npc_lloyd4', 'Alright I''ll help you.', 'Gut, ich werde dir helfen.', 'Guet, ich wird der helfe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_StopWorking', 'dl_npc_lloyd4', 'You can start looking for another idiot to do your job.', 'Du kannst dir einen anderen Idioten suchen, der deinen Job macht.', 'Du chasch der en andere Tubel sueche wo din Job macht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_KeepWorking', 'dl_npc_lloyd4', '(Lloyd nods acknowledging)', '(Lloyd nickt anerkennend)', '(De Lloyd nickt anerkennend)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_StopWorking', 'dl_npc_lloyd4', '(Disdainfully) I don''t need you.', '(Verächtlich) Ich brauche dich nicht.', '(Verächtlich) Ich bruch dich nid.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_Bye', 'dl_npc_lloyd4', 'Be safe.', 'Pass auf dich auf.', 'Pass uf dich uf.');
