INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HelloUnpolite', 'dl_npc_lloyd', 'Hi.', 'Servus.', 'Sali du.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HelloMediumPolite', 'dl_npc_lloyd', 'Hello, Lloyd.', 'Hallo, Lloyd.', 'Hallo, Lloyd.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HelloPolite', 'dl_npc_lloyd', 'All hail, Commander Lloyd.', 'Seid gegrüsst, Kommandant Lloyd.', 'Grüezi wohl, Kommandant Lloyd.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_HelloUnpolite', 'dl_npc_lloyd', '(Lloyd glares down at you, clearly displeased) What do you want? Keep it short.', '(Lloyd starrt auf dich herunter, sichtlich verärgert) Was willst du? Fass dich kurz.', '(De Lloyd starrt uf dich abe, zimlich apisst) Was wetsch? Halt dich churz. ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_HelloPolite', 'dl_npc_lloyd', '(Lloyd nods to you) Hello, young mage. What brings you to me?', '(Lloyd nickt dir zu) Hallo, junger Magier. Was führt dich zu mir?', '(De Lloyd nickt dir zue) Hallo, junge Magier. Was führt dich zu mir?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RecommendationQuest', 'dl_npc_lloyd', 'I got this from Inina for you. (Give letter)', 'Ich habe diesen Brief von Inina für dich. (Brief geben)', 'Ich ha de Brief vode Inina für dich. (Brief gä)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IWantToJoin', 'dl_npc_lloyd', 'I want to become a cleric.', 'Ich will ein Kleriker werden.', 'Ich will en Kleriker werde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_ReadLetter', 'dl_npc_lloyd', '(Lloyd reads the letter) Hm, interesting. Thank you for helping us with that problem.', '(Lloyd liest den Brief) Hm, interessant. Danke, dass du uns bei diesem Problem geholfen hast.', '(De Lloyd list de Brief) Hm, interessant. Danke, dass üs mit dem Problem gholfe hesch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_ReadLetter2', 'dl_npc_lloyd', 'Inina seems to think a great deal of you. She wants you to join the clerics and help her find out where that monster came from.', 'Inina scheint ja viel von dir zu halten. Sie möchte, dass du den Klerikern beitrittst und ihr dann hilfst, herauszufinden, woher dieses Monster gekommen ist.', 'D''Inina schiint ja einiges vo dir z''halte. Si wett, dass du de Kleriker biitrittsch und ihre denn usefinde hilfsch, woher das Monster cho isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_ReadLetter3', 'dl_npc_lloyd', 'You two should go back to the place where you defeated that monster and look for hints.', 'Ihr zwei solltet zurück an den Ort, wo du das Monster besiegt hast, um nach Hinweisen zu suchen.', 'Ihr zwei söted zrugg a de Ort, wo du dem Viech de Gar us gmach hesch, zum nach Hiiwis sueche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_NoCleric', 'dl_npc_lloyd', 'Well, you may be a mage, but why should I trust you? We can''t just take anyone.', 'Nun, du magst zwar ein Magier sein, aber wieso sollte ich dir vertrauen? Wir können nicht einfach jeden aufnehmen.', 'Naja, du bisch zwar en Magier, aber weso söt ich dir traue? Mir chönd nid jede x-beliebig bi üs ufne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_AnotherGuild', 'dl_npc_lloyd', 'I''m sorry, but I see that you have already chosen another path. I still hope you''ll use your magic for to do good.', 'Tut mir leid, aber ich sehe, dass du bereits einen anderen Weg gewählt hast. Ich hoffe, du benutzt deine Magie dennoch für das Gute.', 'Sorry, aber ich gse dass du scho en andere Weg gwählt hesch. Ich hoff dass du dini Magie trotzdem fürs Guete bruchsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OneGuild', 'dl_npc_lloyd', 'What if I want to join another guild?', 'Was ist, wenn ich einer anderen Gilde beitreten will?', 'Was isch, weni enere andere Gilde biitrette wet?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatIfIJoin', 'dl_npc_lloyd', 'What happens if I join you?', 'Was passiert, wenn ich euch beitrete?', 'Was passiert, weni eu biitrete?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_OneGuild', 'dl_npc_lloyd', 'That''s for you to decide. But make your decision carefully, as you may only join one guild.', 'Das musst du selbst wissen. Aber entscheide überlegt, da du nur einer Gilde beitreten kannst.', 'Da muesch selber wüsse. Aber entscheid dich guet, well du nur zu einere Gilde ghöre chasch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_WhatIfIJoin', 'dl_npc_lloyd', 'You''ll get to learn divine magic. Also, we''ll give you a job that will bring you gold and honour.', 'Du würdest heilige Magie erlernen. Ausserdem kannst du Aufträge für uns erledigen, die dir Gold und Ehre einbringen.', 'Du würsch heiligi Magie lerne. Usserdem chasch Ufträg für üs erledige, wo der Gold und Ehr gend.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_JoinQuestion', 'dl_npc_lloyd', 'Alright, young mage. If you want to join the order of the clerics, you''ll have to swear the oath of the Eternal Light.', 'Gut, junger Magier. Wenn du dem Orden der Kleriker beitreten willst, musst du den Eid des Ewigen Lichts ablegen.', 'Guet, junge Magier. Wenn du em Orde vode Kleriker bitrette wetsch, muesch de Eid vom Ewige Liecht ablege.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_JoinQuestion2', 'dl_npc_lloyd', 'Promise to use your magic to heal and protect the innocent and to punish the ones who do not follow the path of the light.', 'Versprich, dass du deine Magie einsetzen wirst, um die Unschuldigen zu schützen und zu heilen und diejenigen zu bestrafen, die vom Pfad des Lichts abgekommen sind.', 'Versprich, dass dini Magie bruche wirsch, zum di Unschuldige heile und beschütze und die zbestrafe, wo vom Pfad vom Liecht abcho sind.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Promise', 'dl_npc_lloyd', 'I promise. [JOIN THE CLERICS]', 'Ich verspreche es. [DEN KLERIKERN BEITRETEN]', 'Ich versprichs. [DE KLERIKER BIITRETE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Rethink', 'dl_npc_lloyd', 'I need more time to think about this.', 'Ich muss das nochmal überdenken.', 'Ich mun do nomol drüber nadenke.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_JointClerics', 'dl_npc_lloyd', 'I now declare you a mage of the Order of the Clerics. May the Eternal Light guide thee. ', 'Ich mache dich nun zu einem Magier des Ordens der Kleriker. Möge das Ewige Licht dich führen.', 'Ich mach dich ez zumne Magier vom Orde vode Kleriker. Es Ewige Liecht söll dich führe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HowCanIHelp', 'dl_npc_lloyd', 'What needs to be done?', 'Was gibt es zu tun?', 'Was gits z''tue?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CathedralFinished', 'dl_npc_lloyd', 'I''ve purified the necrotic grimoire.', 'Ich habe das nekrotische Zauberbuch geläutert.', 'Ich has nekrotisch Zauberbuech g''lüüteret.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YashaStart', 'dl_npc_lloyd', 'Is there anything else I can do for you?', 'Gibt es sonst noch etwas, was ich für euch tun kann?', 'Gits susch na öppis woni für eu mache chan?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YashaDoubt', 'dl_npc_lloyd', 'What if I don''t survive the leap?', 'Was, wenn ich den Sprung nicht überlebe?', 'Was, weni de Sprung nid überleb?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YashaComplete', 'dl_npc_lloyd', 'I survived the leap of faith.', 'Ich habe den Glaubenssprung überlebt.', 'Ich ha de Glaubenssprung überlebt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_CathedralCleric', 'dl_npc_lloyd', 'We have found a dangerous grimoire about necromancy and brought it into our cathedral.', 'Wir haben ein gefährliches Buch über Nekromantie gefunden und es in unsere Kathedrale gebracht.', 'Mir hend es gföhrlichs Buech über Nekromantie gfunde unds i üsi Kathedrale brocht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_CathedralCleric2', 'dl_npc_lloyd', 'But the book seems to have its own will - we couldn''t destroy it yet.', 'Aber das Buch scheint seinen eigenen Willen zu haben - wir konnten es noch nicht vernichten.', 'Aber es Buech schiint sin eigene Wille z''ha - mer hends nanig vernichte chöne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_CathedralCleric3', 'dl_npc_lloyd', 'I want you to go to our cathedral. Kindle the divine candles with your light magic and purify that deviant book. ', 'Ich will, dass du in unsere Kathedrale gehst. Entzünde die heiligen Kerzen mit deiner Lichtmagie und läutere dieses abartige Buch.', 'Ich will, dass du i üsi Kathedrale gosch. Entzünd di heilige Chärze mit dinere Liechtmagie und läutere das abartig Buech.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_CathedralFinished', 'dl_npc_lloyd', 'Well done. It''s twisted magic is no longer besmirching our cathedral.', 'Gut gemacht. Seine verdorbene Magie besudelt unsere Kathedrale nicht mehr.', 'Guet gmacht. Sini verdorbe Magie besudled üsi Kathedrale nümm.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_CathedralFinished2', 'dl_npc_lloyd', 'I think you''ve gained enough experience to learn how to shield yourself against evil magic. Take this scroll as a reward for your help.', 'Ich denke, du hast nun genug Erfahrung um zu lernen, wie man sich gegen solch bösartige Magie schützt. Nimm diese Schriftrolle als Belohnung für deine Hilfe.', 'Ich denk, du hesch ez gnueg Erfahrig zum lerne, we me sich geg so bösi Magie schützt. Do, nimm die Schriftrolle als Belohnig für dini Hilf.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_YashaStart', 'dl_npc_lloyd', 'You''ve already done a lot for us and showed your skill with staff and magic.', 'Du hast schon viel für uns getan und dein Können mit Stab und Schwert gezeigt.', 'Du hesch scho viel für üs gmacht und dis Könne mit Stab und Schild zeigt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_YashaStart2', 'dl_npc_lloyd', 'The time has come to prove your faith in the Eternal Light.', 'Die Zeit ist nun gekommen, auch deinen Glauben ans Ewige Licht unter Beweis zu stellen.', 'D''Ziit isch ez cho, zum au din Glaube as Ewige Liecht unter Bewiis z''stelle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_YashaStart3', 'dl_npc_lloyd', 'How should I prove my faith?', 'Wie soll ich meinen Glauben unter Beweis stellen?', 'We söli min Glaube unter Bewiis stelle?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_YashaStart4', 'dl_npc_lloyd', 'There''s an abadoned temple, guarded by a demon in the Highlands of Admantris. Find this temple and face its tests.', 'Es gibt einen verlassenen Temel im Hochland von Admantris, der von einem Dämon bewacht wird. Du musst diesen Tempel finden und dich seinen Prüfungen stellen. ', 'Es git en verlassene Tempel im Hochland vo Admantris, wo vomne Dämon bewacht wird. Du muesch de Tempel finde und dich sine Prüefige stelle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_YashaStart5', 'dl_npc_lloyd', 'If you find a way through the temple, you''ll discover a dark abyss. With the power of the Eternal Light, summon a holy shield and jump.', 'Wenn du einen Weg durch den Tempel findest, wirst du an einen dunkeln Abgrund kommen. Mit der Macht des Ewigen Lichts kannst du einen heiligen Schild beschwören und springen.', 'Wenn du en Weg dur de Tempel findsch, wirsch anen dunkle Abgrund cho. Mit de Chraft vom Ewige Liecht chasch en heilige Schild beschwöre und gumpe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_YashaStart6', 'dl_npc_lloyd', 'If your faith is strong, you won''t only survive it, but also gain the power to never ever get stuck in a dark abyss again. ', 'Wenn dein Glaube stark ist, wirst du es nicht nur überleben, sondern auch die Fähigkeit gewinnen, nie mehr in einem solchen dunkeln Abgrund festzustecken.', 'Wenn din Glaube starch isch, wirsches nid nur überlebe, sondern au d''Fähigkeit becho, nie me i somne dunkle Abgrund stecke z''bliibe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_YashaDoubt', 'dl_npc_lloyd', 'That would mean you''re unworthy. Nobody would miss you.', 'Das würde bedeuten, dass du unwürdig bist. Niemand würde dir nachweinen.', 'Da wür bedüüte, dass du unwürdig bisch. Niemert wür di vermisse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_YashaComplete', 'dl_npc_lloyd', 'Yes, I can feel the light flow through you. May it always guide you.', 'Ja, ich kann spüren, wie das Licht durch dich fliesst. Möge es dich immer leiten.', 'Ja, ich cha gspüre, wes Liecht dur dich flüsst. Uf das es dich immer leite mag.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_VeliusFound', 'dl_npc_lloyd', 'I found a letter from Inina... (Show letter)', 'Ich habe einen Brief von Inina gefunden... (Brief zeige)', 'Ich han en Brief vode Inina gfunde... (Brief zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_VeliusFound', 'dl_npc_lloyd', 'That "V." is called Velius. He abducted some mages including Inina and I should go into the crypt of Gandria.', 'Dieser "V." heisst Velius. Er hat ein paar Magier samt Inina entführt und ich sollte in die Krypta von Gandria gehen.', 'De "V." heisst Velius. Er het es paar Magier samt Inina entführt und ich sött id Krypta vo Gandria go.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_VeliusFound', 'dl_npc_lloyd', 'Velius is the one who abducted Inina? Interesting.', 'Velius hat also Inina entführt? Interessant.', 'De Velius het also d''Inina entführt? Interessant.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_VeliusFound2', 'dl_npc_lloyd', 'The only Velius I know is one of the royal mages. I would never dare to accuse him of something.', 'Der einzige Velius, den ich kenne, ist einer der königlichen Hofmagier. Ich würde es niemals wagen, ihm etwas zu unterstellen.', 'De einzig Velius, wo ich kenn, isch eine vode königliche Hofmagier. Ich würs nie woge, ihm öppis z''unterstelle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_VeliusFound3', 'dl_npc_lloyd', 'I''m a subordinate of the king and his mages. I''m not allowed to help you.', 'Ich bin dem König und seinen Magiern unterstellt. Ich darf dir nicht helfen.', 'Ich bi em König und sine Magier unterstellt. Ich dörf der nid helfe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_VeliusFound4', 'dl_npc_lloyd', 'This letter might be a trap as well...', 'Dieser Brief könnte auch ein Falle sein...', 'De Brief chönt au e Falle sii...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lloyd_VeliusFound5', 'dl_npc_lloyd', 'However, I won''t forbid you to go into the crypt and look for yourself.', 'Allerdings werde ich dir nicht verbieten, in die Krypta zu gehen und es selbst herauszufinden.', 'Allerdings wirdi der nid verbüte, selber id Krypta z''go und es usezfinde.');
