INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Bow', 'dl_npc_loganthird', '(Bow to Logan)', '(Sich vor Logan verbeugen)', '(Sich vorem Logan verbüüge)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Highness', 'dl_npc_loganthird', '(Cough) Your highness?', '(Hust) Eure Hoheit?', '(Hust) Euri Hoheit?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mark', 'dl_npc_loganthird', '(Show him the mark)', '(Ihm das Mal zeigen)', '(Ihm es Zeiche zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Bow', 'dl_npc_loganthird', '... (Logan doesn''t seem to be present)', '... (Logan scheint nicht anwesend zu sein)', '... (De Logan isch glaub nid ganz do)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Highness', 'dl_npc_loganthird', '... (Logan stares past you)', '... (Logan starrt an dir vorbei)', '... (De Logan starrt a dir verbii)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Mark', 'dl_npc_loganthird', '... (Logan finally turns his head and looks at you) You may speak.', '... (Logan dreht endlich seinen Kopf und schaut dich an) Du darfst sprechen.', '... (De Logan treiht endli sin Chopf und luegt dich ah) Du derfsch rede.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Velius', 'dl_npc_loganthird', 'Your royal mage, Velius, we have to talk about him.', 'Dein königlicher Magier, Velius, wir müssen über ihn reden.', 'Din königliche Magier, de Velius, mer mönd über ihn rede.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Eyes', 'dl_npc_loganthird', 'What''s the matter with your eyes?', 'Was ist mit deinen Augen los?', 'Was isch mit dine Auge los?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Father', 'dl_npc_loganthird', 'Where is your father?', 'Wo ist dein Vater?', 'Wo isch din Vatter?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Young', 'dl_npc_loganthird', 'Don''t you think you''re a bit too young to be king?', 'Glaubst du nicht, dass du etwas zu jung bist, um König zu sein?', 'Glaubsch nid, dass chli z''jung bisch zum König sii?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Outside', 'dl_npc_loganthird', 'Have you ever been outside the castle?', 'Warst du jemals ausserhalb des Schlosses?', 'Bisch schomal usem Schloss duss gsi?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mage', 'dl_npc_loganthird', 'Are you a mage?', 'Bist du ein Magier?', 'Bisch du en Magier?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_KnowMe', 'dl_npc_loganthird', 'Do you know me?', 'Kennst du mich?', 'Kennsch du mich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatMark', 'dl_npc_loganthird', 'What does this mark mean?', 'Was bedeutet dieses Mal überhaupt?', 'Was bedüüted das Zeiche überhaupt?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Velius', 'dl_npc_loganthird', 'Why don''t you talk to him yourself?', 'Wieso redest du nicht selbst mit ihm?', 'Weso redsch nid selber mit ihm?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereVelius', 'dl_npc_loganthird', 'Where can I find him?', 'Wo kann ich ihn finden?', 'Wo ischer denn?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Ookay', 'dl_npc_loganthird', 'Oookay...', 'Oookay...', 'Oookay...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_WhereVelius', 'dl_npc_loganthird', '(Points at your mark) You should know.', '(Zeigt auf dein Mal) Das solltest du wissen.', '(Zeigt uf dis Zeiche) Da sötsch wüsse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Oookay', 'dl_npc_loganthird', '...', '...', '...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Eyes', 'dl_npc_loganthird', 'That''s no concern of yours. Don''t waste my time.', 'Das geht dich nichts an. Verschwende meine Zeit nicht.', 'Das goht dich nüt ah. Verschwend mini Ziit nid.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Father', 'dl_npc_loganthird', 'He''s fine.', 'Ihm geht''s gut.', 'Ihm gohts guet.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Father2', 'dl_npc_loganthird', '... (Logan''s eyes suddenly light up) No, he isn''t. Please help me!', '... (Logans Augen leuchten plötzlich auf) Nein, ist er nicht. Bitte hilf mir!', '... (Em Logan sini Auge lüüchted plötzli uf) Nei, ischer nid. Bitte, hilf mir!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HowHelp', 'dl_npc_loganthird', 'How can I help you?', 'Wie kann ich dir helfen?', 'We chani der helfe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatFather', 'dl_npc_loganthird', 'So, what exactly happened to him?', 'Was ist denn genau mit ihm passiert?', 'Was isch denn gnau mitem passiert?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_HowHelp', 'dl_npc_loganthird', '(Logan is grappling for words, but then falls back into his old, absent state) ...', '(Logan ringt um Worte, fällt dann aber wieder zurück in seinen alten, abwesenden Zustand) ...', '(De Logan ringt um Wort, fallt denn aber wieder zrugg i sin alte, abwesende Zuestand) ...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Young', 'dl_npc_loganthird', '... No. Do you have a problem with it?', '... Nein. Hast du ein Problem damit?', '... Nei. Hesch es Problem demit?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotMyKing', 'dl_npc_loganthird', 'You''re not my king. I didn''t vote for you.', 'Du bist nicht mein König. Ich habe dich nicht gewählt.', 'Du bisch nid min König. Ich ha dich nid gwählt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_MyKing', 'dl_npc_loganthird', 'No, not at all.', 'Nein, sicher nicht.', 'Nei, voll nid.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_NotMyKing', 'dl_npc_loganthird', 'You don''t vote for kings.', 'Könige werden nicht gewählt.', 'En König wird nid gwählt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_MyKing', 'dl_npc_loganthird', '...', '...', '...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Outside', 'dl_npc_loganthird', 'Why should I.', 'Wieso sollte ich.', 'Weso sötti.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_KingShouldKnow', 'dl_npc_loganthird', 'A king should know his people.', 'Ein König sollte sein Volk kennen.', 'En König sött sis Volk kenne.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BeautifulOutside', 'dl_npc_loganthird', 'It''s beautiful outside!', 'Es ist wunderschön draussen!', 'Es isch wunderschön dusse!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotOutside', 'dl_npc_loganthird', 'Yeah, you''re right. The life outside is not something for you.', 'Ja, du hast Recht. Das Leben da draussen ist nichts für dich.', 'Ja, hesch Recht. Es Lebe det usse isch nüt für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_KingShouldKnow', 'dl_npc_loganthird', 'Don''t you tell me what a king should do.', 'Sag mir nicht, was ein König zu tun hat.', 'Seg mer nid, was en König z''tue het.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_BeautifulOutside', 'dl_npc_loganthird', 'I don''t care.', 'Ist mir egal.', 'Isch mer egal.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_NotOutside', 'dl_npc_loganthird', '...', '...', '...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Mage', 'dl_npc_loganthird', '... Yes.', '... Ja.', '... Jo.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_KnowMe', 'dl_npc_loganthird', '... No.', '... Nein.', '... Nei.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_WhatMark', 'dl_npc_loganthird', 'You should know that. It''s the reason you''re here. ', 'Du solltest das wissen. Deshalb bist du ja hier.', 'Du sötsch das wüsse. Drum bisch ja do.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_WhatMark2', 'dl_npc_loganthird', 'And it''s the reason you will *die* here.', 'Und es ist der Grund, wieso du hier *sterben* wirst.', 'Und es isch de Grund, weso du do *sterbe* wirsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_BadDecision', 'dl_npc_loganthird', 'That''s a very bad decision, boy. You against me?', 'Das ist eine sehr schlechte Entscheidung, Junge. Du gegen mich?', 'Da isch e sehr schlechti Entscheidig, Bueb. Du gege mich?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CantKill', 'dl_npc_loganthird', 'Why would you do that?', 'Wieso willst du das tun?', 'Weso wetsch da mache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_BadDecision', 'dl_npc_loganthird', 'No. Just you and my GUARDS!', 'Nein. Nur du und meine WACHEN!', 'Nei. Nur du und mini WACHE!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_ForcedStay', 'dl_npc_loganthird', 'I''ve answered enough questions. Guards, kill him!', 'Ich habe genügend Fragen beantwortet. Wachen, tötet ihn!', 'Ich ha ez gnueg Froge beantwortet. Wache, töted ihn!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Afraid', 'dl_npc_loganthird', 'You... you killed my guards!', 'Du... du hast meine Wachen getötet!', 'Du... du hesch mini Wache tötet!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Logan_Afraid2', 'dl_npc_loganthird', 'AAAAaaaaaaAAAAAH!', 'AAAAaaaaaaAAAAAH!', 'AAAAaaaaaaAAAAAH!');
