INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_Hello', 'dl_npc_lua', 'Oh, hello. It''s very uncommon to see a mage with such a special aura. You''re an elementalist, right?', 'Oh, hallo. Es ist ziemlich unüblich, einen Magier mit einer solch speziellen Aura zu treffen. Du bist ein Elementarmagier, nicht wahr?', 'Oh, hallo. Es isch ziemlig ungwöhnlich ä Magier mit sonere speziellä Aura z''träffe. Du bisch ä Elementarmagier, gäll?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Elementalist', 'dl_npc_lua', 'Yes, you''re right. How do you know?', 'Ja, bin ich. Woher weisst du das?', 'Ja, bini. Woher weisch du das?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Flirt', 'dl_npc_lua', 'It''s also uncommon to meet such a beautiful lady here.', 'Es ist auch unüblich, hier so eine hübsche Dame zu treffen.', 'Es isch au ungwöhnlich, da sone hübschi Dame z''treffe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_Elementalist', 'dl_npc_lua', 'I''m a clairvoyant. (Smiles mysteriously)', 'Ich bin eine Sehende. (Lächelt geheimnisvoll)', 'Ich bi ä Sehendi. (Lächlet gheimnisvoll)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_Flirt', 'dl_npc_lua', '(Blushes) Oh, thank you. What a pity, my skills as a clairvoyant only show me your aura, not your thoughts.', '(Errötet) Oh, danke. Wie schade, meine Fähigkeiten als Sehende enthüllen nur deine Aura, nicht aber deine Gedanken.', '(Errötet) Oh, merci. Schad, mini Fähigkeite als Sehendi enthüllä nur dini Aura, aber nid dini Gedanggä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Clairvoyant', 'dl_npc_lua', 'What does it mean to be a clairvoyant?', 'Was bedeutet es, eine Sehende zu sein?', 'Was heissts, e Sehendi z''sii?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_MyAura', 'dl_npc_lua', 'What colour does my aura have then?', 'Was für eine Farbe hat dann meine Aura?', 'Wa füre Farb het denn mini Aura?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoNecromancer', 'dl_npc_lua', 'You don''t look like a necromancer...', 'Du siehst nicht gerade wie ein Nekromant aus...', 'Du gsesch nid grad we en Nekromant us...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_EyeQuest', 'dl_npc_lua', 'Can I help you with something?', 'Kann ich dir irgendwie helfen?', 'Chan ich dir irgendwie helfe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GotEye', 'dl_npc_lua', 'I got your amulet.', 'Ich habe dein Amulett.', 'Ich ha dis Amulett.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_Clairvoyant', 'dl_npc_lua', 'Most mages only feel the power of someone''s aura. People like me can see their colour - the mage''s affinity to a type of magic.', 'Die meisten Magier spüren nur die Kraft von Auren. Leute wie ich sehen aber ihre Farbe - die Affinität eines Magiers zu einem Zweig der Magie.', 'Diä meischtä Magier gspüürä nur d''Kraft vo Aurenä. Lüt wiä ich gsehn aber ihri Farb - d''Affinität vom Magier zunere gwüsse Art vo Magie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_MyAura', 'dl_npc_lua', 'For me, it appears in a fiery red. Exceptionally beautiful to look at.', 'Für mich erscheint sie in einem feurigen Rot. Ein aussergewöhnlich schöner Anblick. ', 'Für mich erschiint sie imene Fyyrrot. Wirgglig ussergwöhnlig zum aaluege.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_NoNecromancer', 'dl_npc_lua', 'Just because I don''t wear their robes? Well, to be honest, I just came here to read.', 'Einfach weil ich nicht ihre Roben trage? Nun, um ehrlich zu sein, bin ich einfach zum Lesen hergekommen.', 'Nur willi nit ihri Robe aa ha? Jo wenni ehrlig bi bini au nur doo zum läse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_NoNecromancer2', 'dl_npc_lua', 'I love this library. So much knowledge that would be burned by the clerics if they got the chance...', 'Ich liebe diese Bibliothek. So viel Wissen, welches von den Klerikern verbrannt werden würde, wenn es ihnen möglich wäre.', 'Ich lieb die Bibliothek. So viel Wüssä wo vo dä Kleriker verbrennt wärde würd, wennsi d''Möglichkeit drzue hättä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_EyeQuest', 'dl_npc_lua', 'Oh, that''s so nice of you to ask. ', 'Oh, lieb von dir, dass du fragst.', 'Oh, isch lieb, dassd fragsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_EyeQuest2', 'dl_npc_lua', 'My dear magical amulet, the "Eye of the Moon", got stolen.', 'Mein geliebtes magisches Amulett, das "Auge des Mondes", wurde gestohlen.', 'Mis gliebt Amulet, es "Mondaug" isch gstoohle worde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_EyeQuest3', 'dl_npc_lua', 'Unfortunately, I don''t have any clue where it could be.', 'Leider habe ich keine Idee, wo es sein könnte.', 'Leider hani gar keni Ideä, wos si chenti.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_EyeQuest4', 'dl_npc_lua', 'But I would give you my gold and precious stones, if you happen to find it.', 'Aber ich würde dir mein Gold und Edelsteine überlassen, wenn du es finden solltest.', 'Aber ich wür der mis Gold und Edelstei ge, wennds finde söttsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_GotEye', 'dl_npc_lua', 'Wow, you really found it?', 'Wow, du hast es wirklich gefunden?', 'Wow, du hesches würkli gfunde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_GotEye2', 'dl_npc_lua', 'I really, really missed it. Thank you, my hero. Here, take your reward.', 'Das habe ich wirklich sehr vermisst. Vielen Dank, mein Held. Hier, nimm deine Belohnung.', 'Das hani wirgglig sehr vrmisst. Danke, min Held. Da, nimm dini Belohnig.');
