INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_Hello', 'dl_npc_lua', 'Oh, hello. It''s very uncommon to see a mage with such a special aura. You''re an elementalist, right?', 'Oh, hallo. Es ist ziemlich unüblich, einen Magier mit einer solch speziellen Aura zu treffen. Du bist ein Elementarmagier, nicht wahr?', 'Oh, hallo. Es isch zimli ungwöhnlich, en Magier mit sonere spezielle Aura z''treffe. Du bisch en Elementarmagier, gell?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Elementalist', 'dl_npc_lua', 'Yes, you''re right. How do you know?', 'Ja, bin ich. Woher weisst du das?', 'Ja, bini. Woher weisch du das?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Flirt', 'dl_npc_lua', 'It''s also uncommon to meet such a beautiful lady here.', 'Es ist auch unüblich, hier so eine hübsche Dame zu treffen.', 'Es isch au ungwöhnlich, da sone hübschi Dame z''treffe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_Elementalist', 'dl_npc_lua', 'I''m a clairvoyant. (Smiles mysteriously)', 'Ich bin eine Sehende. (Lächelt geheimnisvoll)', 'Ich bin e Sehendi. (Lächlet gheimnisvoll)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_Flirt', 'dl_npc_lua', '(Blushes) Oh, thank you. What a pity, my skills as a clairvoyant only show me your aura, not your thoughts.', '(Errötet) Oh, danke. Wie schade, meine Fähigkeiten als Sehende enthüllen nur deine Aura, nicht aber deine Gedanken.', '(Errötet) Oh, danke. We schad, mini Fähigkeite als Sehendi enthülled nur dini Aura, aber nid dini Gedanke.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Clairvoyant', 'dl_npc_lua', 'What does it mean to be a clairvoyant?', 'Was bedeutet es, eine Sehende zu sein?', 'Was heissts, e Sehendi z''sii?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_MyAura', 'dl_npc_lua', 'What colour does my aura have then?', 'Was für eine Farbe hat dann meine Aura?', 'Wa füre Farb het denn mini Aura?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoNecromancer', 'dl_npc_lua', 'You don''t look like a necromancer...', 'Du siehst nicht gerade wie ein Nekromant aus...', 'Du gsesch nid grad we en Nekromant us...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_Clairvoyant', 'dl_npc_lua', 'Most mages only feel the power of someone''s aura. People like me can see their colour - the mage''s affinity to a type of magic.', 'Die meisten Magier spüren nur die Kraft von Auren. Leute wie ich sehen aber ihre Farbe - die Affinität eines Magiers zu einem Zweig der Magie.', 'Di meiste Magier spüred nur d''Chraft von Aurene. Lüt wi ich gsend aber ihri Farb - d''Affinität vom Magier zunere gwüsse Art vode Magie.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_MyAura', 'dl_npc_lua', 'For me, it appears in a fiery red. Exceptionally beautiful to look at.', 'Für mich erscheint sie in einem feurigen Rot. Ein aussergewöhnlich schöner Anblick. ', 'Für mich erschiint si imne Füürrot. Würkli ussergwöhnlich schön zum aluege.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_NoNecromancer', 'dl_npc_lua', 'Just because I don''t wear their robes? Well, to be honest, I just came here to read.', 'Einfach weil ich nicht ihre Roben trage? Nun, um ehrlich zu sein, bin ich einfach zum lesen hergekommen.', 'Eifach weli nid ihri Robe aha? Oke, weni ehrlich bi bini au nur da zum läse.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Lua_NoNecromancer2', 'dl_npc_lua', 'I love this library. So much knowledge that would be burned by the clerics if they got a chance...', 'Ich liebe diese Bibliothek. So viel Wissen, welches von den Klerikern verbrannt werden würde, wenn es ihnen möglich wäre.', 'Ich lieb die Bibliothek. So viel Wüsse wo vode Kleriker verbrennt werde wür, wenns d''Möglichkeit dezue hetted.');
