INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FirstTalk', 'dl_npc_luiz', 'Syrah was right to send you here. ', 'Syrah hatte also recht damit, dich hierherzuschicken.', 'D''Syrah het also rächt drmit gha, dass sie dich do ane gschiggt het.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FirstTalk2', 'dl_npc_luiz', 'Nothing kept you from following your path - not even using a forbidden spell.', 'Du hast deinen Weg unbeirrt fortgesetzt, auch wenn du dafür verbotene Magie einsetzen musstest.', 'Du bisch trotzdem uf dim Wäg blibe, au wennd drfür vrbotäni Magie hesch miesse bruche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FirstTalk3', 'dl_npc_luiz', 'So, if you''re eager to learn more, you''re welcome in these halls. ', 'Wenn du also noch mehr lernen willst, bist du in diesen Hallen willkommen.', 'Wennd also no meh lerne willsch, bisch in denä Halle willkommä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Forbidden', 'dl_npc_luiz', 'I didn''t know this was forbidden.', 'Ich wusste nicht, dass das verboten war.', 'Ich ha nid gwüsst dass das verbote isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Test', 'dl_npc_luiz', 'Was this some kind of test?', 'War das eine Prüfung?', 'Isch das enart e Prüefig gsi?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_Forbidden', 'dl_npc_luiz', 'Well, it shouldn''t be. Still, mages hang when they are caught using necromancy.', 'Nun, das sollte es natürlich nicht sein. Trotzdem werden noch immer Magier gehängt, wenn sie beim Ausüben von Nekromantie erwischt werden.', 'Naja, es sett natyyrligg nid vrbote syy. Trotzdäm wärde immerno Magier ufghänggt wenns mit Nekromantie vrwütscht wärde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_Test', 'dl_npc_luiz', 'Yes - and you passed. Necromancy is not trivial, I''m impressed.', 'Ja - und du hast bestanden. Nekromantie ist nicht einfach, ich bin beeindruckt.', 'Jo - und du hesch bestande. Nekromantie isch nid eifach, i bi beiidruggt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_YouJoinedOtherGuild', 'dl_npc_luiz', 'I see, you''ve already made your choice. Suit yourself.', 'Ich sehe, du hast dich bereits entschieden. Wie du willst.', 'Ich seh, du hesch dich scho entschide. We du willsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ForbiddenMagic', 'dl_npc_luiz', 'Why is it forbidden to use necromancy?', 'Wieso ist Nekromantie verboten?', 'Wieso isch Nekromantie verbote?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_luiz', 'Are you the leader here?', 'Bist du der Anführer hier?', 'Bisch du de Aführer da?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Learn', 'dl_npc_luiz', 'Where can I learn more?', 'Wo kann ich mehr lernen?', 'Wo chani meh lerne?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatIfJoin', 'dl_npc_luiz', 'Why should I join you?', 'Wieso sollte ich euch beitreten?', 'Weso sötti eu biitrette?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OtherGuild', 'dl_npc_luiz', 'What if I want to join another guild?', 'Was, wenn ich einer anderen Gilde beitreten möchte?', 'Was, weni enere andere Gilde biitrette wett?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WantToJoin', 'dl_npc_luiz', 'I want to join you.', 'Ich möchte euch beitreten.', 'Ich wett bi eu mitmache.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_ForbiddenMagic', 'dl_npc_luiz', 'People are afraid of things they don''t know - it''s just like being afraid of the dark.', 'Menschen fürchten sich vor Dingen, die sie nicht kennen - genau wie sie Angst vor der Dunkelheit haben.', 'Mensche hän Angscht vor de Sache wo si nid kennä - genau so, wie si Angscht vor dr Dunggelheit hän.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_ForbiddenMagic2', 'dl_npc_luiz', 'But where there is light, there is also shadow. One cannot just ignore them. But we can explore and study them until they are no longer alien.', 'Aber wo Licht ist sind auch Schatten, man kann sie nicht einfach ignorieren. Man kann sie aber erforschen und kennenlernen, bis sie einem nicht mehr fremd sind.', 'Aber wo Liecht isch, sin au Schätte, me ka si nid eifach ignoriere. Aber me ka si erforsche und kenneleere, bis si eim nüm eso fremd sind.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_WhoAreYou', 'dl_npc_luiz', 'Yes. I''m Luiz, the leader of the circle. We make sure that this banned magic won''t be forgotten.', 'Ja. Ich bin Luiz, der Anführer des Zirkels. Wir stellen sicher, dass diese verbannte Magie nicht vergessen wird. ', 'Jo. Ich bi dr Luiz, de Ahfüherer vom Zirkel. Mir luegä, dass die verbannti Magie nid vrgässe goht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_Learn', 'dl_npc_luiz', 'I''m pleased to hear that you want to learn more.', 'Es freut mich, dass du mehr lernen möchtest.', 'Es freut mi, dass du meh lerne willsch. ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_Learn2', 'dl_npc_luiz', 'Everyone who makes it to these halls should be able to learn. You may use the library and if you want to learn more spells, you should consider joining us.', 'Jeder, der es bis in diese Hallen schafft, sollte etwas lernen dürfen. Du kannst die Bibliothek benutzen und wenn du mehr Zauber lernen möchtest, kannst du uns beitreten.', 'Jede, wos bis in die Halle schafft, setti öppis dörfe lerne. Du kasch d''Bibliothek bruche und wend meh Zauber lerne willsch, kasch uns byyträtte.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_WhatIfJoin', 'dl_npc_luiz', 'You will learn what necromancy has to offer. We can teach you powerful spells if you help us with our research.', 'Du wirst lernen, was Nekromantie zu bieten hat. Wir können dir mächtige Zauber beibringen, wenn du uns bei unserer Forschung unterstützt.', 'Du wirsch lerne, was Nekromantie so z''biete het. Mir könne dir mächtigi Zauber byybringe, wenn du uns mit unsrer Forschig hilfsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_OtherGuild', 'dl_npc_luiz', 'I won''t object. Everybody should be able to learn whatever they want to learn.', 'Da habe ich nichts dagegen. Jeder sollte das lernen können, was ihn interessiert.', 'Do hani nüt drgäge. Jede sett das lerne könne wo ihn interessiert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_WantToJoin', 'dl_npc_luiz', 'You want to learn the forbidden magic and devote yourself to the mysteries of necromancy?', 'Du möchtest die verbotene Magie erlernen und verschreibst dich den Geheimnissen der Nekromantie?', 'Du möchtisch di verboteni Magie lerne und vrschryybsch di de Gheimnis vo dr Nekromantie?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_JoinNecromancers', 'dl_npc_luiz', 'Yes. [JOIN THE NECROMANCERS]', 'Ja. [DEN NEKROMANTEN BEITRETEN]', 'Jo. [DE NEKROMANTE BIITRETE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_INeedToThink', 'dl_npc_luiz', 'I still need time to think about this.', 'Ich brauche noch etwas Zeit um darüber nachzudenken.', 'Ich bruch namal chli Ziit zum drüber nodenke.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_JoinNecromancers', 'dl_npc_luiz', 'Very well. Welcome to the circle, novice. May your knowledge always grow.', 'Sehr gut. Willkommen im Zirkel, Novize. Möge dein Wissen immer wachsen.', 'So super. Willkomme im Zirkel, Novize. Uf das dis Wüsse immer wachse wird.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TeachSpells', 'dl_npc_luiz', 'Can you teach me some spells?', 'Kannst du mir Zauber beibringen?', 'Chasch du mir Zauber bibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TeachMore', 'dl_npc_luiz', 'Can you teach me more spells?', 'Kannst du mir noch mehr Zauber beibringen?', 'Chasch mer na me Zauber bibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HowCanIHelp', 'dl_npc_luiz', 'How can I help you?', 'Wie kann ich euch helfen?', 'Wie chani eu helfe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatElseHelp', 'dl_npc_luiz', 'What else can I do for you?', 'Was kann ich sonst noch für euch tun?', 'Was chani susch na für eu mache?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FinishedCathedral', 'dl_npc_luiz', 'I got the necrotic grimoire.', 'Ich habe das nekrotische Zauberbuch.', 'Ich hans nekrotisch Zauberbuech.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CompleteLloydsPlan', 'dl_npc_luiz', 'I''ve talked to Lloyd...', 'Ich habe mit Lloyd gesprochen...', 'Ich ha mitem Lloyd gredt...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_KillYasha', 'dl_npc_luiz', 'Do I have to kill the demon to get the mask?', 'Muss ich den Dämon töten um die Maske zu kriegen?', 'Mun ich de Dämon kille zum d''Maske z''becho?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereYasha', 'dl_npc_luiz', 'How can I get to the Highland of Admantris?', 'Wie komme ich ins Hochland von Admantris?', 'We chumi is Hochland vo Admantris?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YashaComplete', 'dl_npc_luiz', 'I found Yaslaw''s mask.', 'Ich habe die Maske von Yaslaw.', 'Ich hanem Yaslaw sini Maske.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FoundVelius', 'dl_npc_luiz', 'I found this letter from Bob... (show letter)', 'Ich habe diesen Brief von Bob gefunden... (Brief zeigen)', 'Ich ha de Brief vom Bob gfunde... (Brief zeige)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FirstSpell', 'dl_npc_luiz', 'Yes, you may learn how to drain the life energy from your enemies, novice. ', 'Ja, du darfst erlernen, wie man die Lebensenergie eines Gegners entzieht, Novize.', 'Jo, du derfsch lerne, wieme d''Läbensenergie vomä Gegner absuggt, Novize.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_NoSpellReputation', 'dl_npc_luiz', 'You''re not ready for that. Help us with our studies, and you will get the experience needed for more powerful spells.', 'Du bist dazu noch nicht bereit. Hilf uns mit unseren Forschungen und du wirst die Erfahrung erlangen, die für die mächtigeren Zauber nötig ist.', 'Für das bisch noni parat. Hilf uns mit unsere Forschige und du wirsch die Erfahrig beko, wode bruchsch, zum mit de mächtigere Zauber umgo.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_LloydsPlan', 'dl_npc_luiz', 'You know the leader of the clerics, Lloyd, don''t you? He''s been acting strangely lately, it seems like he''s planning something.', 'Du kennst den Anführer der Kleriker, Lloyd, nicht wahr? Er hat sich in letzter Zeit seltsam verhalten, es scheint als plane er etwas.', 'Du kennsch dr Aführer vode Kleriker, dr Lloyd, gäll? Er het sich in letschter Zyt kli komisch benoh, es schiint als würd er eppis plane.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_LloydsPlan2', 'dl_npc_luiz', 'We need someone who can talk to him and maybe even work for him. If you find out what he''s up to, come back and report.', 'Wir brauchen jemanden, der mit ihm sprechen kann und vielleicht auch für ihn arbeitet. Falls du herausfindest, was er vorhat, komm wieder und berichte uns davon.', 'Mir bruche öpper, wo mit ihm rede ka und vilicht sogar für ihn schafft. Falls du usefindisch, was er vor het, kum wider und verzell uns devo.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_CathedralNecro', 'dl_npc_luiz', 'Although the clerics do not know our hideout, they managed to get their hands on one of our grimoires about necromancy.', 'Obwohl die Kleriker unser Versteck nicht kennen, haben sie eins unserer Bücher über Nekromantie in die Hände bekommen.', 'Obwohl d''Kleriker unses Versteck nid kennä, hänsi eis vo unsne Biecher über Nekromantie ind Händ beko.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_CathedralNecro2', 'dl_npc_luiz', 'The problem is, it is a particularly dangerous and headstrong book about summoning creatures from the underworld. ', 'Es handelt sich dabei aber um ein ziemlich gefährliches und eigenwilliges Buch darüber, wie man Kreaturen aus der Unterwelt beschwört.', 'Es handlet sich debyy aber um es zimmlig eigewilligs und gföhrligs Buech drüber, wie me Kreature us dr Unterwält beschwört.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_CathedralNecro3', 'dl_npc_luiz', 'They took it into their cathedral to purify it, but this would do even more harm.', 'Sie haben es in ihre Kathedrale gebracht, um es zu läutern, doch das würde noch mehr Schaden anrichten.', 'Si häns in ihri Kathedrale brocht zums läutere, aber das würd no meh Schade arichte.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_CathedralNecro4', 'dl_npc_luiz', 'Bring that book back to our library. I just hope it''s not too late...', 'Geh und bring dieses Buch zurück in unsere Bibliothek. Ich hoffe einfach, es ist nicht schon zu spät...', 'Gang und bring das Buech zrugg in unseri Bibliothek. Ich hoff eifach, es isch nonig z''spot...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FinishedCathedral', 'dl_npc_luiz', 'Good job. I''ll put it back in our library where it belongs.', 'Gut gemacht. Ich werde es in unsere Bibliothek zurückstellen, wo es auch hingehört.', 'Guet gmacht. Ich wird''s in unseri Bibliothek zruggtue wos au aneghört.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FinishedCathedral2', 'dl_npc_luiz', 'I''m slowly starting to trust you. I think you''ve gained the experience to learn how to raise the dead...', 'Ich beginne langsam, dir zu vertrauen. Ich denke, du hast nun die nötige Erfahrung, um zu erlernen, wie man die Toten erweckt...', 'Ich fang langsam ah, dir z''traue. Ich dängg, du hesch jetz die nötigi Erfahrig zum lerne, wieme die Tote erweggt...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_CompleteLloydsPlan', 'dl_npc_luiz', '... Some "V." is abducting mages. Lloyd is trying to find out who that is.', '... Ein gewisser "V." entführt Magier. Lloyd versucht herauszufinden, wer das ist.', '... En gwüsse "V." entführt Magier. De Lloyd probiert usezfinde, wer das isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_CompleteLloydsPlan', 'dl_npc_luiz', 'That''s interesting, thank you for the report.', 'Das ist interessant, danke für den Bericht.', 'Das isch interessant, danke für dä Bricht.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_CompleteLloydsPlan2', 'dl_npc_luiz', 'But now I''m a bit worried about our Robert. He didn''t return from his last trip to the marshlands.', 'Aber jetzt mache ich mir ein wenig Sorgen um unsern Robert. Er ist nicht von seinem letzten Ausflug ins Sumpfland zurückgekehrt.', 'Aber jetz machi mir kli Sorge ume Robert. Er isch nid vo sim letschte Usflug ins Sumpfland zruggko.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_CompleteLloydsPlan3', 'dl_npc_luiz', 'Keep an eye open and tell me if you see him.', 'Halte ein Auge offen und sag mir, falls er dir über den Weg laufen sollte.', 'Bhalt es Aug offe und sagmer, wenn er dir übere Wäg laufe setti.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_NoMoreSpells', 'dl_npc_luiz', 'No, I''m sorry, I taught you everything I know.', 'Nein, tut mir leid, ich habe dir alles beigebracht, was ich weiss.', 'Nei, tuet mr leid, i hader alles zeigt woni weiss.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_NoMoreSpells2', 'dl_npc_luiz', 'But a good necromancer is always searching for new spells. There is more out there than I know.', 'Aber ein guter Nekromant ist immer auf der Suche nach neuen Zaubern. Es gibt mehr da draussen, als wir kennen.', 'Aber ä guetä Nekromant isch immer uf dr Suechi nach neue Zauber. Es git meh dert usse als mr kennä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_YashaStart', 'dl_npc_luiz', 'Hm. Indeed, I do have something for you.', 'Hm. Ja, ich habe etwas für dich.', 'Hm. Jo, ich ha öppis für dich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_YashaStart2', 'dl_npc_luiz', 'There''s a cursed temple in the Highlands of Admantris. A necromancer called Yaslaw once summoned a powerful demon in there.', 'Es gibt einen verfluchten Tempel im Hochland von Admantris. Ein Nekromant namens Yaslaw hat dort vor langer Zeit einen mächtigen Dämon beschworen.', 'Es git ä verfluechtä Tämpel im Hochland vo Admantris. Ä Nekromant namens Yaslaw het dert vor langer Zyyt ä mächtige Dämon beschwore.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_YashaStart3', 'dl_npc_luiz', 'Unfortunately, he got captured by his own creature and never returned. ', 'Unglücklicherweise wurde er von seiner eigenen Kreatur eingesperrt und kam nie zurück. ', 'Blöderwyys ischer vo sinere eigene Kreatur gfange ghalte worde und isch nie wieder zruggko.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_YashaStart4', 'dl_npc_luiz', 'But as far as we know from the old writings, he carried a magical mask with him that granted him great powers.', 'Aber soweit wir aus den alten Schriften lesen können, hatte er stets eine magische Maske bei ihm, die ihm grosse Kräfte verliehen hat.', 'Aber was me us de alte Schrifte läse könne, isch, dass er immer ä magischi Massge mit sich treit het wo ihm grossi Kräft geh het.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_YashaStart5', 'dl_npc_luiz', 'He has been dead long enough. Find his remains in the temple, let him lead you to his mask and bring it to us.', 'Er war nun lange genug tot. Finde seine Überreste im Tempel, lass ihn dich zu seiner Maske führen und bringe sie uns.', 'Er isch jetz lang gnue tot gsy. Find sini Überräschte im Tempel, lo di vo ihm zu sinere Massge füehre und bring si uns.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_KillYasha', 'dl_npc_luiz', 'You''ll have to find out for yourself.', 'Das musst du schon selbst herausfinden.', 'Das muesch scho sälber usefinde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_WhereYasha', 'dl_npc_luiz', 'As far as I know, there''s a path through a volcano in the Marshlands, to the South of Gandria.', 'Soweit ich weiss, gibt es einen Weg durch einen Vulkan in den Marschen, südlich von Gandria.', 'Sowit ich weiss gits ä Wäg dure Vulkan in de Marsche, syydlig vo Gandria.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_YashaComplete', 'dl_npc_luiz', 'And you got his powers too, I see. I''m really impressed, well done.', 'Und du hast ihre Kräfte auch bekommen wie ich sehe. Ich bin beeindruckt.', 'Und du hesch ihri Kräft au beko we ich gseh. Ich bi wyrgligg beidruggt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_YashaComplete2', 'dl_npc_luiz', 'Use its powers wisely, necromancer.', 'Gebrauche ihre Kraft weise, Nekromant.', 'Bruch ihri Kräft gschyyd, Nekromant.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_FoundVelius', 'dl_npc_luiz', 'A mage named Velius abducted him along with other mages. I need to go to the crypt of Gandria and find out more.', 'Ein Magier namens Velius hat ihn zusammen mit anderen Magiern entführt. Ich muss in die Krypta von Gandria gehen und mehr herausfinden.', 'En Magier namens Velius het en zeme mit anderne Magier entführt. Ich mun id Krypta vo Gandria go und me usefinde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FoundVelius', 'dl_npc_luiz', 'Hm. Very interesting. I know a mage named Velius, he studied with me a long time ago.', 'Hm. Sehr interessant. Ich kenne einen Magier namens Velius, er studierte mit mir vor langer Zeit.', 'Hm. Sehr interessant. Ich kenn ä Magier namens Velius, er het mit mir vor langer Zyyt studiert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FoundVelius2', 'dl_npc_luiz', 'He, abducting mages... I don''t know. That doesn''t sound like him. But you should go into the crypt and find out more.', 'Aber Magier entführen passt nicht zu ihm. Du solltest aber in die Krypta und mehr herausfinden.', 'Er... und Magier entfiehre... Ich weiss nid. Das passt nid zu ihm. Aber du settsch in die Krypta goh und meh uusefindä.');
