INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FirstTalk', 'dl_npc_luiz', 'Syrah was right about sending you here. ', 'Syrah hatte also recht damit, dich hierherzuschicken.', 'D''Syrah het also recht demit gha, dass sie dich da ane gschickt het.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FirstTalk2', 'dl_npc_luiz', 'Nothing kept you from following your way - not even using a forbidden spell.', 'Du hast deinen Weg unbeirrt fortgesetzt, auch wenn du dafür verbotene Magie einsetzen musstest.', 'Du bisch trotzdem uf dim Weg blibe, au wend defür verboteni Magie hesch möse bruche.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FirstTalk3', 'dl_npc_luiz', 'So, if you''re eager to learn more, you''re welcome in these halls. ', 'Wenn du also noch mehr lernen willst, bist du in diesen Hallen willkommen.', 'Wend also na me lerne wetsch, bisch i dene Halle willkomme.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Forbidden', 'dl_npc_luiz', 'I didn''t know this was forbidden.', 'Ich wusste nicht, dass das verboten war.', 'Ich ha nid gwüsst dass das verbote isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Test', 'dl_npc_luiz', 'Was this some kind of test?', 'War das eine Prüfung?', 'Isch das enart e Prüefig gsi?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_Forbidden', 'dl_npc_luiz', 'Well, it shouldn''t be. Still, mages hang when they are caught using necromancy.', 'Nun, das sollte es natürlich nicht sein. Trotzdem werden noch immer Magier gehängt wenn sie beim ausüben von Nekromantie erwischt werden.', 'Naja, es söt natürli nid verbote si. Trotzdem werded immerna Magier ufghänkt wens mit Nekromantie verwütscht werded.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_Test', 'dl_npc_luiz', 'Yes - and you passed. Necromancy is not trivial, I''m impressed.', 'Ja - und du hast bestanden. Nekromantie ist nicht einfach, ich bin beeindruckt.', 'Ja - und du hesch bestande. Nekromantie isch nid eifach, ich bi beiidruckt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_YouJoinedOtherGuild', 'dl_npc_luiz', 'I see, you''ve already made your choice. Suit yourself.', 'Ich sehe, du hast dich bereits entschieden. Wie du willst.', 'Ich gse, du hesch dich scho entschide. We du wetsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ForbiddenMagic', 'dl_npc_luiz', 'Why is it forbidden to use necromancy?', 'Wieso ist Nekromantie verboten?', 'Wieso isch Nekromantie verbote?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_luiz', 'Are you the leader here?', 'Bist du der Anführer hier?', 'Bisch du de Aführer da?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Learn', 'dl_npc_luiz', 'Where can I learn more?', 'Wo kann ich mehr lernen?', 'Wo chani meh lerne?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatIfJoin', 'dl_npc_luiz', 'Why should I join you?', 'Wieso sollte ich euch beitreten?', 'Weso sötti eu biitrette?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OtherGuild', 'dl_npc_luiz', 'What if I want to join another guild?', 'Was, wenn ich einer anderen Gilde beitreten möchte?', 'Was, weni enere andere Gilde biitrette wett?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WantToJoin', 'dl_npc_luiz', 'I want to join you.', 'Ich möchte euch beitreten.', 'Ich wett bi eu mitmache.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_ForbiddenMagic', 'dl_npc_luiz', 'People are afraid of things they don''t know - it''s just like being afraid of the dark.', 'Menschen fürchten sich vor Dingen, die sie nicht kennen - genau wie sie Angst vor der Dunkelheit haben.', 'Mensche hend Angscht vor de Sache wo si nid kenned - genau so wi si Angst vor de Dunkelheit hend.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_ForbiddenMagic2', 'dl_npc_luiz', 'But where there is light, there is also shadow. One cannot just ignore them. But we can explore and study them until they are no longer alien.', 'Aber wo Licht ist sind auch Schatten, man kann sie nicht einfach ignorieren. Man kann sie aber erforschen und kennen lernen, bis sie einem nicht mehr fremd sind.', 'Aber wo Liecht isch sind au Schatte, me cha si nid eifach ignoriere. Aber me cha si erforsche und kennelerne, bis si eim nüme so fremd sind.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_WhoAreYou', 'dl_npc_luiz', 'Yes. I''m Luiz, the leader of the circle. We make sure that this banned magic won''t be forgotten.', 'Ja. Ich bin Luiz, der Anführer des Zirkels. Wir stellen sicher, dass diese verbannte Magie nicht vergessen wird. ', 'Jo. Ich bi de Luiz, de Aführer vom Zirkel. Mir lueged, dass die verbannt Magie nid vergesse wird.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_Learn', 'dl_npc_luiz', 'I''m pleased to hear that you want to learn more.', 'Es freut mich, dass du mehr lernen möchtest.', 'Es freut mich, dass du meh lerne wetsch. ');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_Learn', 'dl_npc_luiz', 'Everyone who makes it to these halls should be able to learn. You may use the library and if you want to learn more spells, you should consider joining us.', 'Jeder, der es bis in diese Hallen schafft, sollte etwas lernen dürfen. Du kannst die Bibliothek benutzen und wenn du mehr Zauber lernen möchtest, kannst du uns beitreten.', 'Jede, wos bis i die Halle schafft, sött öppis lerne dörfe. Du chasch d''Bibliothek bruche und wend me Zauber lerne wetsch, chasch üs biitrette.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_WhatIfJoin', 'dl_npc_luiz', 'You will learn what necromancy has to offer. We can teach you powerful spells if you help us with our research.', 'Du wirst lernen, was Nekromantie zu bieten hat. Wir können dir mächtige Zauber beibringen wenn du uns bei unserer Forschung unterstützt.', 'Du wirsch lerne, was Nekromantie so z''büüte het. Mir chönd dir mächtigi Zauber biibringe wenn du üs mit üsere Forschig hilfsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_OtherGuild', 'dl_npc_luiz', 'I won''t object. Everybody should be able to learn whatever they want to learn.', 'Da habe ich nichts dagegen. Jeder sollte das lernen können, was ihn interessiert.', 'Do hani nüt degege. Jede söt das lerne chöne wo ihn interessiert.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_WantToJoin', 'dl_npc_luiz', 'You want to learn the forbidden magic and devote yourself to the mysteries of necromancy?', 'Du möchtest die verbotene Magie erlernen und verschreibst dich den Geheimnissen der Nerkomantie?', 'Du möchtisch di verboteni Magie lerne und verschriibsch dich de Gheimnis vode Nekromantie?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_JoinNecromancers', 'dl_npc_luiz', 'Yes. [JOIN THE NECROMANCERS]', 'Ja. [DEN NEKROMANTEN BEITRETEN]', 'Jo. [DE NEKROMANTE BIITRETE]');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_INeedToThink', 'dl_npc_luiz', 'I still need time to think about this.', 'Ich brauche noch etwas Zeit um darüber nachzudenken.', 'Ich bruch namal chli Ziit zum drüber nodenke.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_JoinNecromancers', 'dl_npc_luiz', 'Very well. Welcome to the circle, novice. May your knowledge always grow.', 'Sehr gut. Willkommen im Zirkel, Novize. Möge dein Wissen immer wachsen.', 'Sehr guet. Willkomme im Zirkel, Novize. Uf das dis Wüsse immer wachse wird.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TeachSpells', 'dl_npc_luiz', 'Can you teach me some spells?', 'Kannst du mir Zauber beibringen?', 'Chasch du mir Zauber bibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TeachMore', 'dl_npc_luiz', 'Can you teach me more spells?', 'Kannst du mir noch mehr Zauber beibringen?', 'Chasch mer na me Zauber bibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_FirstSpell', 'dl_npc_luiz', 'Yes, you may learn how to drain the life energy from your enemies, novice. ', 'Ja, du darfst erlernen wie man die Lebensenergie eines Gegners entzieht, Novize.', 'Ja, du derfsch lerne weme d''Lebensenergie vomne Gegner absuugt, Novize.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_NoSpellReputation', 'dl_npc_luiz', 'You''re not ready for that. Help us with our studies, and you will get the experience needed for more powerful spells.', 'Du bist dazu noch nicht bereit. Hilf uns mit unseren Forschungen und du wirst die Erfahrung erlangen, die für die mächtigeren Zauber nötig ist.', 'Da bisch nanig parat defür. Hilf üs mit üsne Forschige und du wirsch die Erfahrig becho wod bruchsch zum mit de mächtigere Zauber umga.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luiz_SecondSpell', 'dl_npc_luiz', 'Yes, you''ve earned the experience to learn how to raise the dead...', 'Ja, du hast nun die nötige Erfahrung um zu erlernen, wie man die Toten erweckt...', 'Jo, du hesch ez di nötig Erfahrig zum lerne, weme di Tote erweckt...');
