INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_luv', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luv_WhoAreYou', 'dl_npc_luv', 'I''m Luv, a sailor. Normally, I''d sail on the sea instead of sitting here.', 'Ich bin Luv, ein Matrose. Normalerweise würde ich übers Meer segeln statt hier zu versauern.', 'Ich bi da Luv, a Matros. Normalerwis würi übers Meer segla statt do z''vergammla.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luv_WhoAreYou2', 'dl_npc_luv', 'And if it wasn''t for that idiot Lee, I would still be with my crew on our ship.', 'Und wenn dieser Idiot Lee nicht wäre, würde ich immer noch mit meiner Crew auf unserem Schiff sein.', 'Und wenn dä Idiot Lee nid wär, würdi immer no mit minera Crew ufam Schiff si.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FirstRant', 'dl_npc_luv', 'Your mate told me you''re the one to blame.', 'Dein Kumpel hat gesagt du seist Schuld an eurem Schlamassel.', 'Din Kolleg het gseit du segsch Schuld a eurem Schlamassel.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_SecondRant', 'dl_npc_luv', 'Your mate says you shouldn''t have gotten drunk.', 'Dein Kumpel sagt du hättest dich nicht betrinken sollen.', 'Din Kolleg seit du hetsch dich nid betrinke söle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luv_OtherSays', 'dl_npc_luv', 'Oh yeah, did he? Well you can tell him that we would have made it if he wasn''t drunk when our ship cast off.', 'Ach ja, hat er das? Du kannst ihm sagen er hätte sich nicht betrinken sollen als unser Schiff abgelegt hat.', 'Ach jo, häter das? Du chaschäm sega er het sich halt nid betrinka söla wo üses Schiff abglait het.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luv_SecondRant', 'dl_npc_luv', 'Pha! If he hadn''t have slept when our captain called our sailors back, we wouldn''t have missed it either!', 'Pha! Hätte er nicht geschlafen, als unser Kapitän die Matrosen zurück aufs Schiff gerufen hat, hätten wir es auch nicht verpasst!', 'Pha! Wener ni pennt het, wo üsa Kapitän d''Matrosa zrugg ufs Schiff gruefa hät, hetemärs au ni verpasst!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luv_Thanks', 'dl_npc_luv', 'Thanks!', 'Danke!', 'Danka!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CalmDown', 'dl_npc_luv', 'I think you both should calm down.', 'Ich denke ihr beide solltet euch beruhigen.', 'Ich denk ihr beidi söted eu beruhige.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_HelpYou', 'dl_npc_luv', 'Maybe I could help you.', 'Vielleicht kann ich euch helfen.', 'Vilicht chani eu helfe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_QuestComplete', 'dl_npc_luv', 'I got a compass for you.', 'Ich habe einen Kompass für euch.', 'Ich hanen Kompass für eu.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luv_CalmDown', 'dl_npc_luv', 'Yes maybe you''re right. This doesn''t get us anywhere.', 'Ja vielleicht hast du recht. Das bringt uns auch nicht weiter.', 'Jo velicht hesch rächt. Da bringt üs au ni witer.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luv_CalmDown2', 'dl_npc_luv', 'We know where our captain wanted to sail and we could buy a small boat here.', 'Wir wissen, wo unser Kapitän hinwollte und wir könnten hier auch ein kleines Boot kaufen.', 'Miar wüssad, wo üsa Kapitän heräwella het und miar chönted au as chlises Boot chaufe.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luv_CalmDown3', 'dl_npc_luv', 'But to sail over the sea, we''d need a compass and nobody in this damn city has one.', 'Doch um über das Meer zu segeln bräuchten wir einen Kompass und niemand in dieser verfluchten Stadt hat einen.', 'Aber zum übers Meer sägla brüchtemer a Kompass und niamert i dera verfluachte Stadt het eina.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luv_HelpYou', 'dl_npc_luv', 'Yes maybe. If you could get us a working compass, we''d give you all we have left.', 'Ja vielleicht. Wenn du uns einen funktionierenden Kompass besorgen kannst, werden wir dir alles geben, was wir noch haben.', 'Jo velicht. Wenn du üs a funktionierenda Kompass bringsch, gänd miar diar alles wo mr hend.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luv_QuestComplete', 'dl_npc_luv', 'Awesome! Thank you so much, I can''t wait to set sails! ', 'Grossartig! Danke vielmals, ich kann es kaum erwarten, wieder in die See zu stechen!', 'Super! Danka viel mol, chas chum erwarta wieder is Meer z''stecha!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Luv_QuestComplete2', 'dl_npc_luv', 'Here, take this pile of magical pearls we found, they are worth a lot here!', 'Hier, nimm diesen Haufen magischer Perlen, die wir gefunden haben, sie sind hier eine Menge wert.', 'Do, nimm de Hock magischi Perla womar gfunda hend, si sind do richtig viel wert.');
