INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Mona_Hi', 'dl_npc_mona', 'Oh, a new guest. Welcome to the Red Pearl.', 'Oh, ein neuer Gast. Willkommen in der Roten Perle.', 'Oh, en neue Gast. Willkomme ide Rote Perle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Mona_Hi2', 'dl_npc_mona', 'If you got gold and are respectful to my girls, you can have an extraordinary time here. (Smiles)', 'Wenn du Gold hast und respektvoll mit meinen Mädels umgehst, kannst du hier eine aussergewöhnliche Zeit geniessen. (Lächelt)', 'Wenn du Gold hesch und respektvoll mit mine Meitli umgasch, chasch da e sehr ussergwöhnlichi Ziit gnüsse. (Lächlet)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_UpperFloor', 'dl_npc_mona', 'How can I get to the upper floor?', 'Wie komme ich in den oberen Stock?', 'We chumi in obere Stock?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OpenGate', 'dl_npc_mona', 'Let me go to the upper floor. (100 $gold$)', 'Lass mich in den oberen Stock. (100 $gold$)', 'Lah min in obere Stock. (100 $gold$)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_OnlyMeet', 'dl_npc_mona', 'I only need to talk to someone briefly.', 'Ich muss nur kurz mit jemandem sprechen.', 'Ich mu nur churz mit öpperem rede.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_FunUnlock', 'dl_npc_mona', 'What if I... you know... wanna have some fun?', 'Was wenn ich... du weisst... mich amüsieren möchte?', 'Was isch wenn ich... du weisch... mich amüsiere möcht?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Fun', 'dl_npc_mona', 'I wanna have some fun.', 'Ich will mich amüsieren.', 'Ich wett mich chli amüsiere.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Trade', 'dl_npc_mona', 'Can I get a drink here?', 'Krieg ich hier etwas zu trinken?', 'Bechumi da öppis zum trinke?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Mona_UpperFloor', 'dl_npc_mona', 'I''ll open the gate for you for a small compensation so you can enjoy the show.', 'Für eine kleine Entschädigung öffne ich dir die Türe und du kannst dir die Vorführung ansehen.', 'Für e chliini Entschädigung mach ich dir das Törli uf und du chasch der d''Vorstellig aluege.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Mona_OpenGateNot', 'dl_npc_mona', 'You don''t have enough gold.', 'Du hast nicht genügend Gold.', 'Du hesch nid gnueg Gold.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Mona_OpenGate', 'dl_npc_mona', 'Sure, have fun.', 'Sicher, viel Spass.', 'Sicher, viel Spass.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Mona_OnlyMeet', 'dl_npc_mona', 'Then you can wait until they leave again. If you go up there, you''ll have to pay.', 'Dann musst du warten, bis sie wieder gehen. Wenn du nach oben willst, musst du zahlen.', 'Denn muesch hald warte bis si wieder gönd. Wend uega wetsch muesch au zahle.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Mona_FunUnlock', 'dl_npc_mona', 'Hmm... (Mona eyeballs you) How old are you?', 'Hmm... (Mona mustert dich) Wie alt bist du?', 'Hmm... (d''Mona musteret dich) We alt bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AgeDontKnow', 'dl_npc_mona', 'I don''t know. I lost my memory. Old enough?', 'Ich weiss nicht. Ich habe meine Erinnerungen verloren. Genug alt?', 'Ich weiss nid. Ich ha mini Erinnerige verlore. Alt gnueg?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_AgeEighteen', 'dl_npc_mona', 'Eighteen.', 'Achtzehn.', 'Achzähni.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_TwentyFive', 'dl_npc_mona', 'Twenty-five. (Lie)', 'Fünfundzwanzig. (Lügen)', 'Füfezwanzgi. (Lüge)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Mona_AgeDontKnow', 'dl_npc_mona', 'Hm. I guess you may nearly be of age. I tell my girls to be gentle with you. (Smiles)', 'Hm. Ich denke mal, du bist fast volljährig. Ich sage meinen Mädchen, dass sie mit dir sanft umgehen sollen. (Lächelt) ', 'Hm. Ich denk mol, du bisch fasch volljöhrig. Ich seg mine Meitli, dass si chli sanft mit dir söled umgo. (Lächlet)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Mona_AgeEighteen', 'dl_npc_mona', 'Hm. Alright, that may be true. It''s okay then. (Smiles)', 'Hm. Gut, das könnte stimmen. Also ist es in Ordnung. (Lächelt)', 'Hm. Guet, das chönt stimme. Also isches in Ornig. (Lächlet)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Mona_TwentyFive', 'dl_npc_mona', 'No, never. If you''re not old enough to tell the truth, you''re not old enough for my girls. Get out.', 'Nein, niemals. Wenn du nicht genügend alt bist, die Wahrheit zu sagen, dann bist du auch nicht genügend alt für meine Mädchen. Raus mit dir.', 'Nei, sicher nid. Wennd nid gnueg alt bisch zum d''Wohret sege, bisch au nid gnueg alt für mini Meitli. Use mit dir.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Mona_HaveSomeFun', 'dl_npc_mona', 'Alright. Go to Lola, she''s already waiting for you. (Winks)', 'Gut. Geh zu Lola, sie wartet schon auf dich. (Zwinkert)', 'Guet. Gang zude Lola, sie warted scho uf dich. (Zwinkeret)');
