INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Hi', 'dl_npc_morris', 'Hi, I''m new here.', 'Hi, ich bin neu hier.', 'Hi, ich bi neu da.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_IAm', 'dl_npc_morris', 'Ah, a new prisoner. And what brought you here?', 'Ah, ein Neuer. Und wieso bist du denn hier gelandet?', 'Ah, en Neue. Und weso bisch du denn da glandet?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Stole', 'dl_npc_morris', 'I stole something.', 'Ich habe etwas gestohlen.', 'Ich ha öppis klaut.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Murdered', 'dl_npc_morris', 'I''ve murdered someone.', 'Ich habe jemanden ermordet.', 'Ich han öppert abgmurkst.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Innocent', 'dl_npc_morris', 'I''m innocent!', 'Ich bin unschuldig!', 'Ich bi unschuldig!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NoneBusiness', 'dl_npc_morris', 'That''s none of your business.', 'Das geht dich nichts an.', 'Das goht dich nüt ah.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_Stole', 'dl_npc_morris', 'Yes, the observer spells can be trouble, I can tell from my own experience.', 'Ja, die Beobachter-Zauber können wirklich mühsam sein, musste ich auch schon erfahren.', 'Ja, d''Beobachter-Zauber chönd würkli müesam si, hani au scho möse erfahre.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_Murdered', 'dl_npc_morris', 'Funny, you don''t like someone that is capable of murdering people. But I''ll watch my back. (Grins)', 'Lustig, du siehst nicht aus wie jemand, der Leute ermordet. Aber ich werde wohl besser auf meinen Rücken aufpassen. (Grinst)', 'Lustig, du gsesch nid us we öpper, wo Lüt killt. Aber ich pass glaub besser chli uf mit dir, he. (Grinst)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_Innocent', 'dl_npc_morris', 'Aren''t we all?', 'Sind wir das nicht alle?', 'Simmer das nid alli?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_NoneBusiness', 'dl_npc_morris', 'Oh well. Must be something really bad then. (Rolls his eyes)', 'Hm. Muss ja was ganz schlimmes sein, was. (Rollt seine Augen)', 'Hm, mu ja was ganz schlimms si, he. (Rollt sini Auge)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatBroughtYou', 'dl_npc_morris', 'And why are you here?', 'Und wieso bist du da?', 'Und weso bisch du da?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_MyCrime', 'dl_npc_morris', 'This and that. Pilferage, gambling, fraud... Oh, and beating up the guy who snitched on me.', 'Dies und das. Diebstahl, Glücksspiele, Betrug... Oh, und ich habe den Typen verprügelt, der mich verraten hat.', 'Chli dies und das. Diebstahl, Glücksspiel, Betrug... Oh, und ich ha de Typ zemegschlage, wo mi verpfiffe het.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_Game', 'dl_npc_morris', 'Speaking of gambling, I still offer some fun to the prisoners here. Interested?', 'Wenn wir gerade von Glücksspielen reden, ich habe noch immer etwas Spass für die Gefangenen hier im Angebot. Interessiert?', 'Apropos Glücksspiel, ich büte immer na chli Spass ah für di Gfangene da. Interessiert?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_GameRules', 'dl_npc_morris', 'How does your game work?', 'Wie funktioniert dein Spiel?', '');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_GameRules', 'dl_npc_morris', 'It''s pretty simple. See this coin? You bet a certain amount of gold on head or tails, I toss it. If you''re correct with your guess, I double your gold. Otherwise, I keep it.', 'Es ist ziemlich einfach. Siehst du diese Münze? Du setzt einen bestimmten Betrag auf Kopf oder Zahl. Dann werfe ich die Münze. Falls du gewinnst, verdopple ich dir deinen Einsatz. Falls nicht, behalte ich ihn. ', 'Es isch zimli eifach. Gsesch die Münze? Du setzisch en gwüsse Goldbetrag uf Chopf oder Zahl. Denn wirfi si und wend richtig grate hesch verdoppli din Isatz. Und wenn nid, phalti en.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IWantPlay', 'dl_npc_morris', 'I want to play...', 'Ich will spielen...', 'Ich will spile...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Gold', 'dl_npc_morris', '... Here is my bet.', '... Hier ist mein Einsatz.', '... Da isch min Isatz.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_NotEnoughGold', 'dl_npc_morris', 'I''ll come back later.', 'Ich komme später wieder.', 'Ich chume spöter wider.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_HeadOrTails', 'dl_npc_morris', 'Fine. Head or tails?', 'Gut. Kopf oder Zahl?', 'Guet. Chopf oder Zahl?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Head', 'dl_npc_morris', 'Head.', 'Kopf.', 'Chopf.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Tails', 'dl_npc_morris', 'Tails.', 'Zahl.', 'Zahl.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_TossCoin', 'dl_npc_morris', '(Morris tosses the coin...)', '(Morris wirft die Münze...)', '(De Morris wirft d''Münze...)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_DaggerBet', 'dl_npc_morris', 'That''s a really high bet. I don''t have enough gold, but I put my dagger then, it''s worth more than 100 gold.', 'Das ist ein ziemlich hoher Einsatz. Ich habe nicht genügend Gold, aber ich setze meinen Dolch dafür. Er ist mehr wert als 100 Gold.', 'Das isch en zimli hoche Isatz. Ich ha nid gnueg Gold, aber ich setz min Dolch. De isch me wert als 100 Gold.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_WinHead', 'dl_npc_morris', 'Head! Lucky one. Here is your doubled bet.', 'Kopf! So ein Glück. Hier ist dein Gewinn.', 'Chopf! Glückspilz du. Da isch din Gwünn.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_LoseHead', 'dl_npc_morris', 'Tails! I''m so sorry, I''ll have to keep your gold.', 'Zahl! Tut mir leid, ich muss dein Gold wohl behalten.', 'Zahl! Tuet mer leid, ich mu dis Gold glaub phalte.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_WinTails', 'dl_npc_morris', 'Tails! You win. Here is your bet.', 'Zahl! So ein Glück. Hier ist dein Gewinn.', 'Zahl! Glückspilz du. Da isch din Gwünn.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_LoseTails', 'dl_npc_morris', 'Head! I''m so sorry, I''ll have to keep your gold.', 'Kopf! Tut mir leid, ich muss dein Gold wohl behalten.', 'Chopf! Tuet mer leid, ich mu dis Gold glaub phalte.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_WinHeadDagger', 'dl_npc_morris', 'Head! You win. I guess I have to leave you my beloved dagger then. Take good care of it.', 'Kopf! Du gewinnst. Ich glaube, ich muss dir jetzt meinen Dolch geben... Pass gut auf ihn auf!', 'Chopf! Du gwünnsch. Ich glaub, ich mu der jetz min Dolch ge... Pass guet ufen uf!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morris_WinTailsDagger', 'dl_npc_morris', 'Tails! You win. I guess I have to leave you my beloved dagger then. Take good care of it.', 'Zahl! Du gewinnst. Ich glaube, ich muss dir jetzt meinen Dolch geben... Pass gut auf ihn auf!', 'Zahl! Du gwünnsch. Ich glaub, ich mu der jetz min Dolch ge... Pass guet ufen uf!');
