INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morton_BeGone', 'dl_npc_morton', 'What are you doing here? You''re disturbing the peace of the dead. Be gone!', 'Was machst du hier? Du störst die Ruhe der Toten. Hinfort mit dir.', 'Was machsch du da? Du störsch d''Rueh vode Tote. Husch, furt mit dir!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Syrah', 'dl_npc_morton', 'Syrah sent me. I need to go into that crypt.', 'Syrah schickt mich. Ich muss in diese Krypta.', 'D''Syrah het mich gschickt. Ich mun i die Krypta.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morton_Syrah', 'dl_npc_morton', 'Ah, Syrah. (Morton stares at you) Why does she always hire guys like you. ', 'Ah, Syrah. (Morton starrt dich an) Wieso heuert sie auch immer Typen wie dich für ihre Aufträge an.', 'Ah, d''Syrah. (De Morton starrt dich ah) Weso stellt si au immer so Type we dich ah.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Morton_Syrah2', 'dl_npc_morton', '(Sighs) You can go in. But hurry up and don''t touch anything you don''t need.', '(Seufzt) Du kannst rein. Aber beeil dich und fass nichts an, das du nicht brauchst.', '(Süüfzet) Du chasch ie. Aber mach chli schnell und lang nüt ah wod nid bruchsch.');
