INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Congratulations', 'dl_npc_nuray', 'So you''re new here. Well done. Vincent won''t let everyone join us.', 'Du bist also der neue hier. Gut gemacht. Vincent lässt nicht einfach jeden beitreten.', 'Du bisch also de neu da. Guet gmacht. De Vincent laht nid eifach jede bitrette.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_nuray', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_WhoAreYou', 'dl_npc_nuray', 'I''m Nuray and I can sell you armour if you''re interested.', 'Ich bin Nuray und ich kann dir Rüstungen verkaufen, wenn du möchtest.', 'Ich bi d''Nuray und ich cha dir Rüstige verchaufe wend wetsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Spells', 'dl_npc_nuray', 'Can you teach me some spells?', 'Kannst du mir Zauber beibringen?', 'Chasch du mir Zauber biibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Trade', 'dl_npc_nuray', 'Show me your wares.', 'Zeig mir deine Waren.', 'Zeig mer dini Ware.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Leader', 'dl_npc_nuray', 'Is Vincent the leader of the guild?', 'Ist Vincent der Anführer der Gilde?', 'Isch de Vincent de Aführer vode Gilde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Receiver', 'dl_npc_nuray', 'Can I sell you my valuable stuff?', 'Kann ich bei dir wertvolle Dinge loswerden?', 'Chan ich bi dir wertvolli Sache loswerde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_VeliusFound', 'dl_npc_nuray', 'Your brother was abducted by some mage named Velius.', 'Dein Bruder wurde von einem Magier namens Velius entführt.', 'Din Brüeder isch vomne Magier namens Velius entführt worde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Spells', 'dl_npc_nuray', 'No, I''m no mage. I''m the only one in my family born without this... curse.', 'Nein, ich bin keine Magierin. Ich bin die einzige in meiner Familie, die ohne diesen... Fluch geboren wurde.', 'Nei, ich bi ke Magierin. Ich bi di einzig i minere Familie wo ohni de... Fluech gebore worde isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Spells2', 'dl_npc_nuray', 'But I''m quite okay without it. (Points at her daggers)', 'Aber ich komme auch ohne ziemlich gut klar. (Zeigt auf ihre Dolche)', 'Aber ich chum au ohni ganz guet z''schlag. (Zeigt uf iri Dölch)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Leader', 'dl_npc_nuray', '(Smiles) No, we don''t really have a "leader". Just people with more prestige than others. ', '(Lächelt) Nein, wir haben nicht wirklich einen "Anführer". Nur Leute mit mehr Ansehen als andere.', '(Lächlet) Nei, mir hend nid würkli en "Aführer". Eifach Lüt wo me Ruef hend we anderi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Receiver', 'dl_npc_nuray', 'You''re looking for a receiver, eh. I''m not your girl then, but talk to Douglas on the market square.', 'Du suchst also einen Hehler, eh. Ich bin es nicht, aber du kannst ja mal mit Douglas auf dem Markt sprechen.', 'Du suechsch also en Hehler, he. Ich bis nid, aber du chasch ja mal mitem Douglas ufem Markt rede.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Receiver2', 'dl_npc_nuray', 'Just ask him how much his monocle costs and he''ll know what''s up.', 'Frag ihn einfach wie viel sein Monokel kostet und er weiss, was du möchtest.', 'Frög in eifach wevil sis Monokel chostet und er weiss was Sach isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_VeliusFound', 'dl_npc_nuray', 'What?! Damn mages! Go and find him!', 'Was?! Verdammte Magier! Geh und finde ihn!', 'Was?! Verdammti Magier! Gang und finden!');
