INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Congratulations', 'dl_npc_nuray', 'So you''re new here. Well done. Vincent won''t let everyone join us.', 'Du bist also der neue hier. Gut gemacht. Vincent lässt nicht einfach jeden beitreten.', 'Du bisch also dä neu do. Guet gmacht. De Vincent loht nid eifach jede biträttä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_nuray', 'Who are you?', 'Wer bist du?', 'Wer bisch du?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_WhoAreYou', 'dl_npc_nuray', 'I''m Nuray and I can sell you armour if you''re interested.', 'Ich bin Nuray und ich kann dir Rüstungen verkaufen, wenn du möchtest.', 'Ich bi d''Nuray und ich tuä dir Rüstigä verchaufe wend wotsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Spells', 'dl_npc_nuray', 'Can you teach me some spells?', 'Kannst du mir Zauber beibringen?', 'Chasch du mir Zauber biibringe?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Trade', 'dl_npc_nuray', 'Show me your wares.', 'Zeig mir deine Waren.', 'Zeig mer dini Ware.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Leader', 'dl_npc_nuray', 'Is Vincent the leader of the guild?', 'Ist Vincent der Anführer der Gilde?', 'Isch de Vincent de Aführer vode Gilde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Receiver', 'dl_npc_nuray', 'Can I sell you my valuable stuff?', 'Kann ich bei dir wertvolle Dinge loswerden?', 'Chan ich bi dir wertvolli Sache loswerde?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_VeliusFound', 'dl_npc_nuray', 'Your brother was abducted by some mage named Velius.', 'Dein Bruder wurde von einem Magier namens Velius entführt.', 'Din Brüeder isch vomne Magier namens Velius entführt worde.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereRich', 'dl_npc_nuray', 'Do you know where I could get rich?', 'Wo gibt''s hier was zu holen?', 'Wo gits do öppis z''hole?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_PirateTreasure', 'dl_npc_nuray', 'Here. Now tell me more about that treasure.', 'Hier. Jetzt erzähl mir mehr über diesen Schatz.', 'Do. Jetz verzell mer me über de Schatz.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhyTreasure', 'dl_npc_nuray', 'If that treasure is so valuable, why didn''t you get it by now?', 'Wenn dieser Schatz so wertvoll ist, wieso hast du ihn nicht schon geholt?', 'Wenn de Schatz so wertvoll isch, weso heschen nid scho gholt?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Spells', 'dl_npc_nuray', 'No, I''m no mage. I''m the only one in my family born without this... curse.', 'Nein, ich bin keine Magierin. Ich bin die einzige in meiner Familie, die ohne diesen... Fluch geboren wurde.', 'Nei, ich bi kei Magierin. Ich bi di einzig i minerä Familie wo ohni dä... Fluäch gebore worde isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Spells2', 'dl_npc_nuray', 'But I''m quite okay without it. (Points at her daggers)', 'Aber ich komme auch ohne ziemlich gut klar. (Zeigt auf ihre Dolche)', 'Aber ich chum au ohni ganz guet zrächt. (Zeigt uf iri Dölch)');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Leader', 'dl_npc_nuray', '(Smiles) No, we don''t really have a "leader". Just people with more prestige than others. ', '(Lächelt) Nein, wir haben nicht wirklich einen "Anführer". Nur Leute mit mehr Ansehen als andere.', '(Lächlet) Nei, mir hend nöd würkli en "Aführo". Eifach Lüt wo me Ruäf hend wie anderi.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Receiver', 'dl_npc_nuray', 'You''re looking for a receiver, eh. I''m not your girl then, but talk to Douglas on the market square.', 'Du suchst also einen Hehler, eh. Ich bin es nicht, aber du kannst ja mal mit Douglas auf dem Markt sprechen.', 'Du suechsch also en Hehler, he. Ich bis nöd, aber du chasch ja mal mitem Douglas ufem Markt redä.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_Receiver2', 'dl_npc_nuray', 'Just ask him how much his monocle costs and he''ll know what''s up.', 'Frag ihn einfach wie viel sein Monokel kostet und er weiss, was du möchtest.', 'Frog in eifach wivil sis Monokel chostä tuet und er weiss was Sach isch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_VeliusFound', 'dl_npc_nuray', 'What?! Damn mages! Go and find him!', 'Was?! Verdammte Magier! Geh und finde ihn!', 'Was?! Verdammti Magier! Gang und finden!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_WhereRich', 'dl_npc_nuray', 'Not in this shithole, that''s for sure.', 'Sicher nicht hier in diesem Drecksloch.', 'Sicher nid do i dem Drecksloch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_WhereRich2', 'dl_npc_nuray', 'But for some golden coins, I could tell you where to find a real treasure.', 'Aber für ein paar goldene Münzen könnte ich dir sagen, wo du einen richtigen Schatz findest.', 'Aber für es paar goldigi Münze chönti der sege, wo du en richtige Schatz findsch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_PirateTreasure', 'dl_npc_nuray', 'Hehe. Well, there are rumours of a pirate treasure, hidden somewhere near the beach of Gandria.', 'Hehe. Nun, es gibt Gerüchte über einen Piratenschatz, der nahe der Küste von Gandria versteckt sein soll.', 'Hehe. Jo, es git Grüchtli über en Pirateschatz, wo ide Nöchi vode Küste vo Gandria söll versteckt si.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_PirateTreasure2', 'dl_npc_nuray', 'You could try to find it...', 'Du könntest versuchen, ihn zu finden...', 'Du chöntsch versueche, ihn z''finde...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_WhyTreasure', 'dl_npc_nuray', 'They say, the treasure can only be found with wind magic, which was used by the pirates.', 'Man sagt, der Schatz kann nur mit Windmagie gefunden werden - welche die Piraten beherrschten.', 'Me seit, de Schatz cha nur mit Windmagie gfunde werde, wo d''Pirate beherrscht hend.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Nuray_WhyTreasure2', 'dl_npc_nuray', 'No one here is able to use wind magic - and it''s only a legend.', 'Niemand hier kann Windmagie benutzen - und es ist nur eine Legende.', 'Niemort do cha Windmagie bruche - und es isch eh nur e Legende.');
