INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Armor', 'dl_npc_paladin1', 'Nice armour you got there...','Nette Rüstung...','E hübschi Rüstig hesch da...', 'Bonita armadura tienes ahí...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Paladin', 'dl_npc_paladin1', 'How is it like to be a paladin?','Wie ist es so, ein Paladin zu sein?','Und, we isches so als Paladin?','¿Qué se siente ser paladín?');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Choice_Bye', 'dl_npc_paladin1', 'Nevermind...','Schon gut...','Scho guet...', 'Olvídalo...');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Paladin1_IAm', 'dl_npc_paladin1', 'I''m a paladin of the Eternal Light, serving our lord and king Logan the Second.','Ich bin ein Paladin des Ewigen Lichts und diene unserem Herrscher und König Logan dem Zweiten.','I bi ä Paladin vom Ewigä Liecht u dienä üsm Herrscher u Chönig äm Logan dr Zwöit.', 'Soy un paladín de la Eterna Luz en servicio de nuestro señor y rey Logan II.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Paladin1_Armor', 'dl_npc_paladin1', 'Ye, right? But you''ll need to work hard to get something like this, so don''t even think about it.','Ja, schon ziemlich nett, was? Aber du wirst hart arbeiten müssen, um an eine solche Rüstung zu kommen, also denk'' gar nicht erst dran.','Ja, gäu? Abr du wirsch hert müesse chrampfä für zum so eini z''becho, auso dänk besser gar nid dra.', '¡Claro que sí! Pero necesitarás trabajar duro para conseguir algo así, entonces ni siquiera lo pienses.');
INSERT INTO text (text_id, text_type, english, german, swiss_german, spanish) values ('DL_Paladin1_PissedOff', 'dl_npc_paladin1', 'How is it like to be a little pain in the neck like you?','Wie ist es so, eine kleine Nervensäge wie du zu sein?','Wie isches ä so, ä chlyni Närväsagi z''si wie du eini bisch?', '¿Qué se siente ser un pequeño dolor de cabeza como tú?');