INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Paladin1_IAm', 'dl_npc_paladin1', 'I''m a paladin of the Eternal Light, serving our lord and king Logan the Second.','Ich bin ein Paladin des Ewigen Lichts und diene unserem Herrscher und König Logan dem Zweiten.','Ich bin en Paladin vom Ewige Liecht und diene üserem Herrscher und König em Logan em Zweite.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Paladin1_Armor', 'dl_npc_paladin1', 'Ye, right? But you''ll need to work hard to get something like this, so don''t even think about it.','Ja, schon ziemlich nett, was? Aber du wirst hart arbeiten müssen, um an eine solche Rüstung zu kommen, also denk'' gar nicht erst dran.','Ja, gell? Aber du wirsch hart schaffe möse um a e sonigi z''cho, also denk besser gar nid dra.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Paladin1_PissedOff', 'dl_npc_paladin1', 'How is it like to be a little pain in the neck like you?','Wie ist es so, eine kleine Nervensäge wie du zu sein?','We isches so, en chliine Nervchopf z''sii we du eine bisch?');