INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Paladin2_WhatIsIt', 'dl_npc_paladin2','What is it?','Was ist?','Wa isch?');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatAreYouDoing', 'dl_npc_paladin2','What are you doing here?','Was macht ihr da?','Wa mached ihr da?');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_Mage', 'dl_npc_paladin2','Are you a mage?','Bist du ein Magier?','Bisch du en Magier?');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhatIsDuty', 'dl_npc_paladin2','What is the Priestess'' duty?','Was ist die Aufgabe der Priesterin?','Was isch denn d''Ufgab vode Priesterin?');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_Bye', 'dl_npc_paladin2','See you.','Bis später.','Tschüss.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Paladin2_AskHer', 'dl_npc_paladin2','I got no time to chit-chat. Go and ask her yourself. But you better show her some respect, we''re watching you.','Ich habe keine Zeit zu plaudern. Geh und frage sie selbst. Aber behandle sie mit Respekt, wir beobachten dich.','Ich ha ke Ziit zum plaudere. Gang und frög si selber. Aber zeig ihre chli Respekt, mir beobachted dich.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Paladin2_GuardingInina', 'dl_npc_paladin2','We''re making sure that our High Priestess can fulfill her duty in peace.','Wir sorgen dafür, dass unsere Hohepriesterin ihre Aufgabe erledigen kann, ohne gestört zu werden.','Mir lueged, das üsi Hohepriesterin ihri Ufgab da erledige chan ohni dass si öpper stört.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Paladin1_Mage', 'dl_npc_paladin2','Of course. You can''t get the rank of a paladin while being a mere commoner.','Natürlich. Man kann nicht den Rang eines Paladins erhalten, wenn man ein Gewöhnlicher ist.','Natürli. Me chan nid de Rang vomne Paladin ha wenme en Gwöhnliche isch.');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhereIsInina', 'dl_npc_paladin2','Where is Inina?','Wo ist Inina?','Wo isch d''Inina?');
INSERT INTO text (text_id, text_type, english, german, swiss_german) values ('DL_Paladin2_WhereInina', 'dl_npc_paladin2','She''s gone into the caverns herself. Someone''s got to find out how and why this beast came so close to the city.','Sie ist jetzt selbst in die Höhlen gegangen. Jemand muss herausfinden, wie und wieso dieses Biest der Stadt so nahe gekommen ist.','Si isch ez selber id Höhle gange. Öpper mu usefinde, was das Biest ide Höhle so nöch vor de Stadt z''sueche gha het.');