INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Hey', 'dl_npc_rappa', 'Excuse me...', 'Hi!', 'Hey!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_StopIt', 'dl_npc_rappa', 'Stop screaming, it hurts my ears, can''t you see?', 'Hör auf mit deinem Geschrei!', 'Hör uf mit dim Gschrey!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ItWasLoud', 'dl_npc_rappa', 'It was loud, I agree.', 'Wenn du sagst, dass es so sei...', 'Okey...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_WhoAreYou', 'dl_npc_rappa', 'And you are ...?', 'Was ist denn dein Name ...?', 'Und du bisch ...?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Bye', 'dl_npc_rappa', 'Bye, I''ll leave you alone.', 'Tschüss, ich habe noch anderes zu tun.', 'Tschau, ich ha na anders ztue.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_FinallyFree', 'dl_npc_rappa', 'You can be lucky you''re finally free!', 'Sei froh, bist du endlich frei!', 'Sig froh, bisch endli frey!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_YoureConfused', 'dl_npc_rappa', 'Ehm... I''ll leave - you seem to be confused...', 'Ehm... Ich gehe - ich glaube, du bist verwirrt...', 'Ehm... Ich gang den mol - du bisch glaub verwirrt...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ISee', 'dl_npc_rappa', 'You talk about screaming and being free, you''re talking about...', 'Du redest von Geschrei und frei sein, redest du von ...', 'Du redsch vo Gschrei und frei sii, redsch du vo ...');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_IKnowTheGame', 'dl_npc_rappa', 'As free as I can be.', 'Ja, aus den Gittern aus Blei.', 'Ja, us de Gitter us Bley.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_YoureConfused', 'dl_npc_rappa', 'Then go! You see, I''m not amused.', 'Dann geh! Du - nicht ich - hast dich verirrt.', 'Denn gang doch! Du - nöd ich - hesch dich verirrt.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_APlay', 'dl_npc_rappa', '... a play!', '... einem Theaterakt!', '... emne Theaterakt!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_APrison', 'dl_npc_rappa', '... a prison?', '... einem Gefängnis?', '... emne Gfängnis?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_ABird', 'dl_npc_rappa', '... a bird?', '... einem Papagei?', '... emne Vögeli?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_APlay', 'dl_npc_rappa', 'Ah, you''re too dumb. I''m not gonna stay!', 'Hat''s dir ins Hirn gekackt? Du verstehst es nicht, geh mir aus der Sicht.', 'Ah und da hesches verkackt. Du peilsches nöd, ich gang! Das wirdmer z''blöd.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_NPC_ImAfraid', 'dl_npc_rappa', 'That reminds me of hell! I have to go - farewell!', 'Du machst mir echt Schiss... Zeit, dass ich mich verpiss!', 'Du machschmer echt Schiss... S''wird Ziit, dassi mich verpiss.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_ABird', 'dl_npc_rappa', 'The bird is a word! The most stupid thing I''ve ever heard - that''s why I leave, nerd!', 'Deine kuriosen Ideen sind einerlei - doch du verstehst es nicht, geh mir aus der Sicht!', 'Das müend dini Chopfbewohner si. Du peilsches nöd, ich gang - das wirdmer z''blöd!');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Cendric_IKnowTheGame', 'dl_npc_rappa', 'See, I can talk in rhymes as well.', 'Siehst du, du bist nicht der einzige, der das Spiel kennt.', 'Gsesch, Schund zemereime chani au.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_KnowTheGame', 'dl_npc_rappa', 'Yes, you''re a smart one, I can tell. But you remind me of a place I don''t wanna be.', 'Ich wusste, du bist intelligent. Aber dies ist mir jetzt einerlei, du erinnerst mich an Schmerz und Geschrei.', 'Ich ha gwüsst du bisch schlau. Aber das isch mer glich, well du erinnerisch mich.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_KnowTheGame2', 'dl_npc_rappa', 'That scares me like hell - I''ll leave, farewell!', 'Das mag ich nicht, geh mir nun bitte aus der Sicht.', 'Das macht mer Schiss... Es wird Ziit, dasi mi verpiss.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_WhoAreYou', 'dl_npc_rappa', 'Carrots, celery, meat... no fish on this bar?', 'Karotten, Sellerie, Fleisch... wo ist der Fisch?', 'Rüebli, Sellerie, Fleisch... wo isch de Fisch?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_RappaConfused', 'dl_npc_rappa', 'You seem to be a bit confused.', 'Ich sehe, du bist ein wenig verwirrt.', 'Ich gse, du hesch en verwirrte Chopf.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_CountStuff', 'dl_npc_rappa', 'You''re counting the stuff in the shelf?', 'Du zählst die Sachen im Regal?', 'Du zellsch d''Sache im Regal?');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Choice_Bizarre', 'dl_npc_rappa', 'Maybe there''s something in that jar.', 'Das hättest du wohl gerne auf dem Tisch.', 'Das hetsch wohl gern ufem Tisch.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_RappaConfused', 'dl_npc_rappa', 'I wanted to talk, but you refused.', 'Ich wollte mit dir reden, doch du hast dich verirrt.', 'Ha nu wele rede, du arme Tropf.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_CountStuff', 'dl_npc_rappa', 'You''re dumb, leave me to myself.', 'Deine Dummheit ist eine echte Qual.', 'Dini Dummheit isch e Qual.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_Bizarre', 'dl_npc_rappa', 'I see, we''re on par. Anyway, I miss some potato, I really really have to go.', 'Ich sehe, das wirkt authentisch. Trotzdem musst du jetzt gehen, doch wir werden uns wiedersehen.', 'Ich gseh, dassd ez scho gschiider bisch. Trotzdem muesch ez ga und mich wieder eleini lah.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_Bye', 'dl_npc_rappa', 'It''s better like that, I should have known.', 'Ah... endlich kann ich mich ausruhn.', 'Ah... endlich Rue.');
INSERT INTO text(text_id, text_type, english, german, swiss_german) values ('DL_Rappa_Shelf', 'dl_npc_rappa', '(Rappa seems to be ordering the stuff in the shelf)', '(Rappa scheint dabei zu sein, sein Regal in Ordnung zu bringen)', '(De Rappa isch debii, sis Regal in Ornig z''bringe)');
